power_user.custom_css:
/* ==========================================================
   样式作者：@Junezz
   版本：v1.54
   发布于：类脑 Discord 社区

   本样式仅供个人使用与参考，禁止任何形式的商用。
   可二改不可二传。请保留本注释和原作者署名。
   ========================================================== */

/* 字体 */
@import url("https://fontsapi.zeoseven.com/515/main/result.css");

body {
    font-family: "LXGW Neo XiHei Plus";
    font-weight: normal;
}

  :root {
    --mainFontSize: calc(var(--fontScale)* 16px);
    --standardIconSize: calc(var(--mainFontSize)* 1);
    --bottomFormIconSize: calc(var(--mainFontSize)* 1.3);
    --genericRadius: 8px; /* 通用圆角 */
    --avatar-base-width: 48px;
    --avatar-base-height: 48px;
    --scrollBarWidth: 5px;
    --pagePadding: calc(var(--scrollBarWidth) + 3px);
    --black30a: #ffffff10 !important;
    --warning: #ce3636;
    --crimson70a: #0252ff66;
    --crimson-hover: #2a60d5ba;
    --boxShadowSubtle: 0px 8px 22px -6px rgba(24, 39, 75, 0.12);
    --menuBackgroundColor: #181819;
    --mesHeadlineColor: #ffffff0b;
    --buttonFill: #ececee10;
  }

  /* 聊天框背景 */
  #chat {
    padding: var( --pagePadding) 3px var( --pagePadding) var( --pagePadding);
    overflow-y: scroll;
    border-radius: 0px 0px var(--genericRadius) var(--genericRadius);
  }

  /* 顶栏 */
  #top-bar {
    background-color: transparent !important;
    height: var(--topBarBlockSize);
  }

  #top-settings-holder {
    box-shadow: var(--boxShadowSubtle);
  }

/* Apply unified background color to nav & menu buttons */
  #rightNavHolder,
  #persona-management-button,
  #extensions-settings-button,
  #logo_block,
  #user-settings-button,
  #WI-SP-button,
  #advanced-formatting-button,
  #sys-settings-button,
  #ai-config-button {
  background-color: var(--menuBackgroundColor);
  }

  #extensions_settings .inline-drawer-toggle.inline-drawer-header,
  #extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
  #user-settings-block h4,
  .standoutHeader,
  .userSettingsInnerExpandable, .bg_example {
    border: transparent !important;
  }

  #left-nav-panel,
  #rm_api_block,
  #AdvancedFormatting,
  #WorldInfo,
  #user-settings-block,
  #Backgrounds,
  #rm_extensions_block,
  .drawer-content.openDrawer,
  #right-nav-panel {
    background-color: var(--menuBackgroundColor) !important;
    border-radius: 0px 0px var(--genericRadius) var(--genericRadius);
    padding: 8px;
  }

  .drawer-content {
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
    box-shadow: 0 1px 0 0 #3b352d;
    padding-top: 8px;
  }

  /* Icon Sizes & Colors */
  #completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt span span span,
  #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt .completion_prompt_manager_prompt_name .fa-solid,
  #rm_button_characters, #rm_button_panel_pin_div, #lm_button_panel_pin_div, #WI_button_panel_pin_div,
  #completion_prompt_manager .completion_prompt_manager_footer a,
  .fa, .fa-brands, .fa-classic, .fa-regular, .fa-sharp, .fa-solid, .fab, .far, .fas {
    font-size: var(--standardIconSize);
  }

  /* 底栏icon */
  #rightSendForm>div:not(.mes_stop), #leftSendForm>div, .fa-solid.fa-circle-stop {
    font-size: var(--bottomFormIconSize);
  }

   /* 底栏icon 对齐 */
   #leftSendForm, #rightSendForm {
    align-items: center;
  }

  /* 标题样式 */

  /* 清除设置页相关模块标题的背景图，并精简上下 padding */
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4, .standoutHeader{
  background-image: none !important;
  padding: 2px 0 !important;
}

 /* 原始元素样式 */
.standoutHeader:not(h4),
#rm_PinAndTabs,
#title_api,
#rm_api_block .flex-container.alignItemsBaseline,
#AdvancedFormatting .flex-container.alignItemsBaseline:not(h3),
#WorldInfo .alignitemscenter:not(#world_popup *),
#user-settings-block div[name="userSettingsRowOne"],
#rm_extensions_block .alignitemscenter.flex-container.wide100p:not(.justifyCenter),
#top-settings-holder .drawer:not(:first-child):not(:last-child) .drawer-content .alignItemsBaseline.wide100p {
  position: relative; /* 必须要有 */
  padding-left: 12px;
  margin-top: calc(var(--mainFontSize) * 0.2) !important;
  margin-bottom: calc(var(--mainFontSize) * 0.2) !important;
}

/* 小竖线 */
#rm_PinAndTabs::before,
#title_api::before,
#rm_api_block .flex-container.alignItemsBaseline::before,
#AdvancedFormatting .flex-container.alignItemsBaseline:not(h3)::before,
#WorldInfo .alignitemscenter:not(#world_popup *)::before,
#user-settings-block div[name="userSettingsRowOne"]::before,
#rm_extensions_block .alignitemscenter.flex-container.wide100p:not(.justifyCenter)::before,
#top-settings-holder .drawer:not(:first-child):not(:last-child) .drawer-content .alignItemsBaseline.wide100p::before {
  content: '';
  position: absolute;
  left: 0;
  top: 6px;
  bottom: 6px;
  width: 3px;
  background-color: var(--SmartThemeUnderlineColor);
  border-radius: 1px;
}

  /* === 统一标题文字的行高（使阅读更舒适）=== */
  .standoutHeader strong,
  #right-nav-panel h2,
  .drawer-content > h3,
  .drawer-content .flex-container h3:first-of-type:not(#Backgrounds .textAlignCenter),
  .drawer-content .flex-container .flex-container h3:first-of-type,
  .popup h3:not(dialog *) {
    line-height: 1.6;
  }

  .flex-container {
    gap: 8px;
  }


  .redOverlayGlow {
    color: #9c2c2c;
  }

  /* AI聊天气泡样式 */
  body.bubblechat .mes[is_user="false"]{
    margin-top: 8px;
    margin-bottom: 16px;
  }

  /* menu颜色 */
  #options, #extensionsMenu, .popup .popper-modal, .select2-dropdown {
    background-color: var(--menuBackgroundColor);
  }

  /* 聊天气泡 */
  body.bubblechat .mes {
    border-radius: var(--genericRadius);
    padding: 16px;
    display: flex;
    flex-direction: column;
  }

  /* 头像 */
  .mesAvatarWrapper {
    display: flex;
    flex-direction: row; /* 从竖排改成横排 */
    align-items: center; /* 垂直居中 */
    gap: 8px; /* 头像和气泡之间的间距 */
    background-color: var(--mesHeadlineColor);
    padding: 8px; /* 间距 */
    padding-right: 12px; /* 右侧间距 */
    border-top-left-radius: var(--genericRadius);
    border-top-right-radius: var(--genericRadius);
    overflow: hidden;
    width: 100%;
  }

    /* 头像处理 */
  .mes .avatar img{
   padding: 0px;
  }

  body.square-avatars .avatar, body.square-avatars .avatar img{
    border-radius: var(--genericRadius) !important;
  }

  .avatar img {
    border: none;
    box-shadow: none !important;
  }

  body.big-avatars .avatar {
    align-items: self-start !important;
  }

    /* 名字 */
  .mes_block .ch_name {
    background-color: var(--mesHeadlineColor);
    border-bottom-left-radius: var(--genericRadius);
    border-bottom-right-radius: var(--genericRadius);
    padding: 8px; /* 间距 */
    margin-bottom: 12px;
  }

    /* 正文 */
  .mes_text {
    padding: 0px 4px !important;
  }

  .last_mes .mesAvatarWrapper {
    padding-bottom: 8px !important;
}
  .mes_block {
    padding-left: 0px !important;
  }

  /* 快捷回复 */
  .menu_button  {
    filter: none !important;
    background-color: var(--buttonFill);
    border-radius: 4px !important;
    border:transparent !important;
  }

  /* 发送bar */
  body.no-blur #send_form {
    border-top: #3b352d solid 1px;
    border-radius: var(--genericRadius);
    padding: 6px 8px;
    box-shadow: 0 -1px 0px 0 var(--SmartThemeChatTintColor);
  }

  /* 按键选项等颜色 */
  select, .range-block-counter input, .text_pole, textarea, .neo-range-input {
    filter: none !important;
    background-color: var(--buttonFill) !important;
    border: transparent !important;
    margin: 4px 0px !important;
  }

  /* 发送input */
  #send_textarea {
    background-color: transparent !important;
    border: none !important;
    margin: 0px !important;
  }

  .menu_button.menu_button_default {
    box-shadow: none !important;
  }

   /* 世界书设置 */
   .wi-card-entry {
    margin-top: 12px;
    padding: 8px;
    background-color: var(--mesHeadlineColor);
  }

  /* 滑轮 */
  input[type="range"] {
    box-shadow: none;
    background-color: var(--black30a) !important;
    height: 5px;
  }

  /* Checkbox */
  input[type='checkbox'] {
    background-color: #ffffff2b;
    border: none;
    box-shadow: none;
    border-radius: 2px;
  }
  input[type="checkbox"]::before {
    box-shadow: inset 1em 1em var(--SmartThemeEmColor);
  }

  /* 预设 */
  #completion_prompt_manager_list {
    background-color: var(--menuBackgroundColor) !important;
    padding: 0px 8px;
  }

  #completion_prompt_manager .caution {
    color: var(--warning);
  }

  #completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt {
    background-color: #1c1c1e;
    border: none;
    border-radius: 2px;
    padding: 8px;
    margin-bottom: 8px;
  }
  #completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt.completion_prompt_manager_prompt_disabled {
    background-color: var(--menuBackgroundColor);
    border: none;
    border-radius: 2px;
    padding: 8px;
    margin-bottom: 8px;
  }

  #completion_prompt_manager #completion_prompt_manager_list 
.completion_prompt_manager_prompt .completion_prompt_manager_prompt_name .fa-solid {
  color: inherit;
  }

  /* Spacing Adjustment */
  .range-block-title {
    margin: 8px 0px;
    text-align: left;
  }

  hr {
    background-image: none;
    background-color: var(--SmartThemeUnderlineColor);
    margin: 12px 0px;
  }

  .range-block {
    margin-bottom: 8px;
  }
  .checkbox_label {
    margin: 2px 0px;
  }

  #result_info_text {
    color: var(--SmartThemeBodyColor);
  }

  .editable-slider-notification {
    top:10px;
  }

  /* 圆形滑动条 */
  input[type="range"]::-webkit-slider-thumb {
    box-shadow: none;
    background-color: var(--SmartThemeEmColor);
    width: 12px;
    height: 12px;
  }

  #userSettingsRowOne {
    margin: 8px 0px;
  }

  .mes_reasoning_header_title {
    padding-right: 16px;
  }

  #rm_print_characters_block {
    margin-top: 8px;
  }

  #ui_language_select {
    width: 6em !important;
 }

  /* 角色 */
  #rm_print_characters_pagination {
    background-color: var(--menuBackgroundColor);
    border-radius: var(--genericRadius);
    padding: 8px;
    margin: 8px;
  }
  .avatar-container.selected {
    border: 1px solid var(--SmartThemeUnderlineColor);
    border-radius: 4px;
  }

  .avatar-container:hover {
    background-color: var(--white20a);
  }

  .character_select.is_fav .ch_name, .group_select.is_fav .ch_name, .group_member.is_fav .ch_name {
    color: var(--SmartThemeUnderlineColor);
  }

  .character_select.is_fav .avatar {
    outline: 1px solid var(--SmartThemeUnderlineColor);
  }

  /* 思维链 */
  .mes_reasoning_header {
    background-color: #ffffff18;
    justify-content: center;
    padding: 8px;
    letter-spacing: 0.5px;
    margin-bottom: 12px;
    transition: all 0.25s ease;
    border-left: 3px solid var(--SmartThemeUnderlineColor);
    cursor: pointer;
  }

  .mes_reasoning_header:hover {
    background-color: #ffffff24;
  }

  .mes_reasoning_details .mes_reasoning_arrow {
    height: var(--mainFontSize)*0.9;
    width: var(--mainFontSize)*0.9;
  }

  .mes_reasoning {
    border-left: var(--SmartThemeUnderlineColor) solid 2px;
    color: var(--SmartThemeBodyColor)
  }

  /* scrollbar */
  ::-webkit-scrollbar {
    width: var(--scrollBarWidth);
    height: var(--scrollBarWidth);
  }

   ::-webkit-scrollbar-track {
    background: transparent;
  }

  ::-webkit-scrollbar-thumb:vertical,
  ::-webkit-scrollbar-thumb:horizontal {
  background-color: var(--white20a);
  border:none;
  box-shadow: none;
  }

  /* 其他 */
  .drawer-content .alignItemsBaseline {
    align-items: center !important;
  }

  .flex1.flex-container.alignItemsBaseline {
    min-width: 214px;
  }

  .fa-solid.fa-circle-question.note-link-span {
    padding-top: 5px;
    padding-bottom: 5px;
  }

  .doubleRangeContainer > .doubleRangeInputContainer::after {
    background-color: var(--SmartThemeEmColor);
    box-shadow: none !important;
  } 

  /* pagination */
  div[is_user="false"].last_mes .mes_block {
    padding-bottom: 48px;
  }

  /* 对话样式 */
  .mes_text q {
    color: var(--SmartThemeQuoteColor);
    background-color: #faffdf14;
    margin: 0px 3px;
    padding: 2px 4px;
    padding-left: 8px;
    border-radius: 2px;
    border-left: #faffdf61 1.5px solid;
    border-right: #faffdf61 1.5px solid;
    font-size: calc(0.97* var(--mainFontSize));
  }

  /* 角色收藏滚动条 */
  #HotSwapWrapper > div {
    overflow: auto hidden;
    flex-wrap: nowrap;
    justify-content: flex-start;
    gap: 8px;
    min-height: calc(var(--avatar-base-height)* 1.5);
  }

  #HotSwapWrapper > div::-webkit-scrollbar-thumb {
  background-color: var(--white20a);
  }

  #HotSwapWrapper {
    overflow: hidden;
  }

 /* 表格样式 */
 .mes_text table {
  width: 100%;
  border-collapse: collapse;
  margin: 8px 0;
  color: var(--SmartThemeQuoteColor);
  background-color: rgba(255, 255, 255, 0.04);
  border-radius: var(--genericRadius);
  overflow: hidden;
 }

 .mes_text th,
 .mes_text td {
  border: none;
  border-right: 1px solid rgba(255, 255, 255, 0.05);
  padding: 10px 14px;
  text-align: left;
  border-bottom: 1px solid rgb(243 227 192 / 12%);
 }

 .mes_text th {
  color: var(--SmartThemeQuoteColor);
  font-weight: 700;
 }

 .mes_text tr:last-child td {
  border-bottom: none;
 }

 .mes_text tr:nth-child(even) {
  background-color: rgba(255, 255, 255, 0.025);
 }

 .mes_text td:last-child,
 .mes_text th:last-child {
  border-right: none;
 }

/* 基础代码块 */
.mes_text pre {
  background-color: rgba(255, 255, 255, 0.04);
  padding: 12px 14px;
  border-radius: var(--genericRadius);
  font-family: inherit;    
  font-size: calc(var(--mainFontSize)* 0.95);
  letter-spacing: 0.3px;
  overflow-x: auto;
  white-space: pre-wrap;
  word-break: break-word;
  border-left: 3px solid var(--SmartThemeUnderlineColor);
  margin: 10px 0;
  position: relative; 
}

 .mes_text pre code{
  line-height: 1.4;
}
   .hljs-variable {
  color: #ff6074;
}


/* 保留字体继承，不破坏复制按钮结构 */
.mes_text pre code {
  font-family: inherit;
  color: inherit;
  background: none;
  border: none
}

/* 记忆插件表格 */

.tableToolbar {
    background: var(--grey10) !important;
    border: 1px solid var(--grey30) !important;
}

/* 手机适配 */
@media (max-width: 600px) {
  #rm_extensions_block {
    padding-right: 24px;
  }
  #bg_menu_content {
    width: 100%;
  }
}

/* Safari 适配 */
@supports (-webkit-touch-callout: none) {
  #chat {
    padding-right: var(--pagePadding); /* 手动留出滚动条空间 */
  }
  .drawer-content:not(#left-nav-panel):not(#right-nav-panel) {
    max-width: 100dvw;
  }
  #world_popup {
    width: 100%;
  }
}

/* iPad safari */
@supports (-webkit-touch-callout: none) {
  @media screen and (min-width: 768px) {
    #sheld {
      width: var(--sheldWidth);
      margin-left: auto;
      margin-right: auto;
    }
    #top-bar, #top-settings-holder .drawer .drawer-content {
      width: var(--sheldWidth);
    }
    #left-nav-panel,
    #right-nav-panel,
    #character_popup,
    #world_popup,
    .drawer-content {
      top: var(--topBarBlockSize);
    }
    #character_popup,
    #world_popup,
    .drawer-content {
      margin-top: 0px;
      top: var(--topBarBlockSize);
    }
  }
}


/* 插件适配 */

/* 文生图 */
#settings-panel input, #settings-panel select, #settings-panel textarea,
#settings-panel2 input, #settings-panel2 select {
  background-color: var(--buttonFill) !important;
}

/* qr隐藏插件 */
#quick-reply-rocket-button i {
  font-size: var(--bottomFormIconSize) !important;
  color: var(--SmartThemeBodyColor) !important;
}

/* 输入助手 */
.form-group label {
  color: var(--SmartThemeBodyColor) !important;
}

.form-group input, .form-group select {
  border: none !important;
  background-color: var(--buttonFill) !important;
  color: var(--SmartThemeBodyColor) !important;
}

