class DynamicCharacter {
    constructor(name, initialTraits) {
        this.name = name;
        this.baseTraits = {...initialTraits};

        // 特质系统
        this.traitValues = new Map(Object.entries(initialTraits));
        this.traitWeights = new Map();
        this.#normalizeWeights();

        // 情感触发系统
        this.wordEmotionMap = new Map();
        this.#buildEmotionTriggers();

        // 记忆系统
        this.contextWindow = 3;
        this.conversationHistory = [];

        // 响应系统
        this.responseDB = this.#buildResponseSystem();
        this.lastUsedTraits = [];

        // 系统参数
        this.decayRate = 0.93;
        this.learningRate = 0.15;
        this.contextWeight = 0.4
    #buildEmotionTriggers() {
        const emotionDefinitions = new Map([
            ['happy', new Map([['happy', 0.3], ['joy', 0.4], ['good', 0.2]])],
            ['sad', new Map([['sad', 0.4], ['unhappy', 0.3], ['depressed', 0.5]])],
            ['angry', new Map([['angry', 0.5], ['mad', 0.4], ['pissed', 0.6]])],
            ['curious', new Map([['curious', 0.4], ['why', 0.2], ['how', 0.2]])],
            ['confident', new Map([['confident', 0.5], ['certain', 0.4], ['sure', 0.3]])]
        ]);

        for (const [emotion, triggers] of emotionDefinitions) {
            for (const [word, weight] of triggers) {
                if (!this.wordEmotionMap.has(word)) {
                    this.wordEmotionMap.set(word, []);
                }
                this.wordEmotionMap.get(word).push([emotion, weight]);
            }
        }
    }

    #buildResponseSystem() {
        return new Map([
            ['happy', [
                ["What a wonderful day!", ['joyful', 'optimistic']],
                ["Everything seems so bright!", ['cheerful', 'positive']]
            ]],
            ['sad', [
                ["I feel like the world is heavy...", ['melancholy', 'introspective']],
                ["Everything seems so gray today.", ['pensive', 'sensitive']]
            ]],
            ['angry', [
                ["This is completely unacceptable!", ['assertive', 'frustrated']],
                ["I won't tolerate this anymore!", ['decisive', 'strong-willed']]
            ]],
            ['curious', [
                ["Could you explain that in more detail?", ['inquisitive', 'attentive']],
                ["What's the story behind this?", ['analytical', 'observant']]
            ]],
            ['confident', [
                ["I'm certain we can solve this.", ['assertive', 'optimistic']],
                ["This is clearly the best approach.", ['decisive', 'knowledgeable']]
            ]]
        ]);
    }

    #normalizeWeights() {
        let total = Array.from(this.traitValues.values()).reduce((a, b) => a + b, 0);

        if (total <= 0) {
            this.traitValues = new Map(Object.entries(this.baseTraits));
            total = Array.from(this.traitValues.values()).reduce((a, b) => a + b, 0);
        }

        this.traitWeights = new Map(
            Array.from(this.traitValues).map(([k, v]) => [
                k, 
                Math.pow(v / total, 1.5)
            ])
        );
    }

    #analyzeEmotionalContext(text) {
        const scores = new Map();
        const cleanedText = text.toLowerCase().replace(this.cleanTextRegex, '');

        cleanedText.split(/\s+/).forEach(word => {
            if (this.wordEmotionMap.has(word)) {
                this.wordEmotionMap.get(word).forEach(([emotion, weight]) => {
                    const count = (cleanedText.match(new RegExp(word, 'g'))?.length || 0;
                    const increment = weight * (1 + count * 0.2);
                    scores.set(emotion, (scores.get(emotion) || 0) + increment);
                });
            }
        });

        const maxScore = Math.max(...Array.from(scores.values()), 0);
        return new Map(
            Array.from(scores).map(([k, v]) => 
                [k, Math.pow(v / maxScore, 1.3)]
            )
        );
    }

    updateTraits(userInput, feedback = null) {
        const emotionScores = this.#analyzeEmotionalContext(userInput);

        // 应用衰减和更新
        Array.from(this.traitValues.keys()).forEach(trait => {
            let value = this.traitValues.get(trait) * this.decayRate;

            if (emotionScores.has(trait)) {
                let delta = emotionScores.get(trait) * this.learningRate;
                if (feedback) {
                    delta *= {positive: 1.4, negative: 0.6}[feedback] || 1.0;
                }
                value += delta;
            }
            this.traitValues.set(trait, value);
        });

        // 上下文影响
        if (this.conversationHistory.length > 0) {
            const counter = new Map();
            this.conversationHistory
                .flatMap(([_, traits]) => traits)
                .forEach(t => counter.set(t, (counter.get(t) || 0) + 1));

            counter.forEach((count, trait) => {
                this.traitValues.set(trait, 
                    this.traitValues.get(trait) + this.contextWeight * count
                );
            });
        }

        this.#normalizeWeights();
        this.conversationHistory.push([
            userInput, 
            Array.from(emotionScores.keys())
        ]);
        if (this.conversationHistory.length > this.contextWindow) {
            this.conversationHistory.shift();
        }
    }

    generateResponse() {
        // 权重采样
        const traits = Array.from(this.traitWeights);
        const cumulative = traits.reduce((acc, [_, w], i) => {
            acc.push((acc[i-1] || 0) + w);
            return acc;
        }, []);

        // 动态选择3个特质
        const selected = new Set();
        while (selected.size < 3) {
            const rand = Math.random() * cumulative[cumulative.length - 1];
            const index = cumulative.findIndex(v => v >= rand);
            selected.add(traits[Math.max(0, index)][0]);
        }

        // 构建候选响应
        const candidates = [];
        Array.from(selected).forEach(trait => {
            if (this.responseDB.has(trait)) {
                this.responseDB.get(trait).forEach(([resp, traits]) => {
                    const weight = traits.reduce((sum, t) => 
                        sum + (this.traitWeights.get(t) || 0), 0);
                    candidates.push({ resp, traits, weight });
                });
            }
        });

        if (candidates.length === 0) {
            return "I'm not sure what to say right now.";
        }

        // 加权选择
        const totalWeight = candidates.reduce((sum, c) => sum + c.weight, 0);
        let random = Math.random() * totalWeight;
        for (const candidate of candidates) {
            if (random < candidate.weight) {
                this.lastUsedTraits = candidate.traits;
                return candidate.resp;
            }
            random -= candidate.weight;
        }

        return candidates[0].resp; // Fallback
    }

    processFeedback(feedbackType) {
        const valid = new Set(['positive', 'negative', 'neutral']);
        if (!valid.has(feedbackType)) {
            throw new Error(`Invalid feedback type: ${feedbackType}`);
        }

        const multiplier = {
            positive: 1.5,
            negative: 0.6,
            neutral: 1.0
        }[feedbackType];

        this.lastUsedTraits.forEach(trait => {
            if (this.traitValues.has(trait)) {
                this.traitValues.set(trait, 
                    this.traitValues.get(trait) * multiplier
                );
            }
        });
        this.#normalizeWeights();
    }

    longTermDevelopment() {
        // 基础回归
        Object.entries(this.baseTraits).forEach(([trait, baseVal]) => {
