    $starElement.html(isBecomingStarred ? '★' : '☆').attr('title', isBecomingStarred ? '取消标记' : '标记为常用');
    $item.toggleClass(STARRED_REGEX_CLASS, isBecomingStarred);

    try {
        const currentStarred = getStarredRegexFromStorage();
        let newStarred = isBecomingStarred
            ? [...currentStarred, regexName]
            : currentStarred.filter(name => name !== regexName);
        newStarred = [...new Set(newStarred)];
        saveStarredRegexToStorage(newStarred);

        // Refresh list display
        const parentDoc = window.parent.document;
