/* Toggle Regex Enable State (Deferred Save) */
const toggleRegexEnableState = errorCatched(async (regexName, $itemElement) => {
    console.log(`Combined Selector (Regex): Staging toggle for "${regexName}"`);

    // Determine the *intended* new state based on current UI/pending state
    let currentIntendedEnabledState;
    if (pendingRegexChanges.hasOwnProperty(regexName)) {
        // If already staged, use the staged state as the current one
        currentIntendedEnabledState = pendingRegexChanges[regexName];
    } else {
        // Otherwise, use the state from the data attribute
        currentIntendedEnabledState = $itemElement.attr('data-enabled') === 'true';
    }
    const newIntendedEnabledState = !currentIntendedEnabledState;

    // Update pending changes
    pendingRegexChanges[regexName] = newIntendedEnabledState;
    console.log(`Combined Selector (Regex): Staged "${regexName}" to be ${newIntendedEnabledState ? 'enabled' : 'disabled'}. Pending:`, pendingRegexChanges);

    // Update UI immediately for feedback
    $itemElement.toggleClass(REGEX_ITEM_DISABLED_CLASS, !newIntendedEnabledState);
    // We don't need to update data-enabled here as the final refresh will handle it.
    // We also remove the opacity change as it's less relevant now.

}, 'toggleRegexEnableState');


/* Apply Pending Regex Changes */
const applyPendingRegexChanges = errorCatched(async () => {
    const changesToApply = Object.keys(pendingRegexChanges);
    if (changesToApply.length === 0) {
        console.log('Combined Selector (Regex): No pending changes to apply.');
        return; // Nothing to do
    }

    console.log('Combined Selector (Regex): Applying pending changes:', pendingRegexChanges);

    if (typeof replaceTavernRegexes !== 'function' || typeof getFullTavernRegexes !== 'function') {
        console.error('Combined Selector (Regex): API functions (get/replace) not available. Cannot apply changes.');
        alert('错误：无法应用正则更改，所需函数缺失。');
        pendingRegexChanges = {}; // Clear pending changes as we can't apply them
        return;
    }

    let currentRegexes;
    try {
        currentRegexes = await getFullTavernRegexes();
    } catch (error) {
        console.error('Combined Selector (Regex): Failed to get current regexes before applying changes.', error);
        alert('错误：无法获取当前正则列表以应用更改。');
        // Keep pending changes, maybe it works next time? Or clear them? Let's clear for now.
        pendingRegexChanges = {};
        return;
    }

    let changesMade = false;
    const updatedRegexes = currentRegexes.map(regex => {
        const name = regex.script_name;
        if (pendingRegexChanges.hasOwnProperty(name)) {
            const targetState = pendingRegexChanges[name];
            if (regex.enabled !== targetState) {
                console.log(`Combined Selector (Regex): Applying state ${targetState} to "${name}"`);
                changesMade = true;
                return { ...regex, enabled: targetState };
            } else {
                 console.log(`Combined Selector (Regex): State for "${name}" already matches target ${targetState}. No change needed.`);
                 // Remove from pending if state already matches (e.g., toggled twice)
                 delete pendingRegexChanges[name];
            }
        }
        return regex;
    });

     // Check again if any actual changes remain after comparing with current state
     const finalChangesToApply = Object.keys(pendingRegexChanges);
     if (finalChangesToApply.length === 0 || !changesMade) {
         console.log('Combined Selector (Regex): No actual state changes needed after comparison. Aborting save.');
         pendingRegexChanges = {}; // Clear remaining (now unnecessary) pending changes
         return;
     }

    try {
        console.log(`Combined Selector (Regex): Attempting to replace Tavern regexes (scope: all) with ${finalChangesToApply.length} accumulated changes...`);
        await replaceTavernRegexes(updatedRegexes, { scope: 'all' });
        console.log(`Combined Selector (Regex): Successfully called replaceTavernRegexes. Chat will reload.`);
        pendingRegexChanges = {}; // Clear pending changes after successful application
    } catch (error) {
        console.error(`Combined Selector (Regex): Failed to replace Tavern regexes with accumulated changes.`, error);
        alert(`错误：保存累积的正则更改失败。`);
        // Decide whether to keep or clear pendingChanges on failure. Clearing seems safer.
        pendingRegexChanges = {};
    }
}, 'applyPendingRegexChanges');

/* Toggle Global Lorebook Selection */
const toggleGlobalLorebook = errorCatched(async (lorebookName, $itemElement) => {
    console.log(`Combined Selector (Lorebook): Toggling global lorebook selection ${lorebookName}`);
    $itemElement.toggleClass(SELECTED_LOREBOOK_CLASS);
    const isAdding = $itemElement.hasClass(SELECTED_LOREBOOK_CLASS);

    try {
        const currentSettings = await getLorebookSettingsSafe();
        const currentSelection = currentSettings.selected_global_lorebooks || [];
        let newSelection;

        if (currentSelection.includes(lorebookName)) {
            newSelection = currentSelection.filter(name => name !== lorebookName);
            if (!isAdding) console.log('UI reflects removal.'); else console.warn('UI mismatch during removal.');
        } else {
            newSelection = [...currentSelection, lorebookName];
            if (isAdding) console.log('UI reflects addition.'); else console.warn('UI mismatch during addition.');
        }
        newSelection = [...new Set(newSelection)];

        const newSettings = { ...currentSettings, selected_global_lorebooks: newSelection };
        await setLorebookSettingsSafe(newSettings);
        console.log(`Combined Selector (Lorebook): Successfully updated selection for ${lorebookName}.`);

    } catch (error) {
        console.error(`Combined Selector (Lorebook): Failed to toggle global lorebook selection ${lorebookName}`, error);
        if (typeof triggerSlash === 'function') {
            triggerSlash(`echo severity=error Failed to toggle selection for ${lorebookName}.`);
        }
        $itemElement.toggleClass(SELECTED_LOREBOOK_CLASS); // Revert UI on error
    }
}, 'toggleGlobalLorebook');



