                #${LOREBOOK_CONTENT_ID} .${LOREBOOK_STAR_ICON_CLASS}:hover { transform: scale(1.2); color: #ffd700; }
                #${LOREBOOK_CONTENT_ID} .${STARRED_LOREBOOK_CLASS} { border-left: 3px solid #ffcc33; }
                #${LOREBOOK_CONTENT_ID} .${SELECTED_LOREBOOK_CLASS} { background-color: #4a5d7e; border-color: #7a9dce; font-weight: bold; color: #fff; }
                #${LOREBOOK_CONTENT_ID} .${SELECTED_LOREBOOK_CLASS}:hover { background-color: #5a6d8e; border-color: #8aaade; }
                #${LOREBOOK_CONTENT_ID} .${SELECTED_LOREBOOK_CLASS}.${STARRED_LOREBOOK_CLASS} { background-color: #4a5d7e; border-color: #7a9dce; border-left: 3px solid #ffcc33; font-weight: bold; color: #fff; }
                #${LOREBOOK_CONTENT_ID} .${SELECTED_LOREBOOK_CLASS}.${STARRED_LOREBOOK_CLASS}:hover { background-color: #5a6d8e; border-color: #8aaade; }

                /* --- Combined Button --- */
                #${COMBINED_BUTTON_ID} { margin-left: 5px; padding: 5px 8px; background-color: #444; color: #ddd; border: 1px solid #666; border-radius: 4px; cursor: pointer; transition: background-color 0.2s; height: 30px; display: inline-flex; align-items: center; justify-content: center; vertical-align: middle; }
                #${COMBINED_BUTTON_ID}:hover { background-color: #555; color: #fff; }
                #${COMBINED_BUTTON_ID} i { font-size: 1em; }
                #${COMBINED_BUTTON_ID}.active { background-color: #6a4a7e; border-color: #9a7ace; } /* Use a distinct active color */
            </style>
        `;
        $('head', parentDoc).append(panelStyles);
    }

    /* 2. Create Panel HTML */
    let $panel = $(`#${COMBINED_PANEL_ID}`, parentDoc);
    if ($panel.length === 0) {
        const panelHtml = `
            <div id="${COMBINED_PANEL_ID}">
                <div class="panel-header">
                    <h4>正则&世界书快速选择</h4>
                    <button class="close-button" title="关闭">×</button>
                </div>
                <div class="tab-container">
                    <button id="${REGEX_TAB_ID}" class="tab-button ${ACTIVE_TAB_CLASS}">正则</button>
                    <button id="${LOREBOOK_TAB_ID}" class="tab-button">世界书</button>
                </div>
                <div class="content-container">
                    <div id="${REGEX_CONTENT_ID}" class="tab-content ${ACTIVE_CONTENT_CLASS}">
                        <div class="search-container">
                            <input type="text" class="search-input ${REGEX_SEARCH_INPUT_CLASS}" placeholder="搜索正则...">
                        </div>
                        <div class="list-container ${REGEX_LIST_CLASS}">
                            <!-- Initial content removed -->
                        </div>
                    </div>
                    <div id="${LOREBOOK_CONTENT_ID}" class="tab-content">
                        <div class="search-container">
                            <input type="text" class="search-input ${LOREBOOK_SEARCH_INPUT_CLASS}" placeholder="搜索世界书...">
                        </div>
                        <div class="list-container ${LOREBOOK_LIST_CLASS}">
                            <!-- Initial content removed -->
                        </div>
                    </div>
                </div>
            </div>
        `;
        $('body', parentDoc).append(panelHtml);
        $panel = $(`#${COMBINED_PANEL_ID}`, parentDoc);
        // Explicitly hide after appending to prevent initial flash
        $panel.hide();
    }

    /* 3. Create Button HTML & Insert */
    let $button = $(`#${COMBINED_BUTTON_ID}`, parentDoc);
    if ($button.length === 0) {
        const buttonHtml = `
            <button id="${COMBINED_BUTTON_ID}" title="${COMBINED_BUTTON_TOOLTIP}">
                <i class="${COMBINED_BUTTON_ICON}"></i>
            </button>
        `;
        // Try inserting after existing buttons if they exist
        const $regexButtonOld = $('#global-regex-selector-button', parentDoc);
        const $lorebookButtonOld = $('#global-lorebook-selector-button', parentDoc);
        let inserted = false;
        if ($regexButtonOld.length) {
            try { $(buttonHtml).insertAfter($regexButtonOld); inserted = true; } catch (e) { console.error(e); }
        } else if ($lorebookButtonOld.length) {
            try { $(buttonHtml).insertAfter($lorebookButtonOld); inserted = true; } catch (e) { console.error(e); }
        }

        // Fallback placement
        if (!inserted) {
            const $target = $('#regenerate_button', parentDoc).length ? $('#regenerate_button', parentDoc) : $('#send_but', parentDoc);
            if ($target.length) {
                try { $(buttonHtml).insertAfter($target); inserted = true; } catch (e) { console.error(e); }
            }
        }
        if (!inserted) {
            const $sendForm = $('#send_form', parentDoc);
            if ($sendForm.length) {
                try { $sendForm.append(buttonHtml); inserted = true; } catch (e) { console.error(e); }
            } else {
                try { $('body', parentDoc).append(buttonHtml); inserted = true; } catch (e) { console.error(e); } // Last resort
            }
        }

        if (inserted) $button = $(`#${COMBINED_BUTTON_ID}`, parentDoc);
        else console.error("Combined Selector: Failed to insert button!");
    }

    /* 4. Add Event Listeners (using event delegation on the panel) */
    // Main Toggle Button
    $(parentDoc).off(`click.${COMBINED_BUTTON_ID}`).on(`click.${COMBINED_BUTTON_ID}`, `#${COMBINED_BUTTON_ID}`, function (event) {
        event.preventDefault();
        toggleCombinedPanel();
    });

    // Panel-level listeners
    $panel = $(`#${COMBINED_PANEL_ID}`, parentDoc); // Re-select panel here to ensure we have the reference

    // Close Button
    $panel.off('click.combinedClose').on('click.combinedClose', '.close-button', async function () {
        // Apply pending changes BEFORE hiding
        try {
            await applyPendingRegexChanges();
        } catch (e) {
            console.error("Combined Selector: Error applying regex changes on close:", e);
            // Decide if we should still close or alert the user. Let's close for now.
        }
        const $panelToClose = $(this).closest(`#${COMBINED_PANEL_ID}`);
        const $buttonToDeactivate = $(`#${COMBINED_BUTTON_ID}`, parentDoc);
        if ($panelToClose.length) $panelToClose.hide();
        if ($buttonToDeactivate.length) $buttonToDeactivate.removeClass('active');
    });

    // Tab Buttons
    $panel.off('click.combinedTabs').on('click.combinedTabs', '.tab-button', function () {
        const tabId = $(this).attr('id');
        if (tabId && !$(this).hasClass(ACTIVE_TAB_CLASS)) {
            switchTab(tabId);
        }
    });

    // Search Inputs (delegated)
    $panel.off('input.combinedSearch').on('input.combinedSearch', '.search-input', function () {
        const $activeContent = $panel.find('.tab-content.active-content');
        const searchTerm = $(this).val();
        const $listContainer = $activeContent.find('.list-container');

        if ($listContainer.length) {
            if ($activeContent.attr('id') === REGEX_CONTENT_ID) {
                updateRegexListDisplay($listContainer, searchTerm);
            } else if ($activeContent.attr('id') === LOREBOOK_CONTENT_ID) {
                updateLorebookListDisplay($listContainer, searchTerm);
            }
        }
    });

    // Item Clicks (delegated) - Regex Enable/Disable & Lorebook Select/Deselect
    $panel.off('click.combinedItems').on('click.combinedItems', `.${REGEX_ITEM_CLASS}, .${LOREBOOK_ITEM_CLASS}`, function (event) {
        // Prevent action if a star icon within the item was clicked
        if ($(event.target).hasClass(REGEX_STAR_ICON_CLASS) || $(event.target).closest(`.${REGEX_STAR_ICON_CLASS}`).length ||
            $(event.target).hasClass(LOREBOOK_STAR_ICON_CLASS) || $(event.target).closest(`.${LOREBOOK_STAR_ICON_CLASS}`).length) {
            return;
        }

        if ($(this).hasClass(REGEX_ITEM_CLASS)) {
            const regexName = $(this).data('regex-name');
            if (regexName) {
                toggleRegexEnableState(regexName, $(this));
            } else {
                console.error('Combined Selector (Regex): Clicked item missing data-regex-name.');
            }
        } else if ($(this).hasClass(LOREBOOK_ITEM_CLASS)) {
            const lorebookName = $(this).data('lorebook-name');
            if (lorebookName) {
                toggleGlobalLorebook(lorebookName, $(this));
            } else {
                console.error('Combined Selector (Lorebook): Clicked item missing data-lorebook-name.');
            }
        }
    });

    // Star Clicks (delegated)
    $panel.off('click.combinedStars').on('click.combinedStars', `.${REGEX_STAR_ICON_CLASS}, .${LOREBOOK_STAR_ICON_CLASS}`, function (event) {
        event.stopPropagation(); // Prevent item click listener

        if ($(this).hasClass(REGEX_STAR_ICON_CLASS)) {
            const $item = $(this).closest(`.${REGEX_ITEM_CLASS}`);
            const regexName = $item.data('regex-name');
            if (regexName) {
                toggleStarredRegex(regexName, $(this));
            } else { console.error('Combined Selector (Regex): Star clicked missing name.'); }
        } else if ($(this).hasClass(LOREBOOK_STAR_ICON_CLASS)) {
            const $item = $(this).closest(`.${LOREBOOK_ITEM_CLASS}`);
            const lorebookName = $item.data('lorebook-name');
            if (lorebookName) {
                toggleStarredLorebook(lorebookName, $(this));
            } else { console.error('Combined Selector (Lorebook): Star clicked missing name.'); }
        }
    });

    // Click Outside Listener
    $(parentDoc).off(`click.${COMBINED_PANEL_ID}-outside`).on(`click.${COMBINED_PANEL_ID}-outside`, async function (event) {
        const $panel = $(`#${COMBINED_PANEL_ID}`, parentDoc);
        if ($panel.is(':visible') &&
            !$(event.target).closest(`#${COMBINED_PANEL_ID}`).length &&
            !$(event.target).closest(`#${COMBINED_BUTTON_ID}`).length) {
            try {
                await applyPendingRegexChanges();
            } catch (e) {
                console.error("Combined Selector: Error applying regex changes on click outside:", e);
            }
            const $button = $(`#${COMBINED_BUTTON_ID}`, parentDoc);
            $panel.hide();
            if ($button.length) $button.removeClass('active');
        }
    });

    console.log('Combined Selector: Initialization complete.');
});

$(() => {
  // 在收到消息后触发标签修复功能
  eventOn(tavern_events.MESSAGE_RECEIVED, () => {
    // 获取最新的一条消息
    const lastMessage = SillyTavern.chat[SillyTavern.chat.length - 1];

    // 检查消息是否存在
    if (lastMessage && lastMessage.mes) {
      // 修复消息中的标签
      lastMessage.mes = fixMissingTags(lastMessage.mes);
    }
  });

  /**
   * 简单修复HTML中缺失的闭合标签
   * @param {string} html - 包含可能缺失闭合标签的HTML字符串
   * @return {string} - 修复后的HTML字符串
   */
  function fixMissingTags(html) {
    if (!html || typeof html !== 'string') {
      return html;
    }

    // 使用正则表达式找出所有的开标签和闭标签
    const tagRegex = /<\/?([^\s>/]+)[^>]*>/g;
    const tags = [];
    let match;

    // 收集所有标签信息
    while ((match = tagRegex.exec(html)) !== null) {
      const fullTag = match[0];
      const tagName = match[1];
      const isClosingTag = fullTag.startsWith('</');
      const position = match.index;
      const endPosition = position + fullTag.length;

      tags.push({
        fullTag,
        tagName,
        isClosingTag,
        position,
        endPosition
      });
    }

    // 计算每种标签的开闭标签数量
    const tagCounts = {};
    tags.forEach(tag => {
      if (!tagCounts[tag.tagName]) {
        tagCounts[tag.tagName] = { open: 0, close: 0 };
      }

      if (tag.isClosingTag) {
        tagCounts[tag.tagName].close++;
      } else {
        tagCounts[tag.tagName].open++;
      }
    });

    // 找出缺少闭合标签的标签
    const unclosedTags = {};
    for (const [tagName, count] of Object.entries(tagCounts)) {
      if (count.open > count.close) {
        unclosedTags[tagName] = count.open - count.close;
      }
    }

    // 如果没有未闭合的标签，直接返回原始HTML
    if (Object.keys(unclosedTags).length === 0) {
      return html;
    }

    // 为每个未闭合的标签找到合适的插入位置
    const insertions = [];

    // 按照标签出现顺序处理
    for (let i = 0; i < tags.length; i++) {
      const tag = tags[i];

      // 只处理开标签
      if (tag.isClosingTag) continue;

      // 检查这个标签是否缺少闭合标签
      if (unclosedTags[tag.tagName] && unclosedTags[tag.tagName] > 0) {
        // 默认在下一个标签之前插入
        let insertPos = html.length;
        let foundNextTag = false;

        // 查找下一个标签
        for (let j = i + 1; j < tags.length; j++) {
          if (tags[j].position > tag.endPosition) {
            insertPos = tags[j].position;
            foundNextTag = true;
            break;
          }
        }

        // 尝试找到上一行末尾
        if (foundNextTag) {
          const textBetween = html.substring(tag.endPosition, insertPos);
          const lastNewlinePos = textBetween.lastIndexOf('\n');

          if (lastNewlinePos !== -1) {
            insertPos = tag.endPosition + lastNewlinePos;
          }
        }

        // 记录插入位置
        insertions.push({
          position: insertPos,
          tag: `</${tag.tagName}>`
        });

        // 减少需要闭合的标签数量
        unclosedTags[tag.tagName]--;

        // 如果这个标签已经全部修复，从未闭合标签列表中移除
        if (unclosedTags[tag.tagName] <= 0) {
          delete unclosedTags[tag.tagName];
        }
      }
    }

    // 按位置从后往前排序，避免位置变化
    insertions.sort((a, b) => b.position - a.position);

    // 执行插入
    let result = html;
    insertions.forEach(insertion => {
      result = result.substring(0, insertion.position) + 
               insertion.tag + 
               result.substring(insertion.position);
    });

    return result;
  }
});

