/* --- Helper Function (Error Handling) --- */
function errorCatched(fn, context = 'Combined Selector') {
    const onError = (error) => {
        let iframeName = 'UnknownIframe';
        try {
            iframeName = (typeof getIframeName === 'function') ? getIframeName() : 'UnknownIframe';
        } catch (e) {
            console.warn(`(${context}) Could not get iframe name:`, e);
        }
        const errorMessage = `Error in ${iframeName} (${context}) ${error.stack || error.message || error.name}`;
        console.error(errorMessage);
        if (typeof triggerSlash === 'function') {
            try {
                triggerSlash(`echo severity=error ${errorMessage.substring(0, 500)}...`);
            } catch (e) {
                console.error(`(${context}) Failed to triggerSlash for error:`, e);
            }
        }
    };
    return (...args) => {
        try {
            const result = fn(...args);
            if (result instanceof Promise) {
                return result.catch(error => {
                    onError(error);
                    throw error; // Rethrow so await calls catch it
                });
            }
            return result;
        } catch (error) {
            onError(error);
            throw error; // Rethrow so caller knows it failed
        }
    };
}

