        return storedData ? JSON.parse(storedData) : [];
    } catch (error) {
        console.error('Combined Selector (Regex): Error reading starred regex names from localStorage:', error);
        return [];
    }
}, 'getStarredRegexFromStorage');

const saveStarredRegexToStorage = errorCatched((starredArray) => {
    try {
        const validStarredArray = starredArray.filter(item => typeof item === 'string');
        localStorage.setItem(LOCAL_STORAGE_KEY_REGEX_STARRED, JSON.stringify(validStarredArray));
    } catch (error) {
        console.error('Combined Selector (Regex): Error saving starred regex names to localStorage:', error);
        if (typeof triggerSlash === 'function') {
            triggerSlash(`echo severity=error Error saving starred regex.`);
