            // Decide if we should still close or alert the user. Let's close for now.
        }
        const $panelToClose = $(this).closest(`#${COMBINED_PANEL_ID}`);
        const $buttonToDeactivate = $(`#${COMBINED_BUTTON_ID}`, parentDoc);
        if ($panelToClose.length) $panelToClose.hide();
        if ($buttonToDeactivate.length) $buttonToDeactivate.removeClass('active');
