        const renderDelay = option.option?.init_delay || 0;
        if (renderDelay > 0) {
            setTimeout(() => {
                // æ·»åŠ æ–°çš„åœ°å›¾UI
                $mes_text.append($map_element);
            }, renderDelay);
        } else {
            // æ·»åŠ æ–°çš„åœ°å›¾UI
            $mes_text.append($map_element);
        }
    } catch (error) {
        console.error('åœ°å›¾å¯¼èˆªæ¸²æŸ“é”™è¯¯:', error);
    }
}

