        // --- 辅助函数 ---
        function getIconForField(field, value = '') {
             const iconMap = {
                 '姓名': 'fa-signature', '年龄': 'fa-birthday-cake', '职业': 'fa-briefcase',
                 '家庭背景': 'fa-house-user', '美貌': 'fa-face-grin-stars', '身材': 'fa-person-dots-from-line',
