                 '性欲值': 'fa-fire-flame-curved', '性癖': 'fa-mask', '性经验': 'fa-book-medical',
                 '关系': 'fa-link', '最近性行为': 'fa-clock', '临时状态': 'fa-clock-rotate-left',
                 '永久状态': 'fa-lock', '身体状态': 'fa-heart-pulse', '性伴侣': 'fa-users',
