        function getIconForType(type) {
            const iconMap = { 'npc': 'fa-female', 'child': 'fa-child', 'item': 'fa-boxes-stacked' };
            return iconMap[type] || 'fa-question-circle';
        }
        function formatMultiValue(value, field) {
            const valueStr = (value === null || value === undefined) ? '' : String(value).trim();
            if (!valueStr || valueStr.toLowerCase() === '无' || valueStr.toLowerCase() === '未知') {
                 return `<span class="value-tag">${valueStr || '无'}</span>`;
