                } else { textValue = `${valueStr} (无效值)`; }

                if (field === '好感度') {
                    if (numericValue >= 80) progressClass = 'positive'; else if (numericValue >= 40) progressClass = 'info'; else if (numericValue > 0) progressClass = 'warning'; else progressClass = 'negative';
                } else if (field === '堕落值') {
                    if (numericValue >= 80) progressClass = 'negative'; else if (numericValue >= 40) progressClass = 'warning'; else progressClass = 'positive';
