                let displayPercent = numericValue;
                if (field === '好感度') { displayPercent = (numericValue + 100) / 2; }
                displayPercent = Math.max(0, Math.min(100, displayPercent));

                return `
                    <div class="progress-display">
                        <span class="progress-text">${textValue}</span>
