             }

            if (field === '身体状态') {
                const details = valueStr.split(/,\s*(?=[^,]+:)/);
                const tags = details.map(detail => {
                    detail = detail.trim(); if (!detail) return '';
                    const separatorIndex = detail.indexOf(':');
                    if (separatorIndex > 0) {
                        const key = detail.substring(0, separatorIndex).trim(); const val = detail.substring(separatorIndex + 1).trim();
                        return `<span class="value-tag"><span class="detail-key">${key}:</span> <span class="detail-value">${val || '无'}</span></span>`;
