        // --- 数据解析函数 (适配 v2.00 格式) ---
        function parseDataString(text) {
             console.log("[DEBUG] Starting parseDataString (Fixed Columns)...");
             const data = { npcs: [], children: [], items: [] };
             if (!text || !text.trim()) { console.warn("[DEBUG] Input text is empty."); return data; }
             const findLastBlock = (type, fullText) => {
                 const startTag = `>>${type}_LIST_START`; const endTag = `>>${type}_LIST_END`;
                 const escapedStartTag = startTag.replace(/[-\/\\^$*+?.()|[\]{}]/g, '\\$&');
                 const escapedEndTag = endTag.replace(/[-\/\\^$*+?.()|[\]{}]/g, '\\$&');
                 const regex = new RegExp( escapedStartTag + '([\\s\\S]*?)' + escapedEndTag, 'g');
                 let lastMatch = null; let match;
                 while ((match = regex.exec(fullText)) !== null) { lastMatch = match; }
                 return lastMatch ? lastMatch[1] : null;
             };
             const sections = { 'NPC': { key: 'npcs', prefix: 'NPC:' }, 'CHILD': { key: 'children', prefix: 'CHILD:' }, 'ITEM': { key: 'items', prefix: 'ITEM:' } };
             for (const type in sections) {
                 const content = findLastBlock(type, text);
                 if (content) {
                     const lines = content.trim().split('\n').map(l => l.trim()).filter(l => l); if (lines.length < 1) continue;
                     const fieldsLineIndex = lines.findIndex(l => l.startsWith('FIELDS:')); if (fieldsLineIndex === -1) { console.warn(`[DEBUG] No FIELDS line for ${type}.`); continue; }
                     const fieldsLine = lines[fieldsLineIndex]; const fields = fieldsLine.substring(7).split('|').map(f => f.trim()).filter(f => f); if (fields.length === 0) { console.warn(`[DEBUG] No valid fields for ${type}.`); continue;}
                     console.log(`[DEBUG] Fields for ${type} (${fields.length}): [${fields.join(', ')}]`);
                     const dataLines = lines.slice(fieldsLineIndex + 1).filter(l => l.startsWith(sections[type].prefix)); console.log(`[DEBUG] Data lines for ${type}: ${dataLines.length}.`);
                     dataLines.forEach((line, lineIndex) => {
                         const values = line.substring(sections[type].prefix.length).split('|', fields.length).map(v => v.trim()); while (values.length < fields.length) { values.push(''); }
                         if (values.length === fields.length) {
                             const entry = {};
                             fields.forEach((field, index) => {
                                 let value = values[index];
                                 if (value === '') { if (field.includes('%') || field.includes('值') || field.includes('度') || field === '年龄' || field === '数量' || field === '美貌') { value = '0'; } else if (['最近性行为', '性癖', '临时状态', '永久状态', '性伴侣'].includes(field)) { value = '无'; } else { value = '无'; } }
                                 entry[field] = value;
                             }); data[sections[type].key].push(entry);
                         } else { console.warn(`[DEBUG] Field/Value mismatch for ${type} line ${lineIndex + 1}`); }
                     });
                 } else { console.log(`[DEBUG] No block content for type: ${type}`); }
             }
             console.log("[DEBUG] Finished parseDataString. Counts:", `NPCs: ${data.npcs.length}, Children: ${data.children.length}, Items: ${data.items.length}`);
             return data;
        }

