async function modifyLorebookEntries() {
  try {
    // 获取当前选择的世界书
    const lorebook = $('#world_editor_select', window.parent.document).find(':selected').text();

    if (lorebook == '--- 选择以编辑 ---') {
      return { success: false, error: '请选择世界书' };
    }

    // 获取当前世界书的元素
    const entries = await getLorebookEntries(lorebook);

    // 克隆原始entries用于比较
    const originalEntries = [...entries];

    // 预定义位置映射常量
    const NORMAL_POSITIONS = {
      'before_character_definition': 1,    // 角色定义之前
        'after_character_definition': 2,   // 角色定义之后
        'before_example_messages': 3,      // 示例消息之前
        'after_example_messages': 4,       // 示例消息之后
        'before_author_note': 5,           // 作者注释之前
        'after_author_note': 6             // 作者注释之后
    };

    // 深度相同时，排序为助手，用户，系统
    const DEPTH_POSITIONS = {
      'at_depth_as_assistant': 1, // @D🤖
      'at_depth_as_user': 2,      // @D👤
      'at_depth_as_system': 3     // @D⚙
    };

    // 预计算排序键值
    const sortKeys = new Map();

    for (let i = 0; i < entries.length; i++) {
      const entry = entries[i];
      const enabled = entry.enabled ? 0 : 1;
      const position = entry.position;

      // 计算位置类型和排名 题外话：挺早就知道几个深度位置排序有问题，但一直没人反馈。说明基本上只用到@D⚙，再加上也懒得弄就一直没改
      let posType, posRank;
      if (position in NORMAL_POSITIONS) {
        posType = 0; // 正常位置
        posRank = NORMAL_POSITIONS[position];
      } else if (position in DEPTH_POSITIONS) {
        posType = 1; // 深度位置
        posRank = DEPTH_POSITIONS[position];
      } else {
        posType = 2; // 其他位置，基本不存在
        posRank = 999;
      }

      // 深度值（仅深度位置使用）
      const depth = posType === 1 ? -(entry.depth || 0) : 0;

      // 存储排序键值
      sortKeys.set(entry.uid, [
        enabled,    // 启用状态
        posType,    // 位置类型（0=正常，1=深度，2=其他）
        depth,      // 深度值（仅深度位置有效）
        posRank,    // 位置排名
        entry.order || 0, // 顺序
        entry.uid   // UID
      ]);
    }

    // 创建优化的排序函数
    entries.sort((a, b) => {
      const aKeys = sortKeys.get(a.uid);
      const bKeys = sortKeys.get(b.uid);

      // 依次比较每个排序键
      for (let i = 0; i < 6; i++) {
        const diff = aKeys[i] - bKeys[i];
        if (diff !== 0) return diff;
      }

      return 0; // 不应该达到这里，因为uid总是不同的
    });

    // 检查是否需要更新
    const originalIndexMap = new Map();
    originalEntries.forEach(entry => {
      originalIndexMap.set(entry.uid, entry.display_index);
    });

    // 需不需要更新
    let needsUpdate = false;
    const modifiedEntries = [];

    for (let i = 0; i < entries.length; i++) {
      const entryUid = entries[i].uid; // 只取对应UID
      if (originalIndexMap.get(entryUid) !== i) {
        needsUpdate = true;
        // 只添加需要更换位置的元素
        modifiedEntries.push({
          uid: entryUid,
          display_index: i
        });
      }
    }

    if (needsUpdate) {
      await setLorebookEntries(lorebook, modifiedEntries);
      console.log('世界书已根据实际顺序排序，自定义排序为真实排序');
      return { success: true, message: '自定义真实排序完成' };
    }

    return { success: true, message: '已真实排序' };
  } catch (error) {
    console.error('排序当前世界书出错:', error);
    return { success: false, error: error.message };
  }
}
eventOn(tavern_events.WORLDINFO_UPDATED, modifyLorebookEntries);

