        }
      },
      "TableSpecificRules": {
        "时空表格": "retain only the latest row when multiple exist",
        "角色特征表格": "merge duplicate character entries",
        "角色与<user>社交表格": "delete rows containing <user>",
        "FeatureUpdateLogic": "synchronize latest status descriptions"
      },
      "GlobalCleanupRules": {
