"use strict";

/* --- API Config Manager Configuration --- */
const apiConfigManagerName = "api-config-manager";
const apiConfigManagerContainerId = 'api-config-manager-container';
const apiConfigManagerStylesId = 'api-config-manager-styles';
const apiConfigManagerSettingsKey = `st_${apiConfigManagerName}_settings`;
const apiConfigManagerDBName = 'api-config-manager-db';
const apiConfigManagerDBVersion = 1;
const apiConfigManagerStoreName = 'settings';
const apiConfigManagerEncryptionKey = `st_${apiConfigManagerName}_encryption_key`;

// Default settings with empty profiles
const apiConfigManagerDefaultSettings = {
    profiles: []
};

let apiConfigManagerSettings = {};
let apiConfigManagerDB = null;
let encryptionKey = null;

/* --- Helper Functions --- */
function apiConfigErrorCatched(fn, context = 'API Config Manager') {
    const onError = (error) => {
        let iframeName = 'UnknownIframe';
        try {
            iframeName = (typeof getIframeName === 'function') ? getIframeName() : (window.frameElement ? window.frameElement.id : 'UnknownIframe');
        } catch (e) {
            console.warn(`(${context}) Could not get iframe name:`, e);
        }
        const errorMessage = `Error in ${iframeName} (${context}) ${error.stack || error.message || error.name}`;
        console.error(errorMessage);
        if (typeof toastr !== 'undefined' && toastr) { // Check if toastr exists
            toastr.error(`Error in ${context}. Check console.`);
        } else {
             console.error(`Toastr not available to display error message in ${context}.`);
        }
    };
    return (...args) => {
        try {
            const result = fn(...args);
            if (result instanceof Promise) {
                return result.catch(error => {
                    onError(error);
                    throw error; // Re-throw after logging
                });
            }
            return result;
        } catch (error) {
            onError(error);
            throw error; // Re-throw after logging
        }
    };
}

const log = apiConfigErrorCatched((message) => {
    console.log(`[${apiConfigManagerName}] ${message}`);
}, 'log');

const fallbackCopy = apiConfigErrorCatched((text, doc) => {
    const $temp = $('<textarea>');
    $('body', doc).append($temp);
    $temp.val(text).select();
    let success = false;
    try {
        success = doc.execCommand('copy');
    } catch (e) {
        console.error("复制失败:", e);
    }
    $temp.remove();

    if (typeof toastr !== 'undefined' && toastr) {
        if (success) {
            toastr.success('秘钥已复制到剪贴板');
        } else {
            toastr.error('复制失败，请手动复制');
        }
    }
}, 'fallbackCopy');

/* --- Encryption Functions --- */
const generateEncryptionKey = apiConfigErrorCatched(async () => {
    if (!window.crypto || !window.crypto.subtle) {
        log("您的浏览器不支持Web Crypto API，将使用未加密存储");
        return null;
    }

    try {
        const key = await window.crypto.subtle.generateKey(
            {
                name: "AES-GCM",
                length: 256
            },
            true,
            ["encrypt", "decrypt"]
        );

        const exportedKey = await window.crypto.subtle.exportKey("raw", key);
        const keyBase64 = btoa(String.fromCharCode(...new Uint8Array(exportedKey)));

        localStorage.setItem(apiConfigManagerEncryptionKey, keyBase64);
        log("生成并存储了新的加密密钥");

        return key;
    } catch (err) {
        log("生成加密密钥失败: " + err.message);
        return null;
    }
}, 'generateEncryptionKey');

const loadEncryptionKey = apiConfigErrorCatched(async () => {
    if (!window.crypto || !window.crypto.subtle) {
        log("您的浏览器不支持Web Crypto API，将使用未加密存储");
        return null;
    }

    try {
        const keyBase64 = localStorage.getItem(apiConfigManagerEncryptionKey);
        if (!keyBase64) {
            log("未找到现有加密密钥，将生成新密钥");
            return await generateEncryptionKey();
        }

        const keyBytes = Uint8Array.from(atob(keyBase64), c => c.charCodeAt(0));

        const key = await window.crypto.subtle.importKey(
            "raw",
            keyBytes,
            {
                name: "AES-GCM",
                length: 256
            },
            false, // Should be false if you only need it for this session after import
            ["encrypt", "decrypt"]
        );

        log("加载现有加密密钥成功");
        return key;
    } catch (err) {
        log("加载加密密钥失败: " + err.message + ". 将尝试生成新密钥。");
        // Fallback to generating a new key if loading fails (e.g., corrupted key)
        return await generateEncryptionKey();
    }
}, 'loadEncryptionKey');

const encryptData = apiConfigErrorCatched(async (data) => {
    if (!encryptionKey || !window.crypto || !window.crypto.subtle) {
        log("无法加密数据，无可用加密密钥或不支持 Crypto API");
        return { encrypted: false, data: data }; // Return unencrypted data
    }

    try {
        const iv = window.crypto.getRandomValues(new Uint8Array(12)); // Initialization vector
        const encoder = new TextEncoder();
        const dataBytes = encoder.encode(JSON.stringify(data));

        const encryptedBytes = await window.crypto.subtle.encrypt(
            {
                name: "AES-GCM",
                iv: iv
            },
            encryptionKey,
            dataBytes
        );

        // Combine IV and encrypted data for storage
        const encryptedBase64 = btoa(String.fromCharCode(...new Uint8Array(encryptedBytes)));
        const ivBase64 = btoa(String.fromCharCode(...iv));

        return {
            encrypted: true,
            data: {
                iv: ivBase64,
                encryptedData: encryptedBase64
            }
        };
    } catch (err) {
        log("加密数据失败: " + err.message);
        // Fallback: return unencrypted if encryption fails
        return { encrypted: false, data: data };
    }
}, 'encryptData');

const decryptData = apiConfigErrorCatched(async (encryptedDataObj) => {
    // Check if the input structure is as expected
    if (!encryptedDataObj || typeof encryptedDataObj.encrypted === 'undefined') {
         log("解密数据格式无效，直接返回原始数据");
         return encryptedDataObj; // Or perhaps throw an error, depending on desired handling
    }

    if (!encryptedDataObj.encrypted) {
        // Data wasn't encrypted in the first place
        return encryptedDataObj.data;
    }

    if (!encryptionKey || !window.crypto || !window.crypto.subtle) {
        log("无法解密数据，无可用加密密钥或不支持 Crypto API");
        throw new Error("解密失败：无可用加密密钥或浏览器不支持");
    }

    // Validate the structure of encrypted data
    if (!encryptedDataObj.data || !encryptedDataObj.data.iv || !encryptedDataObj.data.encryptedData) {
        log("解密失败：加密数据结构不完整");
        throw new Error("解密失败：数据结构不完整");
    }

    try {
        const iv = Uint8Array.from(atob(encryptedDataObj.data.iv), c => c.charCodeAt(0));
        const encryptedBytes = Uint8Array.from(atob(encryptedDataObj.data.encryptedData), c => c.charCodeAt(0));

        const decryptedBytes = await window.crypto.subtle.decrypt(
            {
                name: "AES-GCM",
                iv: iv
            },
            encryptionKey,
            encryptedBytes
        );

        const decoder = new TextDecoder();
        const decryptedText = decoder.decode(decryptedBytes);

        return JSON.parse(decryptedText);
    } catch (err) {
        log("解密数据失败: " + err.message);
        // Consider what to do here. Throwing error might be best to signal failure.
        // Returning null or default settings might hide the problem.
        throw new Error("解密失败：" + err.message);
    }
}, 'decryptData');


/* --- IndexedDB Functions --- */
const openDatabase = apiConfigErrorCatched(() => {
    return new Promise((resolve, reject) => {
        if (!window.indexedDB) {
            log("您的浏览器不支持IndexedDB，将使用localStorage");
            resolve(null); // Resolve with null to indicate IDB is not available
            return;
        }

        const request = indexedDB.open(apiConfigManagerDBName, apiConfigManagerDBVersion);

        request.onerror = (event) => {
            log("IndexedDB打开失败: " + event.target.errorCode);
            resolve(null); // Indicate failure
        };

        request.onsuccess = (event) => {
            apiConfigManagerDB = event.target.result;
            log("IndexedDB打开成功");
            resolve(apiConfigManagerDB); // Resolve with the DB instance
        };

        request.onupgradeneeded = (event) => {
            log("创建或升级IndexedDB");
            const db = event.target.result;
            // Check if the object store already exists before creating
            if (!db.objectStoreNames.contains(apiConfigManagerStoreName)) {
                db.createObjectStore(apiConfigManagerStoreName, { keyPath: 'id' });
                log("创建了设置存储: " + apiConfigManagerStoreName);
            }
            // Handle future version upgrades here if needed
        };
    });
}, 'openDatabase');

const saveToIndexedDB = apiConfigErrorCatched(async (settings) => {
    return new Promise(async (resolve) => { // No reject needed, resolve with true/false
        if (!apiConfigManagerDB) {
            log("IndexedDB未打开，无法保存");
            resolve(false);
            return;
        }

        try {
            const encryptedDataObj = await encryptData(settings);

            // Use a transaction for the put operation
            const transaction = apiConfigManagerDB.transaction([apiConfigManagerStoreName], "readwrite");
            const store = transaction.objectStore(apiConfigManagerStoreName);

            const settingsData = {
                id: apiConfigManagerSettingsKey, // Use the constant key
                value: encryptedDataObj // Store the object containing encrypted status and data
            };

            const request = store.put(settingsData); // Use put to insert or update

            request.onsuccess = () => {
                log(`配置已${encryptedDataObj.encrypted ? '加密并' : ''}保存到IndexedDB`);
                resolve(true);
            };

            request.onerror = (event) => {
                log("保存到IndexedDB失败: " + event.target.error);
                resolve(false);
            };

            // Optional: Handle transaction errors
            transaction.onerror = (event) => {
                log("IndexedDB 事务失败: " + event.target.error);
                // Ensure promise resolves even on transaction error
                if (!request.readyState || request.readyState === 'pending') {
                     resolve(false);
                }
            };

        } catch (e) {
            log("保存到IndexedDB时出错: " + e.message);
            resolve(false);
        }
    });
}, 'saveToIndexedDB');


const loadFromIndexedDB = apiConfigErrorCatched(() => {
    return new Promise(async (resolve) => { // No reject needed, resolve with data or null
        if (!apiConfigManagerDB) {
            log("IndexedDB未打开，无法加载");
            resolve(null);
            return;
        }

        try {
            const transaction = apiConfigManagerDB.transaction([apiConfigManagerStoreName], "readonly");
            const store = transaction.objectStore(apiConfigManagerStoreName);
            const request = store.get(apiConfigManagerSettingsKey); // Get by key

            request.onsuccess = async () => {
                if (request.result && request.result.value) {
                    log("从IndexedDB加载了原始配置对象");
                    try {
                        const decryptedData = await decryptData(request.result.value);
                        // Check if decryption returned actual data (not error/null)
                        if (decryptedData) {
                             log(`配置已${request.result.value.encrypted ? '解密' : '直接'}加载`);
                            resolve(decryptedData);
                        } else {
                             log("解密后未获得有效数据，返回 null");
                             resolve(null);
                        }
                    } catch (e) {
                        log("解密从IndexedDB加载的配置失败: " + e.message + ". 可能需要清除存储或生成新密钥。");
                        // Handle decryption failure, e.g., return null or default
                        resolve(null);
                    }
                } else {
                    log("IndexedDB中未找到配置 (key: " + apiConfigManagerSettingsKey + ")");
                    resolve(null); // No data found for the key
                }
            };

            request.onerror = (event) => {
                log("从IndexedDB加载失败: " + event.target.error);
                resolve(null); // Indicate load failure
            };

             // Optional: Handle transaction errors
            transaction.onerror = (event) => {
                log("IndexedDB 读取事务失败: " + event.target.error);
                if (!request.readyState || request.readyState === 'pending') {
                    resolve(null);
                }
            };
        } catch (e) {
            log("从IndexedDB加载时出错: " + e.message);
            resolve(null);
        }
    });
}, 'loadFromIndexedDB');


/* --- Settings Storage Logic --- */
const saveApiConfigSettings = apiConfigErrorCatched(async () => {
    try {
        log(`开始保存 ${apiConfigManagerSettings.profiles.length} 个配置...`);

        let savedSuccessfully = false;
        if (apiConfigManagerDB) {
            savedSuccessfully = await saveToIndexedDB(apiConfigManagerSettings);
            log(`保存到IndexedDB: ${savedSuccessfully ? '成功' : '失败'}`);
        } else {
             log("IndexedDB 不可用，尝试 localStorage");
        }

        // Fallback to localStorage if IndexedDB is unavailable or saving failed
        if (!savedSuccessfully) {
            try {
                const settingsString = JSON.stringify(apiConfigManagerSettings);
                localStorage.setItem(apiConfigManagerSettingsKey, settingsString);
                log(`API配置已成功保存到localStorage (作为后备): ${apiConfigManagerSettings.profiles.length} 个配置`);
                savedSuccessfully = true; // Mark as saved via fallback
            } catch (error) {
                console.error(`[${apiConfigManagerName}] 保存到 localStorage 失败:`, error);
                // If both fail, the overall save failed
            }
        }

        // Verification Step (Optional but recommended)
        if (savedSuccessfully) {
            log("开始保存后验证...");
            let verifiedSettings = null;
            if (apiConfigManagerDB && await saveToIndexedDB(apiConfigManagerSettings)) { // Prefer IDB for verification if it was the primary save method
                 verifiedSettings = await loadFromIndexedDB();
                 log(`保存验证(IndexedDB): ${verifiedSettings ? `成功读取 ${verifiedSettings.profiles?.length ?? 0} 个配置` : '读取失败或无数据'}`);
            } else {
                 try {
                    const savedData = localStorage.getItem(apiConfigManagerSettingsKey);
                    if (savedData) {
                        verifiedSettings = JSON.parse(savedData);
                         log(`保存验证(localStorage): 成功读取 ${verifiedSettings.profiles?.length ?? 0} 个配置`);
                    } else {
                        log(`保存验证(localStorage): 未找到数据`);
                    }
                 } catch (e) {
                     log(`保存验证(localStorage)时解析失败: ${e.message}`);
                 }
            }
             // Simple check: Compare profile counts
             if (verifiedSettings && verifiedSettings.profiles?.length === apiConfigManagerSettings.profiles.length) {
                 log("保存验证：配置数量匹配。");
             } else {
                 log(`保存验证：配置数量不匹配或验证失败 (Expected: ${apiConfigManagerSettings.profiles.length}, Got: ${verifiedSettings?.profiles?.length ?? 'N/A'})`);
                 // Consider more robust verification if needed (e.g., deep comparison)
             }
        }

        if (savedSuccessfully && typeof toastr !== 'undefined' && toastr) {
            toastr.success('API配置已保存');
        } else if (!savedSuccessfully && typeof toastr !== 'undefined' && toastr) {
             toastr.error("无法保存API配置，请查看控制台了解详情。");
        }

        return savedSuccessfully; // Return the overall success status

    } catch (error) {
        console.error(`[${apiConfigManagerName}] 保存设置时发生意外错误:`, error);
        if (typeof toastr !== 'undefined' && toastr) {
            toastr.error("保存API配置时出错，请检查控制台。");
        }
        return false; // Indicate failure
    }
}, 'saveApiConfigSettings');


const loadApiConfigSettingsFromStorage = apiConfigErrorCatched(async () => {
    let loadedSettings = null;
    let loadedFrom = '默认值'; // Track source

    // 1. Try loading from IndexedDB first
    if (apiConfigManagerDB) {
        log("尝试从 IndexedDB 加载...");
        loadedSettings = await loadFromIndexedDB();
        if (loadedSettings) {
            log(`设置已从 IndexedDB 加载，包含 ${loadedSettings.profiles?.length ?? 0} 个配置。`);
            loadedFrom = 'IndexedDB';
        } else {
            log("从 IndexedDB 加载失败或未找到数据，尝试 localStorage。");
        }
    } else {
         log("IndexedDB 不可用，尝试 localStorage。");
    }

    // 2. Fallback to localStorage if IndexedDB failed or wasn't available
    if (!loadedSettings) {
        log("尝试从 localStorage 加载...");
        try {
            const storedSettings = localStorage.getItem(apiConfigManagerSettingsKey);
            if (storedSettings) {
                loadedSettings = JSON.parse(storedSettings);
                log(`设置已从 localStorage 加载，包含 ${loadedSettings.profiles?.length ?? 0} 个配置。`);
                 loadedFrom = 'localStorage';
                 // Optional: If IDB is available now, migrate localStorage data to IDB
                 if (apiConfigManagerDB) {
                     log("检测到localStorage数据，尝试迁移到 IndexedDB...");
                     const migrated = await saveToIndexedDB(loadedSettings);
                     if (migrated) {
                         log("数据已成功迁移到 IndexedDB。考虑清除localStorage中的旧数据。");
                         // localStorage.removeItem(apiConfigManagerSettingsKey); // Uncomment to remove after migration
                     } else {
                         log("迁移到 IndexedDB 失败。");
                     }
                 }
            } else {
                log("在 localStorage 中也未找到设置。");
            }
        } catch (error) {
            console.error(`[${apiConfigManagerName}] 从 localStorage 加载或解析设置时出错:`, error);
            if (typeof toastr !== 'undefined' && toastr) {
                 toastr.warning("无法加载localStorage中的API配置，可能已损坏。将使用默认设置。");
            }
            // Don't assign default here yet, let the final step handle it
        }
    }

    // 3. Use default settings if neither storage yielded results
    if (!loadedSettings) {
        log("未能从任何存储中加载设置，将使用默认设置。");
        loadedSettings = JSON.parse(JSON.stringify(apiConfigManagerDefaultSettings)); // Deep copy defaults
        loadedFrom = '默认值';
    }

    // Ensure loaded settings have the expected structure (at least 'profiles' array)
    if (!loadedSettings || !Array.isArray(loadedSettings.profiles)) {
        console.warn(`[${apiConfigManagerName}] 加载的设置格式无效 (来源: ${loadedFrom})，重置为默认值。`, loadedSettings);
         if (typeof toastr !== 'undefined' && toastr) {
             toastr.warning("加载的API配置格式无效，已重置为默认。");
         }
        loadedSettings = JSON.parse(JSON.stringify(apiConfigManagerDefaultSettings));
        loadedFrom = '默认值 (因格式无效)';
    }

    // Assign to the global variable
    apiConfigManagerSettings = loadedSettings;

    log(`配置加载完成 (来源: ${loadedFrom})。当前配置数量: ${apiConfigManagerSettings.profiles.length}`);

}, 'loadApiConfigSettingsFromStorage');


