const renderProfileTabs = apiConfigErrorCatched(() => {
    const parentDoc = window.parent.document;
    const $container = $('.profile-tabs-container', parentDoc);
    if (!$container.length) {
        log("错误：找不到 .profile-tabs-container 元素");
        return;
    }
    $container.empty(); // Clear existing tabs

    log(`开始渲染 ${apiConfigManagerSettings.profiles?.length ?? 0} 个配置标签...`);

    const profiles = apiConfigManagerSettings.profiles || [];
    let activeIndex = -1; // Find index of currently active tab if exists

    // Find currently active index *before* clearing
     const $currentActive = $container.find('.profile-tab.active');
     if ($currentActive.length) {
         activeIndex = parseInt($currentActive.data('index'));
     }

    // Handle empty profiles: Add a default one if needed
    if (profiles.length === 0) {
        log("没有配置，添加一个默认配置用于显示");
        profiles.push({
            name: "默认配置",
            keys: [''], // Start with one empty key
            endpoint: ""
        });
        // Since we added the first profile, it should be active
        activeIndex = 0;
    }

    // Create tabs for each profile
    profiles.forEach((profile, index) => {
        const tabName = profile.name || `配置 ${index + 1}`;
        const isActive = index === activeIndex; // Determine if this tab should be active
        const $tab = $(`
            <div class="profile-tab ${isActive ? 'active' : ''}" data-index="${index}" draggable="true"
                style="display: inline-block; padding: 5px 10px; margin-right: 5px; background: ${isActive ? 'rgba(58, 123, 213, 0.2)' : 'rgba(255, 255, 255, 0.5)'};
                border: 1px solid ${isActive ? 'rgba(58, 123, 213, 0.5)' : 'rgba(58, 123, 213, 0.3)'}; border-radius: 6px; cursor: pointer; transition: all 0.2s;
                font-size: 12px; color: ${isActive ? '#1e3a8a' : '#3a7bd5'}; position: relative; max-width: 120px; overflow: hidden; text-overflow: ellipsis; white-space: nowrap;"
                title="${tabName}">
                ${tabName}
            </div>
        `);
        $container.append($tab);
    });

     // Ensure at least one tab is active if activeIndex was invalid or not found
     if ($container.find('.profile-tab.active').length === 0 && profiles.length > 0) {
         log("没有找到激活的标签，默认激活第一个");
         $container.find('.profile-tab').first().addClass('active').css({
             'background': 'rgba(58, 123, 213, 0.2)',
             'border': '1px solid rgba(58, 123, 213, 0.5)',
             'color': '#1e3a8a'
         });
          activeIndex = 0; // Update activeIndex
     }

     // Re-initialize drag and drop after rendering tabs
     initDragAndDrop();

    log(`配置标签渲染完成，激活索引: ${activeIndex}`);
}, 'renderProfileTabs');


