/* --- 处理配置标签的拖放功能 --- */
const initDragAndDrop = apiConfigErrorCatched(() => {
    const parentDoc = window.parent.document;
    let dragSrcEl = null;

    function handleDragStart(e) {
        // 'this' is the source element
        dragSrcEl = this;

        // Use 'text/plain' for better compatibility, store the index
        e.dataTransfer.effectAllowed = 'move';
        e.dataTransfer.setData('text/plain', this.getAttribute('data-index'));

        // Add styling to indicate dragging
        this.style.opacity = '0.5';
        this.classList.add('dragging');
    }

    function handleDragOver(e) {
        if (e.preventDefault) {
            e.preventDefault(); // Necessary to allow dropping
        }
        e.dataTransfer.dropEffect = 'move';
        return false;
    }

    function handleDragEnter(e) {
        // 'this' is the potential drop target
        // Add visual feedback (unless it's the source element itself)
         if (this !== dragSrcEl) {
             this.classList.add('over');
             // Optional: Modify style directly if needed, but class is better
             this.style.background = 'rgba(58, 123, 213, 0.15)';
         }
    }

    function handleDragLeave(e) {
        // 'this' is the element the mouse left
        this.classList.remove('over');
         // Reset background based on whether it's active or not
         if (this.classList.contains('active')) {
             this.style.background = 'rgba(58, 123, 213, 0.2)';
         } else {
             this.style.background = 'rgba(255, 255, 255, 0.5)';
         }
    }

    function handleDrop(e) {
        if (e.stopPropagation) {
            e.stopPropagation(); // Stops the browser from redirecting.
        }

        // 'this' is the drop target element
        // Don't do anything if dropping onto the source element
        if (dragSrcEl !== this) {
            const srcIndex = parseInt(e.dataTransfer.getData('text/plain')); // Get index from data
            const destIndex = parseInt(this.getAttribute('data-index'));

             log(`Drop event: Moving from index ${srcIndex} to ${destIndex}`);

             // --- Array Reordering Logic ---
             if (!isNaN(srcIndex) && !isNaN(destIndex) && apiConfigManagerSettings.profiles) {
                 // 1. Remove the item from the source index
                 const [movedProfile] = apiConfigManagerSettings.profiles.splice(srcIndex, 1);

                 // 2. Insert the item at the destination index
                 if (movedProfile) {
                    apiConfigManagerSettings.profiles.splice(destIndex, 0, movedProfile);
                    log(`配置数组已更新`);

                     // 3. Save current form data (important BEFORE re-rendering tabs)
                     //    We need to save based on the VISUAL state before the drop messes up the indices
                     saveCurrentProfileData().then(() => {
                         // 4. Re-render tabs to reflect new order and update indices
                         renderProfileTabs(); // This will also re-bind drag events via initDragAndDrop called within

                         // 5. Re-select the *moved* item (which is now at destIndex) and render its unit
                         const $newActiveTab = $(`.profile-tab[data-index="${destIndex}"]`, parentDoc);
                         if ($newActiveTab.length) {
                             // Simulate click to handle active state styling and rendering unit
                             $newActiveTab.trigger('click'); // Trigger the click handler
                              log(`已重新选择移动后的标签 (新索引 ${destIndex}) 并渲染其内容`);
                         } else {
                             log(`错误：无法找到移动后的目标标签 data-index="${destIndex}"`);
                             // Fallback: select the first tab if the target is somehow gone
                             $('.profile-tab', parentDoc).first().trigger('click');
                         }
                     }).catch(err => {
                          console.error("Error during save/re-render after drop:", err);
                          // Attempt to recover by re-rendering tabs anyway
                          renderProfileTabs();
                          $('.profile-tab', parentDoc).first().trigger('click');
                     });

                 } else {
                      log("拖放错误：无法获取要移动的配置");
                       // Re-render to be safe
                       renderProfileTabs();
                       $('.profile-tab', parentDoc).first().trigger('click');
                 }
             } else {
                 log(`拖放错误：无效的索引 (src: ${srcIndex}, dest: ${destIndex})`);
                  // Reset visual state of the target
                 this.classList.remove('over');
                 handleDragLeave.call(this, e); // Use call to set 'this' correctly
             }

        } else {
             log("Drop cancelled: source is the same as target.");
        }


        return false; // Prevent default drop action
    }


    function handleDragEnd(e) {
        // 'this' is the source element
         log("DragEnd event on source element");
        this.style.opacity = '1'; // Reset opacity
        this.classList.remove('dragging');


        // Clean up 'over' class from all tabs
        const tabs = parentDoc.querySelectorAll('.profile-tab');
        tabs.forEach(function(tab) {
            tab.classList.remove('over');
            // Reset background based on active state, as dragleave might not fire everywhere
            if (!tab.classList.contains('dragging')) { // Don't reset if it's still marked as dragging briefly
                 if (tab.classList.contains('active')) {
                     tab.style.background = 'rgba(58, 123, 213, 0.2)';
                 } else {
                     tab.style.background = 'rgba(255, 255, 255, 0.5)';
                 }
            }
        });
    }

    function bindDragEvents() {
        const tabs = parentDoc.querySelectorAll('.profile-tab');
        log(`绑定拖放事件到 ${tabs.length} 个标签...`);
        tabs.forEach(function(tab) {
            // Remove existing listeners first to prevent duplicates if called multiple times
            tab.removeEventListener('dragstart', handleDragStart, false);
            tab.removeEventListener('dragenter', handleDragEnter, false);
            tab.removeEventListener('dragover', handleDragOver, false);
            tab.removeEventListener('dragleave', handleDragLeave, false);
            tab.removeEventListener('drop', handleDrop, false);
            tab.removeEventListener('dragend', handleDragEnd, false);

            // Add new listeners
            tab.addEventListener('dragstart', handleDragStart, false);
            tab.addEventListener('dragenter', handleDragEnter, false);
            tab.addEventListener('dragover', handleDragOver, false);
