class WorldSimulator {
    constructor() {
        this.eventBus = new EventBus();
        /** @type {NPCBrain[]} */
        this.npcs = Array.from({ length: 100 }, () => 
            new NPCBrain(Math.random() > 0.5 ? 'warrior' : 'merchant')
        );
        this.gameTime = new WorldTime(12, 1, 'full');
    }

    // å¯åŠ¨ä¸–ç•Œå¾ªç¯
    run() {
        setInterval(() => {
            this.#updateWorldState();
            this.eventBus.dispatch();
        }, 1000 / 60); // 60å¸§æ›´æ–°
    }

    // æ›´æ–°æ‰€æœ‰å®ä½“çŠ¶æ€
    #updateWorldState() {
        this.npcs.forEach(npc => {
            const context = this.#generateNPCContext();
            const action = npc.decideAction(context);
            this.#handleNPCAction(action);
        });
    }

    // ç”ŸæˆNPCå†³ç­–ä¸Šä¸‹æ–‡
    #generateNPCContext() {
        return {
            safety: Math.random(),
            combat: Math.random(),
            wealth: Math.random()
        };
    }

    // å¤„ç†NPCè¡ŒåŠ¨
    #handleNPCAction(action) {
        if (action !== 'wait') {
            this.eventBus.publish('npc_action', { 
                action,
                timestamp: Date.now()
            }, 3);
        }
    }
}

