    async function callCustomOpenAI(systemMsgContent, userPromptContent) { /* ... (no change) ... */
        if (!customApiConfig.url || !customApiConfig.model) {
            throw new Error("è‡ªå®šä¹‰API URLæˆ–æ¨¡å‹æœªé…ç½®ã€‚");
        }
        let fullApiUrl = customApiConfig.url;
        if (!fullApiUrl.endsWith('/')) { fullApiUrl += '/'; }
        if (fullApiUrl.endsWith('/v1/')) { fullApiUrl += 'chat/completions'; }
        else if (!fullApiUrl.includes('/chat/completions')) { fullApiUrl += 'v1/chat/completions';}

        const headers = { 'Content-Type': 'application/json' };
        if (customApiConfig.apiKey) { headers['Authorization'] = `Bearer ${customApiConfig.apiKey}`; }
        const body = JSON.stringify({
            model: customApiConfig.model,
            messages: [ { role: "system", content: systemMsgContent }, { role: "user", content: userPromptContent } ],
        });
        logDebug("è°ƒç”¨è‡ªå®šä¹‰API:", fullApiUrl, "æ¨¡å‹:", customApiConfig.model, "é™„å¸¦å¤´éƒ¨ä¿¡æ¯:", headers);
        const response = await fetch(fullApiUrl, { method: 'POST', headers: headers, body: body });
        if (!response.ok) {
            const errorText = await response.text();
            logError("è‡ªå®šä¹‰APIè°ƒç”¨å¤±è´¥:", response.status, response.statusText, errorText);
            throw new Error(`è‡ªå®šä¹‰APIè¯·æ±‚å¤±è´¥: ${response.status} ${response.statusText}. è¯¦æƒ…: ${errorText}`);
        }
        const data = await response.json();
        logDebug("è‡ªå®šä¹‰APIå“åº”:", data);
        if (data.choices && data.choices.length > 0 && data.choices[0].message && data.choices[0].message.content) {
            return data.choices[0].message.content.trim();
        } else {
            logError("è‡ªå®šä¹‰APIå“åº”æ ¼å¼ä¸æ­£ç¡®æˆ–æ— å†…å®¹:", data);
            throw new Error("è‡ªå®šä¹‰APIå“åº”æ ¼å¼ä¸æ­£ç¡®æˆ–æœªè¿”å›å†…å®¹ã€‚");
        }
    }
