            const finalStatusText = unsummarizedCount > 0 && unsummarizedCount < triggerThreshold ?
                `è‡ªåŠ¨æ€»ç»“å®Œæˆã€‚å‰©ä½™ ${unsummarizedCount} æ¥¼æœªè¾¾åˆ°è§¦å‘é˜ˆå€¼ (${triggerThreshold})ã€‚` :
                (unsummarizedCount === 0 ? "æ‰€æœ‰èŠå¤©è®°å½•å·²è‡ªåŠ¨æ€»ç»“å®Œæ¯•ï¼" : `è‡ªåŠ¨æ€»ç»“å·²å¤„ç†å®Œæ¯• (æœªæ€»ç»“ ${unsummarizedCount} < é˜ˆå€¼ ${triggerThreshold})ã€‚`);
            showToastr(unsummarizedCount === 0 ? "success" : "info", finalStatusText);
