            if($statusMessageSpan) $statusMessageSpan.text("é”™è¯¯ï¼šè‡ªå®šä¹‰AIæœªé…ç½®æˆ–æœªé€‰æ¨¡å‹ã€‚");
            else showToastr("error", "é”™è¯¯ï¼šè‡ªå®šä¹‰AIæœªé…ç½®æˆ–æœªé€‰æ¨¡å‹ã€‚");
            return false;
        }

        let proceedToUpload = true;
