$(() => {
  // åœ¨æ”¶åˆ°æ¶ˆæ¯åè§¦å‘æ ‡ç­¾ä¿®å¤åŠŸèƒ½
  eventOn(tavern_events.MESSAGE_RECEIVED, () => {
    // è·å–æœ€æ–°çš„ä¸€æ¡æ¶ˆæ¯
    const lastMessage = SillyTavern.chat[SillyTavern.chat.length - 1];

    // æ£€æŸ¥æ¶ˆæ¯æ˜¯å¦å­˜åœ¨
    if (lastMessage && lastMessage.mes) {
      // ä¿®å¤æ¶ˆæ¯ä¸­çš„æ ‡ç­¾
      lastMessage.mes = fixMissingTags(lastMessage.mes);
    }
  });

  /**
   * ç®€å•ä¿®å¤HTMLä¸­ç¼ºå¤±çš„é—­åˆæ ‡ç­¾
   * @param {string} html - åŒ…å«å¯èƒ½ç¼ºå¤±é—­åˆæ ‡ç­¾çš„HTMLå­—ç¬¦ä¸²
   * @return {string} - ä¿®å¤åçš„HTMLå­—ç¬¦ä¸²
   */
  function fixMissingTags(html) {
    if (!html || typeof html !== 'string') {
      return html;
    }

    // ä½¿ç”¨æ­£åˆ™è¡¨è¾¾å¼æ‰¾å‡ºæ‰€æœ‰çš„å¼€æ ‡ç­¾å’Œé—­æ ‡ç­¾
    const tagRegex = /<\/?([^\s>/]+)[^>]*>/g;
    const tags = [];
    let match;

    // æ”¶é›†æ‰€æœ‰æ ‡ç­¾ä¿¡æ¯
    while ((match = tagRegex.exec(html)) !== null) {
      const fullTag = match[0];
      const tagName = match[1];
      const isClosingTag = fullTag.startsWith('</');
      const position = match.index;
      const endPosition = position + fullTag.length;

      tags.push({
        fullTag,
        tagName,
        isClosingTag,
        position,
        endPosition
      });
    }

    // è®¡ç®—æ¯ç§æ ‡ç­¾çš„å¼€é—­æ ‡ç­¾æ•°é‡
    const tagCounts = {};
    tags.forEach(tag => {
      if (!tagCounts[tag.tagName]) {
        tagCounts[tag.tagName] = { open: 0, close: 0 };
      }

      if (tag.isClosingTag) {
        tagCounts[tag.tagName].close++;
      } else {
        tagCounts[tag.tagName].open++;
      }
    });

    // æ‰¾å‡ºç¼ºå°‘é—­åˆæ ‡ç­¾çš„æ ‡ç­¾
    const unclosedTags = {};
    for (const [tagName, count] of Object.entries(tagCounts)) {
      if (count.open > count.close) {
        unclosedTags[tagName] = count.open - count.close;
      }
    }

    // å¦‚æœæ²¡æœ‰æœªé—­åˆçš„æ ‡ç­¾ï¼Œç›´æ¥è¿”å›åŸå§‹HTML
    if (Object.keys(unclosedTags).length === 0) {
      return html;
    }

    // ä¸ºæ¯ä¸ªæœªé—­åˆçš„æ ‡ç­¾æ‰¾åˆ°åˆé€‚çš„æ’å…¥ä½ç½®
    const insertions = [];

    // æŒ‰ç…§æ ‡ç­¾å‡ºç°é¡ºåºå¤„ç†
    for (let i = 0; i < tags.length; i++) {
      const tag = tags[i];

      // åªå¤„ç†å¼€æ ‡ç­¾
      if (tag.isClosingTag) continue;

      // æ£€æŸ¥è¿™ä¸ªæ ‡ç­¾æ˜¯å¦ç¼ºå°‘é—­åˆæ ‡ç­¾
      if (unclosedTags[tag.tagName] && unclosedTags[tag.tagName] > 0) {
        // é»˜è®¤åœ¨ä¸‹ä¸€ä¸ªæ ‡ç­¾ä¹‹å‰æ’å…¥
        let insertPos = html.length;
        let foundNextTag = false;

        // æŸ¥æ‰¾ä¸‹ä¸€ä¸ªæ ‡ç­¾
        for (let j = i + 1; j < tags.length; j++) {
          if (tags[j].position > tag.endPosition) {
            insertPos = tags[j].position;
            foundNextTag = true;
            break;
          }
        }

        // å°è¯•æ‰¾åˆ°ä¸Šä¸€è¡Œæœ«å°¾
        if (foundNextTag) {
          const textBetween = html.substring(tag.endPosition, insertPos);
          const lastNewlinePos = textBetween.lastIndexOf('\n');

          if (lastNewlinePos !== -1) {
            insertPos = tag.endPosition + lastNewlinePos;
          }
        }

        // è®°å½•æ’å…¥ä½ç½®
        insertions.push({
          position: insertPos,
          tag: `</${tag.tagName}>`
        });

        // å‡å°‘éœ€è¦é—­åˆçš„æ ‡ç­¾æ•°é‡
        unclosedTags[tag.tagName]--;

        // å¦‚æœè¿™ä¸ªæ ‡ç­¾å·²ç»å…¨éƒ¨ä¿®å¤ï¼Œä»æœªé—­åˆæ ‡ç­¾åˆ—è¡¨ä¸­ç§»é™¤
        if (unclosedTags[tag.tagName] <= 0) {
          delete unclosedTags[tag.tagName];
        }
      }
    }

    // æŒ‰ä½ç½®ä»åå¾€å‰æ’åºï¼Œé¿å…ä½ç½®å˜åŒ–
    insertions.sort((a, b) => b.position - a.position);

    // æ‰§è¡Œæ’å…¥
    let result = html;
    insertions.forEach(insertion => {
      result = result.substring(0, insertion.position) + 
               insertion.tag + 
               result.substring(insertion.position);
    });

    return result;
  }
});

