        try {
            const entries = await TavernHelper_API.getLorebookEntries(currentPrimaryLorebook);
            let maxFloor = -1;
            for (const entry of entries) {
                if (entry.enabled && entry.comment && entry.comment.startsWith(SUMMARY_LOREBOOK_PREFIX + currentChatFileIdentifier + "-")) {
                    const match = entry.comment.match(/-(\d+)-(\d+)$/);
                    if (match && match.length === 3) {
                        const endFloorInEntry = parseInt(match[2], 10);
                        if (!isNaN(endFloorInEntry)) {
                            maxFloor = Math.max(maxFloor, endFloorInEntry -1);
                        }
                    }
                }
            }
            return maxFloor;
        } catch (error) {
            logError("ä»ä¸–ç•Œä¹¦è·å–æœ€å¤§æ€»ç»“æ¥¼å±‚æ—¶å‡ºé”™:", error);
            return -1;
        }
    }
    async function applyPersistedSummaryStatusFromLorebook() { /* ... (no change) ... */
        if (allChatMessages.length === 0) {
            logDebug("æ²¡æœ‰èŠå¤©è®°å½•ï¼Œæ— éœ€ä»ä¸–ç•Œä¹¦æ¢å¤çŠ¶æ€ã€‚");
            return;
        }
        allChatMessages.forEach(msg => msg.summarized = false);
        const maxSummarizedFloor = await getMaxSummarizedFloorFromActiveLorebookEntry();
        if (maxSummarizedFloor >= 0) {
            logDebug(`ä»ä¸–ç•Œä¹¦æ£€æµ‹åˆ°æœ€å¤§å·²æ€»ç»“æ¥¼å±‚ (0-based): ${maxSummarizedFloor}`);
            for (let i = 0; i <= maxSummarizedFloor && i < allChatMessages.length; i++) {
                if (allChatMessages[i]) {
                    allChatMessages[i].summarized = true;
                }
            }
        } else {
            logDebug("å½“å‰èŠå¤©åœ¨ä¸–ç•Œä¹¦ä¸­æ²¡æœ‰æ‰¾åˆ°æœ‰æ•ˆçš„å·²å¯ç”¨æ€»ç»“æ¡ç›®ï¼Œæˆ–è§£ææ¥¼å±‚å¤±è´¥ã€‚");
        }
    }

    // Debounced handler for new message events
    async function handleNewMessageDebounced(eventType = "unknown") {
        logDebug(`New message event (${eventType}) detected, debouncing for ${NEW_MESSAGE_DEBOUNCE_DELAY}ms...`);
        clearTimeout(newMessageDebounceTimer);
        newMessageDebounceTimer = setTimeout(async () => {
            logDebug("Debounced new message processing triggered.");
            if (isAutoSummarizing) {
                logDebug("New message processing: Auto-summary already in progress. Skipping check.");
                return;
            }
            if (!coreApisAreReady) {
                 logDebug("New message processing: Core APIs not ready. Skipping check.");
                return;
            }
            // It's crucial that allChatMessages is up-to-date before checking.
            await loadAllChatMessages(); // Reload all messages to ensure we have the latest count
            await applyPersistedSummaryStatusFromLorebook(); // Refresh summarized status from lorebook
            await triggerAutomaticSummarizationIfNeeded(); // Then check if we need to summarize
        }, NEW_MESSAGE_DEBOUNCE_DELAY);
    }


    async function triggerAutomaticSummarizationIfNeeded() { /* ... (no change from v0.3.22) ... */
        if (!coreApisAreReady) {
            logDebug("Automatic summarization trigger: Core APIs not ready.");
            return;
        }
        if (isAutoSummarizing) {
            logDebug("Automatic summarization trigger: Process already running.");
            return;
        }

        if (!customApiConfig.url || !customApiConfig.model) {
            logDebug("Automatic summarization trigger: API not configured. Skipping.");
            return;
        }

        if (allChatMessages.length === 0) {
            logDebug("Automatic summarization trigger: No messages loaded. Skipping.");
            return;
        }

        const effectiveChunkSize = getEffectiveChunkSize("system_trigger");

        const maxSummarizedFloor = await getMaxSummarizedFloorFromActiveLorebookEntry();
        const unsummarizedCount = allChatMessages.length - (maxSummarizedFloor + 1);

        logDebug(`Automatic summarization trigger check: Unsummarized: ${unsummarizedCount}, EffectiveChunkSize: ${effectiveChunkSize}`);

        if (unsummarizedCount >= effectiveChunkSize) {
            showToastr("info", `æ£€æµ‹åˆ° ${unsummarizedCount} æ¡æœªæ€»ç»“æ¶ˆæ¯ï¼Œå°†è‡ªåŠ¨å¼€å§‹æ€»ç»“ (é—´éš”: ${effectiveChunkSize} å±‚)ã€‚`);
            logWarn(`AUTOMATICALLY triggering summarization. Unsummarized: ${unsummarizedCount}, ChunkSize: ${effectiveChunkSize}`);
            handleAutoSummarize();
        } else {
            logDebug("Automatic summarization trigger: Not enough unsummarized messages to trigger automatically.");
        }
    }

