        try {
            const savedSettingsJson = localStorage.getItem(STORAGE_KEY_ADVANCED_HIDE_SETTINGS);
            if (savedSettingsJson) {
                const parsed = JSON.parse(savedSettingsJson);
                 // Ensure full structure by merging with defaults
                return {
