    async function getMaxSummarizedFloorFromActiveLorebookEntry() {
        if (!currentPrimaryLorebook || !currentChatFileIdentifier || currentChatFileIdentifier.startsWith('unknown_chat')) {
            return -1;
        }
        try {
            const entries = await TavernHelper_API.getLorebookEntries(currentPrimaryLorebook);
            let maxFloor = -1;
            // Determine the prefix based on the currently selected summary type
            const currentPrefix = selectedSummaryType === 'small' ? SUMMARY_LOREBOOK_SMALL_PREFIX : SUMMARY_LOREBOOK_LARGE_PREFIX;

            for (const entry of entries) {
                // Only consider entries for the currently selected summary type and current chat
                if (entry.enabled && entry.comment && entry.comment.startsWith(currentPrefix + currentChatFileIdentifier + "-")) {
                    const match = entry.comment.match(/-(\d+)-(\d+)$/); // Matches against the end part like "-1-10"
                    if (match && match.length === 3) {
                        const endFloorInEntry = parseInt(match[2], 10); // Get the end floor from the entry name
                        if (!isNaN(endFloorInEntry)) {
                            maxFloor = Math.max(maxFloor, endFloorInEntry -1); // Store the highest end floor found (0-based)
                        }
                    }
                }
            }
            logDebug(`Max summarized floor for type '${selectedSummaryType}' in chat '${currentChatFileIdentifier}' is ${maxFloor} (using prefix ${currentPrefix})`);
            return maxFloor;
        } catch (error) {
            logError("ä»ä¸–ç•Œä¹¦è·å–æœ€å¤§æ€»ç»“æ¥¼å±‚æ—¶å‡ºé”™:", error);
            return -1;
        }
    }
    async function applyPersistedSummaryStatusFromLorebook() { /* ... (no change) ... */
        if (allChatMessages.length === 0) {
            logDebug("æ²¡æœ‰èŠå¤©è®°å½•ï¼Œæ— éœ€ä»ä¸–ç•Œä¹¦æ¢å¤çŠ¶æ€ã€‚");
            return;
        }
        allChatMessages.forEach(msg => msg.summarized = false);
        const maxSummarizedFloor = await getMaxSummarizedFloorFromActiveLorebookEntry();
        if (maxSummarizedFloor >= 0) {
            logDebug(`ä»ä¸–ç•Œä¹¦æ£€æµ‹åˆ°æœ€å¤§å·²æ€»ç»“æ¥¼å±‚ (0-based): ${maxSummarizedFloor}`);
            for (let i = 0; i <= maxSummarizedFloor && i < allChatMessages.length; i++) {
                if (allChatMessages[i]) {
                    allChatMessages[i].summarized = true;
                }
            }
        } else {
            logDebug("å½“å‰èŠå¤©åœ¨ä¸–ç•Œä¹¦ä¸­æ²¡æœ‰æ‰¾åˆ°æœ‰æ•ˆçš„å·²å¯ç”¨æ€»ç»“æ¡ç›®ï¼Œæˆ–è§£ææ¥¼å±‚å¤±è´¥ã€‚");
        }
    }

    // Debounced handler for new message events
    async function handleNewMessageDebounced(eventType = "unknown") {
        logDebug(`New message event (${eventType}) detected, debouncing for ${NEW_MESSAGE_DEBOUNCE_DELAY}ms...`);
        clearTimeout(newMessageDebounceTimer);
        newMessageDebounceTimer = setTimeout(async () => {
            logDebug("Debounced new message processing triggered.");
            if (isAutoSummarizing) {
                logDebug("New message processing: Auto-summary already in progress. Skipping check.");
                return;
            }
            if (!coreApisAreReady) {
                 logDebug("New message processing: Core APIs not ready. Skipping check.");
                return;
            }
            // It's crucial that allChatMessages is up-to-date before checking.
            await loadAllChatMessages(); // Reload all messages for summarizer's perspective
            await applyPersistedSummaryStatusFromLorebook(); // Refresh summarized status from lorebook for summarizer
            // applyContextVisibility(); // Re-apply visibility rules as chat length might have changed (OLD)
            applyActualMessageVisibility(); // Use new visibility logic
            await triggerAutomaticSummarizationIfNeeded(); // Then check if we need to summarize
        }, NEW_MESSAGE_DEBOUNCE_DELAY);
    }


    async function triggerAutomaticSummarizationIfNeeded() { // Renamed from triggerAutomaticUpdateIfNeeded_ACU
        logDebug("[Summarizer Auto-Trigger] Starting check..."); // Added log

        if (!autoSummaryEnabled) {
            logDebug("[Summarizer Auto-Trigger] Auto update is disabled by user setting. Skipping check."); // Updated log
            return;
        }
        logDebug("[Summarizer Auto-Trigger] Auto update is enabled."); // Added log
        if (!coreApisAreReady) {
            logDebug("Automatic summarization trigger: Core APIs not ready.");
            return;
        }
        if (isAutoSummarizing) {
            logDebug("Automatic summarization trigger: Process already running.");
            return;
        }

        if (!customApiConfig.url || !customApiConfig.model) {
            logDebug("Automatic summarization trigger: API not configured. Skipping.");
            return;
        }

        if (allChatMessages.length === 0) {
            logDebug("Automatic summarization trigger: No messages loaded. Skipping.");
            return;
        }

        const effectiveChunkSize = getEffectiveChunkSize("system_trigger"); // This is our threshold 'M'
        logDebug(`[Summarizer Auto-Trigger] Effective threshold (M) = ${effectiveChunkSize}`); // Added log

        const maxSummarizedFloor = await getMaxSummarizedFloorFromActiveLorebookEntry(); // This is our 'MaxEndFloor'
        if (maxSummarizedFloor === -1) {
            logDebug("[Summarizer Auto-Trigger] MaxEndFloor not found in lorebook or error occurred. Setting to -1 (effectively 0 for count)."); // Added log
        } else {
            logDebug(`[Summarizer Auto-Trigger] MaxEndFloor found in lorebook = ${maxSummarizedFloor}`); // Added log
        }

        const unsummarizedCount = allChatMessages.length - (maxSummarizedFloor + 1);
        logDebug(`[Summarizer Auto-Trigger Check] Total msgs: ${allChatMessages.length}, MaxEndFloor: ${maxSummarizedFloor}, Unupdated count: ${unsummarizedCount}, Threshold (M): ${effectiveChunkSize}`);

        // æ–°é€»è¾‘ï¼šå½“æœªæ€»ç»“æ¥¼å±‚æ•° å¤§äºç­‰äº (é˜ˆå€¼ + 10) æ—¶æ‰è§¦å‘
        const triggerThreshold = effectiveChunkSize + 10; // è§¦å‘é˜ˆå€¼ = æ€»ç»“é—´éš” + 10
        const shouldTrigger = unsummarizedCount >= triggerThreshold;
        logDebug(`[Summarizer Auto-Trigger] New Condition check (unupdatedCount >= M + 10): ${unsummarizedCount} >= ${triggerThreshold} -> ${shouldTrigger}`);

        if (shouldTrigger) {
            showToastr("info", `æ£€æµ‹åˆ° ${unsummarizedCount} æ¡æœªæ€»ç»“æ¶ˆæ¯ï¼Œå°†è‡ªåŠ¨å¼€å§‹æ€»ç»“ (é—´éš”: ${effectiveChunkSize} å±‚)ã€‚`);
            logWarn(`[Summarizer Auto-Trigger] AUTOMATICALLY triggering summarization. Unsummarized: ${unsummarizedCount}, ChunkSize: ${effectiveChunkSize}`);
            handleAutoSummarize();
        } else {
            logDebug("[Summarizer Auto-Trigger] Not enough unsummarized messages to trigger automatically."); // Updated log
        }
    }

