        const finalText = `å½“å‰ç”Ÿæ•ˆ: ä¿ç•™ ${displayValue} æ¡${autoAppliedSuffix}`;

        logDebug(`[UpdateHideUI] Calculated: N=${autoChunkSizeForDisplay}, X=${currentOffsetForLog}, DisplayValue=${displayValue}, Type=${currentSummaryTypeName}. Final Text: "${finalText}"`);
