  function processEmoticonPlaceholders_FAU(text) {
    if (!text || typeof text !== 'string' || EMOTICONS_FAU.size === 0) {
      return text;
    }

    return text.replace(/\[è¡¨æƒ…:([^\]]+)\]/g, (match, aiDescription) => {
      const cleanedDescription = aiDescription.trim();
      // 1. å°†AIç”Ÿæˆçš„æè¿°åˆ†å‰²æˆè¯è¯­
      const descriptionWords = cleanedDescription.split(/\s+/);
      let foundKeyword = null;

      // 2. éå†æè¿°ä¸­çš„è¯è¯­ï¼ŒæŸ¥æ‰¾ç¬¬ä¸€ä¸ªåŒ¹é…çš„å®˜æ–¹å…³é”®è¯
      for (const word of descriptionWords) {
        if (EMOTICON_KEYWORDS_FAU.includes(word)) {
          foundKeyword = word;
          break; // æ‰¾åˆ°ç¬¬ä¸€ä¸ªå°±åœæ­¢
        }
      }

      // å¦‚æœæ²¡æœ‰åœ¨æè¿°ä¸­æ‰¾åˆ°ä»»ä½•å®˜æ–¹å…³é”®è¯ï¼Œåˆ™ä¸è¿›è¡Œæ›¿æ¢
      if (!foundKeyword) {
        logWarn_FAU(
          `No valid keyword found in AI description: "${cleanedDescription}". Returning original placeholder.`,
        );
        return match;
      }

      // 3. ä½¿ç”¨æ‰¾åˆ°çš„å®˜æ–¹å…³é”®è¯æ¥ç­›é€‰è¡¨æƒ…
      const matchingEntries = [];
      for (const [title, url] of EMOTICONS_FAU.entries()) {
        if (title.includes(foundKeyword)) {
          matchingEntries.push({ title, url });
        }
      }

      // 4. å¦‚æœæœ‰åŒ¹é…é¡¹ï¼Œä»ä¸­éšæœºé€‰æ‹©ä¸€ä¸ªå¹¶æ›¿æ¢
      if (matchingEntries.length > 0) {
        const randomIndex = Math.floor(Math.random() * matchingEntries.length);
        const randomMatch = matchingEntries[randomIndex];
        logDebug_FAU(
          `Matched AI description "${cleanedDescription}" to keyword "${foundKeyword}", selected: ${randomMatch.title}`,
        );
        return `<img src="${escapeHtml_FAU(randomMatch.url)}" alt="${escapeHtml_FAU(
          randomMatch.title,
        )}" style="max-width: 150px; max-height: 150px; vertical-align: middle; display: inline-block;">`;
      }

      // å¦‚æœæ ¹æ®å…³é”®è¯ä»ç„¶æ²¡æœ‰æ‰¾åˆ°åŒ¹é…é¡¹ï¼Œè¿”å›åŸå§‹å ä½ç¬¦
      logWarn_FAU(`Found keyword "${foundKeyword}" but no matching emoticon was found in the library.`);
      return match;
    });
  }

  function getEffectiveAutoUpdateThreshold_FAU(calledFrom = 'system') {
    let threshold = autoUpdateThreshold_FAU;
