    autoUpdateThreshold_FAU = newT;

    // Update main panel if it exists and value is different
    if ($mainPanelInstance_FAU) {
      const $mainPanelInput = $mainPanelInstance_FAU.find('#fau-context-depth-input');
      if ($mainPanelInput.length && $mainPanelInput.val() != newT) {
        $mainPanelInput.val(newT);
