    // Update settings panel if it exists and value is different
    if ($settingsPopupInstance_FAU) {
      const $settingsInput = $settingsPopupInstance_FAU.find(`#${SCRIPT_ID_PREFIX_FAU}-auto-update-threshold`);
      if ($settingsInput.length && $settingsInput.val() != newT) {
        $settingsInput.val(newT);
      }
    }

    // Save to localStorage
    try {
      localStorage.setItem(STORAGE_KEY_AUTO_UPDATE_THRESHOLD_FAU, autoUpdateThreshold_FAU.toString());
      logDebug_FAU('Synced and saved threshold:', autoUpdateThreshold_FAU);
    } catch (error) {
      logError_FAU('ä¿å­˜é˜ˆå€¼å¤±è´¥ (localStorage.setItem):', error);
      showToastr_FAU('error', 'ä¿å­˜é˜ˆå€¼æ—¶å‘ç”Ÿå­˜å‚¨é”™è¯¯ã€‚');
    }
  }

