  // New core trigger function based on the provided documentation
  async function triggerAutomaticUpdateIfNeeded_FAU() {
    logDebug_FAU('[FAU Auto-Trigger] Starting check...');

    // 1. Check user's master switch
    if (!autoUpdateEnabled_FAU) {
      logDebug_FAU('[FAU Auto-Trigger] Auto update is disabled by user setting. Skipping check.');
      return;
    }

    // Additional checks from the original script
    if (Date.now() < autoUpdateCooldownUntil_FAU) {
      const remainingSeconds = Math.ceil((autoUpdateCooldownUntil_FAU - Date.now()) / 1000);
      logDebug_FAU(`[FAU Auto-Trigger] In cooldown period. Skipping check for another ${remainingSeconds}s.`);
      return;
    }
    if (!coreApisAreReady_FAU || !TavernHelper_API_FAU) {
      logDebug_FAU('[FAU Auto-Trigger] Core APIs not ready.');
      return;
    }
    if (isAutoUpdatingForum_FAU) {
      logDebug_FAU('[FAU Auto-Trigger] Update already running.');
      return;
    }
    if (!customApiConfig_FAU.url || !customApiConfig_FAU.model) {
      logDebug_FAU('[FAU Auto-Trigger] API not configured.');
      return;
    }
    if (!allChatMessages_FAU || allChatMessages_FAU.length === 0) {
      logDebug_FAU('[FAU Auto-Trigger] No messages.');
      return;
    }

    // 2. Get unsummarized count and threshold
    const currentThreshold_M = getEffectiveAutoUpdateThreshold_FAU('system_auto_trigger');
    let maxEndFloorInLorebook = -1; // Use -1 to correctly calculate count for 0-indexed arrays
    try {
      const primaryLorebookName = await TavernHelper_API_FAU.getCurrentCharPrimaryLorebook();
      if (primaryLorebookName) {
        const entries = await TavernHelper_API_FAU.getLorebookEntries(primaryLorebookName);
        if (Array.isArray(entries) && entries.length > 0) {
          const entryPrefixForCurrentChat = `â˜†FAUâ˜†${currentChatFileIdentifier_FAU}â˜†`;
          for (const entry of entries) {
            if (entry.comment && entry.comment.startsWith(entryPrefixForCurrentChat)) {
              const match = entry.comment.match(/â˜†(\d+)-(\d+)(?:â˜†\d+)?$/);
              if (match && match[2]) {
                const endFloor = parseInt(match[2], 10);
                if (!isNaN(endFloor) && endFloor > maxEndFloorInLorebook) {
                  maxEndFloorInLorebook = endFloor;
                }
              }
            }
          }
        }
      }
    } catch (e) {
      logError_FAU('[FAU Auto-Trigger] Error getting max end floor from lorebook:', e);
      maxEndFloorInLorebook = -1;
    }
    // If we found entries, maxEndFloorInLorebook is 1-indexed. Convert to 0-indexed floor.
    // If no entries found, it remains -1, so unupdatedCount will be total length.
    const maxSummarizedFloor = maxEndFloorInLorebook > 0 ? maxEndFloorInLorebook - 1 : -1;
    const unupdatedCount = allChatMessages_FAU.length - (maxSummarizedFloor + 1);

    // 3. Critical threshold check (M + 2)
    const triggerThreshold = currentThreshold_M + 2;
    const shouldTrigger = unupdatedCount >= triggerThreshold;
    logDebug_FAU(
      `[FAU Auto-Trigger] New Condition check (unupdatedCount >= M + 2): ${unupdatedCount} >= ${triggerThreshold} -> ${shouldTrigger}`,
    );

    // 4. If all conditions are met, start the update process
    if (shouldTrigger) {
      showToastr_FAU(
        'info',
        `æ£€æµ‹åˆ° ${unupdatedCount} æ¡æœªæ›´æ–°æ¶ˆæ¯ï¼Œå°†è‡ªåŠ¨æ›´æ–°è®ºå› (é˜ˆå€¼: ${currentThreshold_M} å±‚)ã€‚`,
      );
      logWarn_FAU(`[FAU Auto-Trigger] AUTOMATICALLY triggering forum content update.`);

      isAutoUpdatingForum_FAU = true;
      const allPosts = await loadPostsFromLorebook_FAU();
      const resultArea = $mainPanelInstance_FAU ? $mainPanelInstance_FAU.find('#aiPostResultArea')[0] : null;

      // Define the exact range of messages to be processed
      const startFloor = maxSummarizedFloor + 1;
      const endFloor = allChatMessages_FAU.length - 1;
      const floorRange = { start: startFloor, end: endFloor };

      const success = await handleAIMaintainForum_FAU(allPosts, resultArea, floorRange);
      isAutoUpdatingForum_FAU = false;

      if (success) {
        const cooldownSeconds = 15;
        autoUpdateCooldownUntil_FAU = Date.now() + cooldownSeconds * 1000;
        logDebug_FAU(`[FAU Auto-Trigger] Automatic maintenance successful. Starting ${cooldownSeconds}s cooldown.`);
        showToastr_FAU('success', `è‡ªåŠ¨ç»´æŠ¤æˆåŠŸï¼å°†åœ¨ ${cooldownSeconds} ç§’åæ¢å¤æ£€æŸ¥ã€‚`);
        if ($mainPanelInstance_FAU) {
          const initialLoadPosts = $mainPanelInstance_FAU.data('initialLoadPosts');
          if (typeof initialLoadPosts === 'function') {
            await initialLoadPosts();
          }
        }
      } else {
        logError_FAU(`[FAU Auto-Trigger] Automatic forum maintenance failed.`);
      }
    } else {
      logDebug_FAU('[FAU Auto-Trigger] Not enough unupdated messages to trigger automatically.');
    }
  }

