  async function deleteLorebookEntry_FAU(uid) {
    if (!coreApisAreReady_FAU || !TavernHelper_API_FAU) {
      showToastr_FAU('error', 'æ ¸å¿ƒAPIæœªå°±ç»ªï¼Œæ— æ³•åˆ é™¤æ¡ç›®ã€‚');
      return false;
    }
    try {
      const primaryLorebookName = await TavernHelper_API_FAU.getCurrentCharPrimaryLorebook();
      if (!primaryLorebookName) {
        showToastr_FAU('warning', 'å½“å‰è§’è‰²æœªè®¾ç½®ä¸»ä¸–ç•Œä¹¦ã€‚');
        return false;
      }

      const uidStr = String(uid);
      logDebug_FAU(`Attempting to delete lorebook entry with UID: ${uidStr} using Get-Filter-Set method.`);

      // 1. Get all entries
      const allEntries = await TavernHelper_API_FAU.getLorebookEntries(primaryLorebookName);
      if (!allEntries.some(entry => String(entry.uid) === uidStr)) {
        logWarn_FAU(`Entry with UID ${uidStr} not found in lorebook. It might have been already deleted.`);
        return true; // Consider it a success if it's already gone.
      }

      // 2. Filter out the entry to be deleted
      const entriesToKeep = allEntries.filter(entry => String(entry.uid) !== uidStr);

      // 3. Set the filtered list back, effectively deleting the entry
      // Using replaceLorebookEntries as it's designed for this purpose.
      await TavernHelper_API_FAU.replaceLorebookEntries(primaryLorebookName, entriesToKeep);

      logDebug_FAU(`FAU: "replaceLorebookEntries" call for UID ${uidStr} completed. Now verifying...`);

      // Verification step
      await new Promise(resolve => setTimeout(resolve, 300));
      const entriesAfterDelete = await TavernHelper_API_FAU.getLorebookEntries(primaryLorebookName);
      const entryStillExists = entriesAfterDelete.some(entry => String(entry.uid) === uidStr);

      if (entryStillExists) {
        logError_FAU(`DELETION VERIFICATION FAILED (Get-Filter-Set): Entry with UID ${uidStr} still exists.`);
        showToastr_FAU('error', `åˆ é™¤éªŒè¯å¤±è´¥ï¼ˆGet-Filter-Setï¼‰ï¼šæ¡ç›® ${uidStr} ä¾ç„¶å­˜åœ¨ã€‚`);
        return false;
      } else {
        logDebug_FAU(
          `DELETION VERIFICATION SUCCESS (Get-Filter-Set): Entry with UID ${uidStr} was successfully removed.`,
        );
        return true;
      }
    } catch (error) {
      logError_FAU(`Error during Get-Filter-Set deletion for UID ${uid}:`, error);
      showToastr_FAU('error', `åˆ é™¤ä¸–ç•Œä¹¦æ¡ç›®æ—¶å‘ç”Ÿé”™è¯¯: ${error.message}`);
      return false;
    }
  }

  async function loadPostsFromLorebook_FAU() {
    logDebug_FAU('FAU: Loading posts from lorebook...');
    if (!coreApisAreReady_FAU || !TavernHelper_API_FAU) {
      showToastr_FAU('error', 'æ ¸å¿ƒAPIæœªå°±ç»ªï¼Œæ— æ³•åŠ è½½å¸–å­ã€‚');
      return [];
    }
    try {
      const primaryLorebookName = await TavernHelper_API_FAU.getCurrentCharPrimaryLorebook();
      if (!primaryLorebookName) {
        showToastr_FAU('warning', 'å½“å‰è§’è‰²æœªè®¾ç½®ä¸»ä¸–ç•Œä¹¦ã€‚');
        return [];
      }
      const entries = await TavernHelper_API_FAU.getLorebookEntries(primaryLorebookName);
      if (!Array.isArray(entries) || entries.length === 0) {
        showToastr_FAU('info', 'ä¸»ä¸–ç•Œä¹¦ä¸ºç©ºï¼Œæ²¡æœ‰å¸–å­å¯åŠ è½½ã€‚');
        return [];
      }

      const loadedPosts = [];
      const postContentEntryPrefix = `â˜†FAUâ˜†${currentChatFileIdentifier_FAU}â˜†`;

      for (const entry of entries) {
        if (entry.enabled && entry.comment && entry.comment.startsWith(postContentEntryPrefix)) {
          try {
            const postDataFromContent = JSON.parse(entry.content);

            // V2.2.0 Core Fix: Create a single object holding all data.
            const fullPostObject = {
              ...entry, // Copy all original metadata (uid, keys, comment, type, etc.)
              ...postDataFromContent, // Add forum data (title, author, fullContent, replies, etc.)
              id: String(entry.uid), // Crucially, ensure 'id' is the entry's UID.
            };
            delete fullPostObject.content; // No longer needed at top level

            // Compatibility for old data
            if (!fullPostObject.comments) {
              fullPostObject.comments = [];
            }
            if (!fullPostObject.lastUpdatedTimestamp) {
              fullPostObject.lastUpdatedTimestamp = fullPostObject.timestamp;
            }
            loadedPosts.push(fullPostObject);
          } catch (e) {
            // If JSON parsing fails, assume it's the old plain text format and handle it compatibly
            logWarn_FAU(`JSON parse failed for UID ${entry.uid}. Attempting fallback for old format.`);
            if (typeof entry.content === 'string') {
              const timestamp = new Date(0).getTime();
              const fullPostObject = {
                ...entry, // Copy all original metadata
                id: String(entry.uid),
                title: entry.content.split('\n')[0] || 'æ—§æ ¼å¼å¸–å­',
                author: 'æœªçŸ¥ä½œè€… (æ—§æ•°æ®)',
                timestamp: timestamp,
                lastUpdatedTimestamp: timestamp,
                fullContent: entry.content,
                upvotes: 0,
                comments: [],
                basedOnFloors: null,
              };
              delete fullPostObject.content;
              loadedPosts.push(fullPostObject);
            } else {
              logError_FAU(
                `Failed to parse post JSON and content is not a string (UID: ${entry.uid}, Comment: ${entry.comment}). Error:`,
                e,
              );
            }
          }
        }
      }

      logDebug_FAU(`FAU: Loaded ${loadedPosts.length} posts from lorebook.`);
      // if (loadedPosts.length > 0) {
      //   showToastr_FAU('success', `æˆåŠŸä»ä¸–ç•Œä¹¦åŠ è½½äº† ${loadedPosts.length} ä¸ªå¸–å­ã€‚`);
      // } else {
      //   showToastr_FAU('info', 'å½“å‰èŠå¤©åœ¨ä¸–ç•Œä¹¦ä¸­æ²¡æœ‰æ‰¾åˆ°å¸–å­ã€‚');
      // }
      return loadedPosts;
    } catch (error) {
      logError_FAU('FAU: Error loading posts from lorebook:', error);
      showToastr_FAU('error', `ä»ä¸–ç•Œä¹¦åŠ è½½å¸–å­å¤±è´¥: ${error.message}`);
      return [];
    }
  }

