      function renderComments(postId, postDetailsContainer) {
        const commentsContainer = postDetailsContainer.querySelector('.comments-container');
        const commentsPaginationContainer = postDetailsContainer.querySelector('.comments-pagination');
        if (!commentsContainer || !commentsPaginationContainer) return;

        const page = currentCommentPages[postId] || 1;
        const post = postsData.find(p => p.id === postId);
        if (!post) return;

        commentsContainer.innerHTML = '';
        const sortedComments = [...post.comments].sort((a, b) => a.timestamp - b.timestamp); // æŒ‰æ—¶é—´æ­£åºæ’åˆ—

        const startIndex = (page - 1) * COMMENTS_PER_PAGE;
        const endIndex = startIndex + COMMENTS_PER_PAGE;
        const paginatedComments = sortedComments.slice(startIndex, endIndex);

        if (sortedComments.length === 0) {
          commentsContainer.innerHTML = '<p style="font-size: 0.85rem; color: #888;">è¿˜æ²¡æœ‰è¯„è®ºï¼Œå¿«æ¥æŠ¢æ²™å‘ï¼</p>';
        } else {
