            const commentElement = document.createElement('div');
            commentElement.className = 'fau-comment-item';
            commentElement.dataset.commentId = comment.id;

            let contentHTML = escapeHtml_FAU(comment.content);
            const replyMatch = contentHTML.match(/^(å›å¤ @[^:]+:)(.*)/s);

            if (comment.replyTo && comment.replyTo.author) {
              const replyToAuthor = escapeHtml_FAU(comment.replyTo.author);
              contentHTML = `<span class="fau-reply-tag">@${replyToAuthor}</span> ${contentHTML}`;
