    }
    const totalMessages = allChatMessages_FAU ? allChatMessages_FAU.length : 0;
    $totalMessagesDisplay.text(`ä¸Šä¸‹æ–‡æ€»å±‚æ•°: ${totalMessages}`);
    if (!currentChatFileIdentifier_FAU || currentChatFileIdentifier_FAU.startsWith('unknown_chat')) {
      $forumUpdateStatusDisplay_FAU.text('å½“å‰èŠå¤©æœªçŸ¥ï¼Œæ— æ³•è·å–æ›´æ–°çŠ¶æ€ã€‚');
      return;
