    if (!coreApisAreReady_FAU || !TavernHelper_API_FAU) {
      $forumUpdateStatusDisplay_FAU.text('æ ¸å¿ƒAPIæœªå°±ç»ªï¼Œæ— æ³•è·å–ä¸–ç•Œä¹¦çŠ¶æ€ã€‚');
