  async function loadAllChatMessages_FAU() {
    if (!coreApisAreReady_FAU || !TavernHelper_API_FAU) return;
    try {
      const lastMessageId = TavernHelper_API_FAU.getLastMessageId
        ? TavernHelper_API_FAU.getLastMessageId()
        : SillyTavern_API_FAU.chat?.length
        ? SillyTavern_API_FAU.chat.length - 1
        : -1;
      if (lastMessageId < 0) {
        allChatMessages_FAU = [];
        logDebug_FAU('No chat messages (FAU).');
        return;
      }
      const messagesFromApi = await TavernHelper_API_FAU.getChatMessages(`0-${lastMessageId}`, {
        include_swipes: false,
      });
      if (messagesFromApi && messagesFromApi.length > 0) {
        allChatMessages_FAU = messagesFromApi.map((msg, idx) => ({ ...msg, id: idx }));
        logDebug_FAU(`FAU Loaded ${allChatMessages_FAU.length} messages for: ${currentChatFileIdentifier_FAU}.`);
      } else {
        allChatMessages_FAU = [];
      }
    } catch (error) {
      logError_FAU('FAUè·å–èŠå¤©è®°å½•å¤±è´¥: ' + error.message);
      allChatMessages_FAU = [];
    }
  }

  async function getAllActiveLorebookContent_FAU() {
    if (!coreApisAreReady_FAU || !TavernHelper_API_FAU || !SillyTavern_API_FAU) {
      logWarn_FAU('getAllActiveLorebookContent_FAU: Core APIs not ready.');
      return '';
    }
    try {
      const primaryLorebookName = await TavernHelper_API_FAU.getCurrentCharPrimaryLorebook();
      if (!primaryLorebookName) {
        logDebug_FAU('No primary lorebook set for current character.');
        return '';
      }
      const entries = await TavernHelper_API_FAU.getLorebookEntries(primaryLorebookName);
      if (!entries || entries.length === 0) {
        logDebug_FAU('Primary lorebook is empty.');
        return '';
      }
      let combinedContent = '';
      for (const entry of entries) {
        // Exclude FAU-generated post entries from the general context to avoid clutter
        if (
          entry.enabled &&
          entry.content &&
          entry.content.trim() !== '' &&
          (!entry.comment || !entry.comment.startsWith('â˜†FAUâ˜†'))
        ) {
          combinedContent += `--- ${entry.comment || 'ä¸–ç•Œä¹¦æ¡ç›®'} ---\n${entry.content.trim()}\n\n`;
        }
      }
      logDebug_FAU(
        `Collected ${combinedContent.length} chars from ${
          entries.filter(e => e.enabled && (!e.comment || !e.comment.startsWith('â˜†FAUâ˜†'))).length
        } active non-FAU lorebook entries.`,
      );
      return combinedContent.trim();
    } catch (error) {
      logError_FAU('Error getting active lorebook content:', error);
      return '';
    }
  }

  async function callConfiguredApi_FAU(messages, jsonMode = false) {
    if (!customApiConfig_FAU.url || !customApiConfig_FAU.model) throw new Error('API URL/Modelæœªé…ç½®ã€‚');
    let fullApiUrl = customApiConfig_FAU.url;
    if (!fullApiUrl.endsWith('/')) fullApiUrl += '/';
    if (fullApiUrl.endsWith('/v1/')) fullApiUrl += 'chat/completions';
    else if (!fullApiUrl.includes('/chat/completions')) fullApiUrl += 'v1/chat/completions';

    const headers = { 'Content-Type': 'application/json' };
    if (customApiConfig_FAU.apiKey) headers['Authorization'] = `Bearer ${customApiConfig_FAU.apiKey}`;

    const bodyPayload = {
      model: customApiConfig_FAU.model,
      messages: messages,
    };

    if (jsonMode) {
      bodyPayload.response_format = { type: 'json_object' };
    }

    const body = JSON.stringify(bodyPayload);

    logDebug_FAU('FAU Calling API:', fullApiUrl, 'Model:', customApiConfig_FAU.model, 'JSON Mode:', jsonMode);
    logDebug_FAU('--- API Request Payload ---');
    logDebug_FAU(JSON.stringify(messages, null, 2));
    logDebug_FAU('--------------------------');

    let response;
    try {
      response = await fetch(fullApiUrl, { method: 'POST', headers: headers, body: body });
    } catch (error) {
      logError_FAU('Network error during fetch:', error);
      throw new Error(`ç½‘ç»œè¯·æ±‚å¤±è´¥: ${error.message}ã€‚è¯·æ£€æŸ¥ç½‘ç»œè¿æ¥ã€APIåœ°å€æ˜¯å¦æ­£ç¡®ï¼Œæˆ–æ˜¯å¦å­˜åœ¨CORSè·¨åŸŸé—®é¢˜ã€‚`);
    }

    if (!response.ok) {
      const errTxt = await response.text();
      logError_FAU(`API request failed with status ${response.status}:`, errTxt);
      throw new Error(`APIè¯·æ±‚å¤±è´¥: ${response.status} ${response.statusText}ã€‚æœåŠ¡å™¨è¿”å›: ${errTxt}`);
    }

    const data = await response.json();
    logDebug_FAU('--- API Raw Response Data ---');
    logDebug_FAU(JSON.stringify(data, null, 2));
    logDebug_FAU('----------------------------');
    if (data.choices && data.choices[0] && data.choices[0].message && data.choices[0].message.content) {
      const content = data.choices[0].message.content.trim();
      logDebug_FAU('--- Extracted AI Content ---');
      logDebug_FAU(content);
      logDebug_FAU('---------------------------');
      return content;
    }
    throw new Error('APIå“åº”æ ¼å¼ä¸æ­£ç¡®æˆ–å†…å®¹ä¸ºç©ºã€‚');
  }

