class NPCBrain {
    /**
     * @param {'warrior'|'merchant'|'wizard'} role 
     */
    constructor(role) {
        this.role = role;
        this.motivationWeights = this.#initMotivationProfile();
        this.currentGoal = null;
    }

    // åˆå§‹åŒ–ä¸åŒè§’è‰²çš„åŠ¨æœºé…ç½®
    #initMotivationProfile() {
        const profiles = {
            warrior: { 
                safety: 0.8, 
                combat: 0.9,
                reputation: 0.6 
            },
            merchant: {
                wealth: 0.95,
                trade: 0.7,
                riskAvoid: 0.8
            }
        };
        return profiles[this.role] || {};
    }

    /**
     * æ ¹æ®ç¯å¢ƒä¸Šä¸‹æ–‡åšå‡ºå†³ç­–
     * @param {Object} context 
     * @returns {string} è¡ŒåŠ¨ç±»å‹
     */
    decideAction(context) {
        if (!this.currentGoal) {
            this.#selectNewGoal(context);
        }
        return this.#generateActionPlan();
    }

    // é€‰æ‹©æ–°ç›®æ ‡
    #selectNewGoal(context) {
        const goalScores = Object.entries(this.motivationWeights)
            .map(([factor, weight]) => ({
                goal: factor,
                score: weight * (context[factor] || 1)
            }));

        this.currentGoal = goalScores.reduce(
            (max, curr) => (curr.score > max.score ? curr : max),
            { score: -Infinity }
        ).goal;
    }

    // ç”Ÿæˆè¡ŒåŠ¨æ–¹æ¡ˆ
    #generateActionPlan() {
        const actionMap = {
            safety: ['retreat', 'seekCover', 'callReinforcements'],
            combat: ['attack', 'taunt', 'prepareDefense'],
            wealth: ['negotiate', 'bargain', 'invest']
        };
        return actionMap[this.currentGoal]?.[Math.floor(Math.random() * 3)] ?? 'wait';
    }
}

