/* 底栏QR按键隐藏 */
#send_form:hover #qr--bar {    
    height: 40px;    
    transition: all 0.5s ease;
}

#qr--bar {    
    height: 0.1px;
}

code span.comment {
    color: #999;
    font-style: italic;
}

body {
    font-family: 'ZhuqueFangsong', serif;
    line-height: 1.5;
    font-weight: 300; 
}

@font-face {
    font-family: 'ZhuqueFangsong';
    src: url("https://files.catbox.moe/138mza.ttf") format('truetype'); /* 修改为新的字体链接 */
    font-weight: 200; /* 设置字体粗细为400 */
    font-style: normal;
}

/* 高亮样式 */
code span.keyword {
    color: #007BFF; /* 关键字颜色 */
    font-weight: bold;
}

code span.string {
    color: #d14;
}

* {
    --black30a: rgb(255 255 255 / 30%);
    --black50a: rgb(255 255 255 / 50%);
    --black70a: rgb(255 255 255 / %);
    --white20a: rgb(255 255 255 / 20%);
    --white30a: rgb(255 255 255 / 30%);
    --white50a: rgb(255 255 255 / 50%);
    --grey30: rgb(255 255 255);
    --grey30a: rgb(255 255 255 / 30%);
    --grey7070a: rgb(255 255 255 / 70%);
}

/* 添加占位符的样式 */
textarea::placeholder, input[type="text"]::placeholder {
    font-family: 'ZhuqueFangsong', serif;
    color: #999; /* 灰色占位符 */
    font-weight: 300; /* 更细的占位符字体 */
    font-size: 14px; /* 占位符稍小 */
}

/* 聚焦时样式 */
textarea:focus, input[type="text"]:focus {
    border-color: #000; /* 聚焦时边框保持黑色 */
    background-color: #f7f7f7; /* 微微变浅的灰白色背景 */
    box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.5); /* 更深的阴影效果 */
}

textarea, input[type="text"] {
    font-family: 'ZhuqueFangsong', serif; /* 嵌入字体 */
    font-size: 16px; /* 字体大小 */
    font-weight: 300; /* 字体粗细 */
    line-height: 1.6; /* 行高 */
    color: #000; /* 黑色字体 */
    background-color: #fff; /* 白色背景 */
    border: 1px solid #000; /* 黑色边框 */
    border-radius: 5px; /* 圆角 */
    padding: 10px; /* 内边距 */
    box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.2); /* 黑色微弱阴影 */
    outline: none; /* 去掉默认聚焦时的外边框 */
    transition: all 0.3s ease; /* 动态效果 */
}

pre {
    background-color: #f8f9fa; /* 浅灰色背景 */
    color: #333; /* 深灰色字体 */
    border: 1px solid #e0e0e0; /* 浅灰色边框 */
    border-radius: 4px; /* 圆角 */
    padding: 8px; /* 内边距 */
    font-family: 'Courier New', monospace; /* 等宽字体 */
    font-size: 14px; /* 字体大小 */
    overflow: auto; /* 长内容滚动 */
}

code {
    background-color: #E6E6E6; /* 灰色背景 */
    color: #000000; /* 黑色文字 */
    font-family: inherit; /* 使用全局字体 */
    border: 1px solid #000000; /* 浅蓝色边框 */
}

/* 父容器 */
.parent-container {
    background-color: transparent !important; /* 父级背景透明 */
}

/* 代码块样式 */
pre {
    position: relative; /* 确保可以定位子元素 */
    background-color: #E6E6E6 !important; /* 浅蓝色背景 */
    color: #000000 !important; /* 黑色文字 */
    border: none !important; /* 去掉边框 */
    box-shadow: none !important; /* 去掉阴影 */
    padding: 12px; /* 内边距 */
    margin: 10px 0; /* 上下间距 */
    overflow: hidden; /* 防止内容溢出 */
}



/* 子代码块 */
pre code {
    background-color: inherit !important; /* 继承父级背景色 */
    color: inherit !important; /* 继承文字颜色 */
    font-family: inherit; /* 全局字体 */
}

/* 隐藏复制按钮 */
pre .copy-button, /* 常见复制按钮类名 */
pre .hljs-copy-button, /* Highlight.js 默认复制按钮 */
pre .prism-copy-button { /* Prism.js 默认复制按钮 */
    display: none !important; /* 隐藏复制按钮 */
}

/* 如果复制按钮没有特定类名 */
pre > div {
    display: none !important; /* 隐藏 pre 下的所有动态子元素 */
}

/* 聊天框字体加粗 */
.chat-message, .mes_text, .chat-container {
    font-weight: 300 !important;
}

@media screen and (max-width: 720px) {
    .mesAvatarWrapper {
        width: 0px;
        display: none;
    }
    .last_mes .mes_text {
        padding-right: 0px;
    }
    .mes_text {
        padding-bottom: 45px;
    }
}

