    function lightenDarkenColor(col, amt) { /* ... (no change) ... */
        let usePound = false; if (col.startsWith("#")) { col = col.slice(1); usePound = true; }
        let num = parseInt(col,16);
        let r = (num >> 16) + amt; if (r > 255) r = 255; else if  (r < 0) r = 0;
        let b = ((num >> 8) & 0x00FF) + amt; if (b > 255) b = 255; else if  (b < 0) b = 0;
        let g = (num & 0x0000FF) + amt; if (g > 255) g = 255; else if (g < 0) g = 0;
        return (usePound?"#":"") + ("000000" + ((r << 16) | (b << 8) | g).toString(16)).slice(-6);
    }
    function getContrastYIQ(hexcolor){ /* ... (no change) ... */
        if(hexcolor.startsWith('#')) hexcolor = hexcolor.slice(1);
        var r = parseInt(hexcolor.substr(0,2),16); var g = parseInt(hexcolor.substr(2,2),16); var b = parseInt(hexcolor.substr(4,2),16);
        var yiq = ((r*299)+(g*587)+(b*114))/1000;
        return (yiq >= 128) ? '#000000' : '#FFFFFF';
    }
    function getEffectiveChunkSize(calledFrom = "system") { /* ... (no change from v0.3.22) ... */
        let chunkSize = DEFAULT_CHUNK_SIZE;
        if (typeof customChunkSizeSetting !== 'undefined' && !isNaN(customChunkSizeSetting) && customChunkSizeSetting >= 2 && customChunkSizeSetting % 2 === 0) {
            chunkSize = customChunkSizeSetting;
        }
        let uiChunkSizeVal = null;
        if ($customChunkSizeInput && $customChunkSizeInput.length > 0) {
            uiChunkSizeVal = $customChunkSizeInput.val();
        }
        if (uiChunkSizeVal) {
            const parsedUiInput = parseInt(uiChunkSizeVal, 10);
            if (!isNaN(parsedUiInput) && parsedUiInput >= 2 && parsedUiInput % 2 === 0) {
                chunkSize = parsedUiInput;
                if (calledFrom === "handleAutoSummarize_UI") {
                    try {
                        localStorage.setItem(STORAGE_KEY_CUSTOM_CHUNK_SIZE, chunkSize.toString());
                        customChunkSizeSetting = chunkSize;
                        logDebug("自定义总结间隔已通过UI交互保存:", chunkSize);
                    } catch (error) { logError("保存自定义总结间隔失败 (localStorage):", error); }
                }
            } else {
                if (calledFrom === "handleAutoSummarize_UI") {
                    showToastr("warning", `输入的总结间隔 "${uiChunkSizeVal}" 无效。将使用之前保存的设置或默认值 (${chunkSize} 层)。`);
                    if($customChunkSizeInput) $customChunkSizeInput.val(chunkSize);
                }
            }
        }
        logDebug(`getEffectiveChunkSize (calledFrom: ${calledFrom}): final effective chunk size = ${chunkSize}`);
        return chunkSize;
    }
    function loadSettings() { /* ... (no change from v0.3.22) ... */
