        try {
            const savedPrompt = localStorage.getItem(STORAGE_KEY_CUSTOM_PROMPT);
            currentSystemPrompt = (savedPrompt && typeof savedPrompt === 'string' && savedPrompt.trim() !== '') ? savedPrompt : DEFAULT_SYSTEM_PROMPT;
        } catch (error) { logError("加载自定义提示词失败:", error); currentSystemPrompt = DEFAULT_SYSTEM_PROMPT; }

        try {
            const savedThemeSettingsJson = localStorage.getItem(STORAGE_KEY_THEME_SETTINGS);
