async function modifyLorebookEntries() {
  try {
    // è·å–å½“å‰é€‰æ‹©çš„ä¸–ç•Œä¹¦
    const lorebook = $('#world_editor_select', window.parent.document).find(':selected').text();

    if (lorebook == '--- é€‰æ‹©ä»¥ç¼–è¾‘ ---') {
      return { success: false, error: 'è¯·é€‰æ‹©ä¸–ç•Œä¹¦' };
    }

    // è·å–å½“å‰ä¸–ç•Œä¹¦çš„å…ƒç´ 
    const entries = await getLorebookEntries(lorebook);

    // å…‹éš†åŸå§‹entriesç”¨äºæ¯”è¾ƒ
    const originalEntries = [...entries];

    // é¢„å®šä¹‰ä½ç½®æ˜ å°„å¸¸é‡
    const NORMAL_POSITIONS = {
      'before_character_definition': 1,    // è§’è‰²å®šä¹‰ä¹‹å‰
        'after_character_definition': 2,   // è§’è‰²å®šä¹‰ä¹‹å
        'before_example_messages': 3,      // ç¤ºä¾‹æ¶ˆæ¯ä¹‹å‰
        'after_example_messages': 4,       // ç¤ºä¾‹æ¶ˆæ¯ä¹‹å
        'before_author_note': 5,           // ä½œè€…æ³¨é‡Šä¹‹å‰
        'after_author_note': 6             // ä½œè€…æ³¨é‡Šä¹‹å
    };

    // æ·±åº¦ç›¸åŒæ—¶ï¼Œæ’åºä¸ºåŠ©æ‰‹ï¼Œç”¨æˆ·ï¼Œç³»ç»Ÿ
    const DEPTH_POSITIONS = {
      'at_depth_as_assistant': 1, // @DğŸ¤–
      'at_depth_as_user': 2,      // @DğŸ‘¤
      'at_depth_as_system': 3     // @Dâš™
    };

    // é¢„è®¡ç®—æ’åºé”®å€¼
    const sortKeys = new Map();

    for (let i = 0; i < entries.length; i++) {
      const entry = entries[i];
      const enabled = entry.enabled ? 0 : 1;
      const position = entry.position;

      // è®¡ç®—ä½ç½®ç±»å‹å’Œæ’å é¢˜å¤–è¯ï¼šæŒºæ—©å°±çŸ¥é“å‡ ä¸ªæ·±åº¦ä½ç½®æ’åºæœ‰é—®é¢˜ï¼Œä½†ä¸€ç›´æ²¡äººåé¦ˆã€‚è¯´æ˜åŸºæœ¬ä¸Šåªç”¨åˆ°@Dâš™ï¼Œå†åŠ ä¸Šä¹Ÿæ‡’å¾—å¼„å°±ä¸€ç›´æ²¡æ”¹
      let posType, posRank;
      if (position in NORMAL_POSITIONS) {
        posType = 0; // æ­£å¸¸ä½ç½®
        posRank = NORMAL_POSITIONS[position];
      } else if (position in DEPTH_POSITIONS) {
        posType = 1; // æ·±åº¦ä½ç½®
        posRank = DEPTH_POSITIONS[position];
      } else {
        posType = 2; // å…¶ä»–ä½ç½®ï¼ŒåŸºæœ¬ä¸å­˜åœ¨
        posRank = 999;
      }

      // æ·±åº¦å€¼ï¼ˆä»…æ·±åº¦ä½ç½®ä½¿ç”¨ï¼‰
      const depth = posType === 1 ? -(entry.depth || 0) : 0;

      // å­˜å‚¨æ’åºé”®å€¼
      sortKeys.set(entry.uid, [
        enabled,    // å¯ç”¨çŠ¶æ€
        posType,    // ä½ç½®ç±»å‹ï¼ˆ0=æ­£å¸¸ï¼Œ1=æ·±åº¦ï¼Œ2=å…¶ä»–ï¼‰
        depth,      // æ·±åº¦å€¼ï¼ˆä»…æ·±åº¦ä½ç½®æœ‰æ•ˆï¼‰
        posRank,    // ä½ç½®æ’å
        entry.order || 0, // é¡ºåº
        entry.uid   // UID
      ]);
    }

    // åˆ›å»ºä¼˜åŒ–çš„æ’åºå‡½æ•°
    entries.sort((a, b) => {
      const aKeys = sortKeys.get(a.uid);
      const bKeys = sortKeys.get(b.uid);

      // ä¾æ¬¡æ¯”è¾ƒæ¯ä¸ªæ’åºé”®
      for (let i = 0; i < 6; i++) {
        const diff = aKeys[i] - bKeys[i];
        if (diff !== 0) return diff;
      }

      return 0; // ä¸åº”è¯¥è¾¾åˆ°è¿™é‡Œï¼Œå› ä¸ºuidæ€»æ˜¯ä¸åŒçš„
    });

    // æ£€æŸ¥æ˜¯å¦éœ€è¦æ›´æ–°
    const originalIndexMap = new Map();
    originalEntries.forEach(entry => {
      originalIndexMap.set(entry.uid, entry.display_index);
    });

    // éœ€ä¸éœ€è¦æ›´æ–°
    let needsUpdate = false;
    const modifiedEntries = [];

    for (let i = 0; i < entries.length; i++) {
      const entryUid = entries[i].uid; // åªå–å¯¹åº”UID
      if (originalIndexMap.get(entryUid) !== i) {
        needsUpdate = true;
        // åªæ·»åŠ éœ€è¦æ›´æ¢ä½ç½®çš„å…ƒç´ 
        modifiedEntries.push({
          uid: entryUid,
          display_index: i
        });
      }
    }

    if (needsUpdate) {
      await setLorebookEntries(lorebook, modifiedEntries);
      console.log('ä¸–ç•Œä¹¦å·²æ ¹æ®å®é™…é¡ºåºæ’åºï¼Œè‡ªå®šä¹‰æ’åºä¸ºçœŸå®æ’åº');
      return { success: true, message: 'è‡ªå®šä¹‰çœŸå®æ’åºå®Œæˆ' };
    }

    return { success: true, message: 'å·²çœŸå®æ’åº' };
  } catch (error) {
    console.error('æ’åºå½“å‰ä¸–ç•Œä¹¦å‡ºé”™:', error);
    return { success: false, error: error.message };
  }
}
