"use strict";

/* --- Configuration --- */
// Combined Panel & Button
const COMBINED_PANEL_ID = 'global-combined-selector-panel';
const COMBINED_BUTTON_ID = 'global-combined-selector-button';
const COMBINED_BUTTON_ICON = 'fa-solid fa-list-alt'; // Generic icon
const COMBINED_BUTTON_TOOLTIP = 'Toggle Selectors';
const ACTIVE_TAB_CLASS = 'active-tab';
const ACTIVE_CONTENT_CLASS = 'active-content';

// Tabs & Content Areas
const REGEX_TAB_ID = 'regex-tab';
const LOREBOOK_TAB_ID = 'lorebook-tab';
const REGEX_CONTENT_ID = 'regex-content';
const LOREBOOK_CONTENT_ID = 'lorebook-content';

// Regex Specific
const REGEX_ITEM_CLASS = 'global-regex-item';
const STARRED_REGEX_CLASS = 'starred-global-regex';
const REGEX_STAR_ICON_CLASS = 'regex-star-icon';
const REGEX_SEARCH_INPUT_CLASS = 'regex-search-input';
const REGEX_LIST_CLASS = 'regex-list';
const REGEX_ITEM_DISABLED_CLASS = 'disabled';
const LOCAL_STORAGE_KEY_REGEX_STARRED = 'globalRegexSelector_starred';

// Lorebook Specific
const LOREBOOK_ITEM_CLASS = 'global-lorebook-item';
const SELECTED_LOREBOOK_CLASS = 'selected-global-lorebook';
const STARRED_LOREBOOK_CLASS = 'starred-global-lorebook';
const LOREBOOK_STAR_ICON_CLASS = 'lorebook-star-icon';
const LOREBOOK_SEARCH_INPUT_CLASS = 'lorebook-search-input';
const LOREBOOK_LIST_CLASS = 'lorebook-list'; // Added for consistency
const LOCAL_STORAGE_KEY_LOREBOOK_STARRED = 'globalLorebookSelector_starred'; // Renamed for clarity

/* --- Helper Function (Error Handling) --- */
function errorCatched(fn, context = 'Combined Selector') {
    const onError = (error) => {
        let iframeName = 'UnknownIframe';
        try {
            iframeName = (typeof getIframeName === 'function') ? getIframeName() : 'UnknownIframe';
        } catch (e) {
            console.warn(`(${context}) Could not get iframe name:`, e);
        }
        const errorMessage = `Error in ${iframeName} (${context}) ${error.stack || error.message || error.name}`;
        console.error(errorMessage);
        if (typeof triggerSlash === 'function') {
            try {
                triggerSlash(`echo severity=error ${errorMessage.substring(0, 500)}...`);
            } catch (e) {
                console.error(`(${context}) Failed to triggerSlash for error:`, e);
            }
        }
    };
    return (...args) => {
        try {
            const result = fn(...args);
            if (result instanceof Promise) {
                return result.catch(error => {
                    onError(error);
                    throw error; // Rethrow so await calls catch it
                });
            }
            return result;
        } catch (error) {
            onError(error);
            throw error; // Rethrow so caller knows it failed
        }
    };
}

/* --- API Wrappers & Polyfills (Ensure functions exist) --- */
// Common
const triggerSlash = window.triggerSlash;
const getIframeName = window.getIframeName;

// Regex Specific
const getTavernRegexes = window.getTavernRegexes;
const replaceTavernRegexes = window.replaceTavernRegexes;

// Lorebook Specific
const getLorebooks = window.getLorebooks;
const getLorebookSettings = window.getLorebookSettings;
const setLorebookSettings = window.setLorebookSettings;

// Wrapped Regex API Call
const getFullTavernRegexes = errorCatched(async () => {
    console.log('Combined Selector (Regex): Reading all Tavern regexes (global & character)...');
    if (typeof getTavernRegexes !== 'function') {
        console.error('Combined Selector (Regex): getTavernRegexes function is not available.');
        throw new Error('getTavernRegexes function not found.');
    }
    try {
        const tavernRegexes = await getTavernRegexes({ scope: 'all' });
        console.log('Combined Selector (Regex): Fetched all Tavern regexes:', tavernRegexes);
        return tavernRegexes || [];
    } catch (error) {
        console.error('Combined Selector (Regex): Error fetching Tavern regexes:', error);
        return [];
    }
}, 'getFullTavernRegexes');

// Wrapped Lorebook API Calls (add error catching)
const getLorebooksSafe = errorCatched(async () => {
    if (typeof getLorebooks !== 'function') {
        console.error('Combined Selector (Lorebook): getLorebooks function is not available.');
        throw new Error('getLorebooks function not found.');
    }
    return await getLorebooks() || [];
}, 'getLorebooks');

const getLorebookSettingsSafe = errorCatched(async () => {
    if (typeof getLorebookSettings !== 'function') {
        console.error('Combined Selector (Lorebook): getLorebookSettings function is not available.');
        throw new Error('getLorebookSettings function not found.');
    }
    return await getLorebookSettings() || { selected_global_lorebooks: [] };
}, 'getLorebookSettings');

const setLorebookSettingsSafe = errorCatched(async (settings) => {
    if (typeof setLorebookSettings !== 'function') {
        console.error('Combined Selector (Lorebook): setLorebookSettings function is not available.');
        throw new Error('setLorebookSettings function not found.');
    }
    await setLorebookSettings(settings);
}, 'setLorebookSettings');

/* --- Mobile Detection Helper --- */
const isMobile = () => {
    // Basic check for touch capability, common heuristic for mobile
    return ('ontouchstart' in window || navigator.maxTouchPoints > 0 || navigator.msMaxTouchPoints > 0);
};

/* --- Starred Item Storage Logic --- */
// Regex
const getStarredRegexFromStorage = errorCatched(() => {
    try {
        const storedData = localStorage.getItem(LOCAL_STORAGE_KEY_REGEX_STARRED);
        return storedData ? JSON.parse(storedData) : [];
    } catch (error) {
        console.error('Combined Selector (Regex): Error reading starred regex names from localStorage:', error);
        return [];
    }
}, 'getStarredRegexFromStorage');

const saveStarredRegexToStorage = errorCatched((starredArray) => {
    try {
        const validStarredArray = starredArray.filter(item => typeof item === 'string');
        localStorage.setItem(LOCAL_STORAGE_KEY_REGEX_STARRED, JSON.stringify(validStarredArray));
    } catch (error) {
