custom_css:
/* 全局去除所有元素的阴影效果 */
* {
    box-shadow: none !important;
}

/* 破限栏条目颜色 */
* {
    --black30a: rgba(255, 255, 255, 0.3);
    --black50a: rgba(255, 255, 255, 0.5);
    --white30a: rgba(0, 0, 0, 0.4);
    --white50a: rgba(0, 0, 0, 0.5);
}

/* 定义主要按键的颜色 */
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4,
.standoutHeader {
    background-image: none;
    background-color: #FFFFFF; 
    border: 1px solid #000000;
    color: #000000;
}

/* 顶部栏的图标颜色 */
#top-settings-holder {
    color: #B0B0B0;
}

.drawer-icon.closedIcon {
    opacity: 0.6 !important;
}

/* 输入框 */
#send_textarea {
    color: #000000;
    background-color: rgba(237, 237, 237, 0.5);
}

/* 格式化文本 */
.formatted-text {
    color: #000000;
}

/* 代码块 */
/* 小代码块 */
code {
    background-color: rgba(237, 237, 237, 0.5);
    color: #000000;
    border: none;
}

/* 禁用 .hljs 的背景颜色和文字颜色 */
.hljs {
    background-color: transparent !important;
    color: inherit !important;
}

/* 大代码块 */
pre {
    font-family: monospace;
    background-color: rgba(237, 237, 237, 0.3);
    color: #000000;
    padding: 10px;
    border-radius: 5px;
    border: none;
    background-clip: padding-box;
    position: relative;
}

/* 按钮和文本框 */
.menu_button, .text_pole, textarea {
    background-color: rgba(237, 237, 237, 0.5);
    color: #000000;
    border: 1px solid #000000;
}

/* 编辑框 */
.edit_textarea {
    background-color: rgba(237, 237, 237, 0.5) !important;
    color: #000000 !important;
    border: 1px solid #000000;
}

/* 消息容器的基础样式 */
body.bubblechat .mes {
    padding: 10px 10px;
}

/* 调整用户消息的容器 */
body.bubblechat .mes[is_user="true"] {
    flex-direction: row-reverse;
}

/* 基础气泡样式 */
body.bubblechat .mes_block {
    position: relative;
    border-radius: 5px;
    max-width: 70%;
    width: fit-content !important;
    overflow: visible !important;
    padding: 6px 10px 2px 10px;
    min-width: min-content;
}

/* 消息文本样式 */
body.bubblechat .mes_text {
    overflow-wrap: break-word !important;
    word-wrap: break-word !important;
    word-break: break-all !important;
    max-width: 100% !important;
    display: block !important;
    padding-right: 5px !important;
    padding-left: 5px !important;
}

/* 修改代码块样式防止溢出 */
body.bubblechat .mes_text code,
body.bubblechat .mes_text pre {
    white-space: pre-wrap !important;
    word-wrap: break-word !important;
    max-width: 100% !important;
    display: block !important;
    overflow: hidden !important;
    padding: 5px !important;
}

/* 为长内容的代码块添加滚动条 */
body.bubblechat .mes_text pre:has(code) {
    max-height: 300px !important;
    overflow-y: auto !important;
}

/* 系统消息气泡(左侧白色) */
body.bubblechat .mes:not([is_user="true"]) .mes_block {
    background-color: #FFFFFF;
    margin-left: 10px;
}

/* 系统消息的三角形 */
body.bubblechat .mes:not([is_user="true"]) .mes_block::after {  
    content: "";
    position: absolute;
    left: -5px;         
    top: 16px;         
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 8px 8px 8px 0;
    border-color: transparent #FFFFFF transparent transparent;
}

/* 用户消息气泡(右侧绿色) */
body.bubblechat .mes[is_user="true"] .mes_block {
    background-color: #9EEA6A;
    margin-right: 10px;
    margin-left: auto;   
}

/* 用户消息的三角形 */
body.bubblechat .mes[is_user="true"] .mes_block::after {  
    content: "";
    position: absolute;
    right: -5px;        
    top: 16px;        
    width: 0;
    height: 0;
    border-style: solid;
    border-width: 8px 0 8px 8px;
    border-color: transparent transparent transparent #9EEA6A;
}

/* 控制按钮容器相关样式 */
body.bubblechat .mes_buttons {
    overflow: visible !important;
    white-space: nowrap !important;
    position: relative !important;
    z-index: 1 !important;
}

/* 确保extraMesButtons正确显示 */
body.bubblechat .mes_text .extraMesButtons {
    overflow: visible !important;
    display: flex !important;
    padding-right: 0 !important;
}

/* 消息发送者名称 - 使用 max-height, max-width 和 opacity 实现平滑展开 */
.mes_block .ch_name {
    max-height: 0;
    max-width: 0;
    overflow: hidden;
    opacity: 0;
    white-space: nowrap;
    transition: max-height 0.5s cubic-bezier(0.25, 0.1, 0.25, 1), max-width 0.5s cubic-bezier(0.25, 0.1, 0.25, 1), opacity 0.5s cubic-bezier(0.25, 0.1, 0.25, 1);
}

/* 鼠标悬停时显示发送者名称 */
.mes_block:hover .ch_name {
    max-height: 58px;
    max-width: 100%;
    opacity: 1;
}

/* 底栏QR按键隐藏 */
#send_form:hover #qr--bar {
    height: 40px;
    transition: all 0.5s ease;
}

#qr--bar {
    height: 0.1px;
}

/* 隐藏代理警告 */
#ReverseProxyWarningMessage {
    display: none !important;
}

/* 范围输入框 */
.neo-range-input {
    background-color: rgba(237, 237, 237, 0.6);
    border: 1px solid #000000;
    color: #000000;
}

.range-block-counter input {
    background-color: rgba(237, 237, 237, 0.6);
    color: #000000;
    border: 1px solid #000000;
}

select {
    background-color: rgba(237, 237, 237, 0.6);
    color: #000000;
    border: 1px solid #000000;
}

table {
    color: #000000;
}
