custom_css:
/*调色*/
* {
    --black30a: rgba(195, 235, 255,0.4);
    --black50a: rgba(255,255,255,0.7);
    --black70a: rgba(245,150,170,0.5);
    --white30a: rgba(165,222,228, 0.6);
    --grey30a: rgba(180,255,255, 0.5); 
}

/* 字体导入 */
@font-face {
  font-family: 'ZiTi';
  src: url('http://sharkpan.xyz/f/OAwXHy/huiwenmingchaoti.otf') format('woff2');
}

/* ==========* 字体字色 *============ */
/* 全局样式 */
*{
  font-family: 'ZiTi';
}

/* 主文本高度 */
body, html {
  font-size: 18px;  /* 字号 */
  letter-spacing: 0.05em;
  word-spacing: 0.05em;
}

/* 常规文本样式 */
p, span, li {
  font-family: 'ZiTi';
  font-size: 18px;  /* 字号 */
  line-height: 1.5em;
  letter-spacing: 0.05em;
  word-spacing: 0.05em;
}

/*其他*/
select, .text_pole, .inline-drawer-header, .neo-range-input,  [class*="mdHotkeys"], #send_textarea  {
  font-family: 'ZiTi';
}

/* 加粗文本 */
strong {
  color: #e16b8c !important; /* 紅梅 */
}

/* 斜加粗文本 */
strong em {
  color: #d75455 !important; /* 鉛丹 */
}

/* 删除文本 */
del, s {
  color:  #51a8dd ; /* 群青 */
}

/* 高亮文本 */
mark {
  background-color: #2ea9df !important; /* 颜色: 露草 */
  color: #fffffb !important; /* 颜色: 胡粉 */
}


/* ==========* 代码块样式 *============ */
/* 代码块内的代码文本大小 - 独立设置 */
pre, pre code, code {
  font-family: 'ZiTi' !important;
  line-height: 1.2;
}

/* 外框 - 代码块 */
pre {
    background: linear-gradient(135deg, rgba(255, 240, 245, 0.8), rgba(224, 245, 255, 0.8)); 
    border: 2px solid #fcd6d9;    
    border-radius: 6px;  
    padding: 10px 12px;
    box-shadow: 0 4px 12px rgba(255, 192, 203, 0.4), 0 0 6px rgba(173, 216, 230, 0.3);
    transition: box-shadow 0.3s ease, transform 0.3s ease;
}


/* 锁死 code 字色 */
code,
[class="hljs-attribute"],
[class="hljs-comment"],
[class="hljs-section"] {
    color: #1c1c1c !important;
}

/* 代码块内代码样式（行内） */
p code, li code, td code, span code, div code {
    font-family: 'ZiTi';
    padding: 2px 5px;
    background: linear-gradient(135deg, rgba(255, 200, 220, 0.3), rgba(200, 180, 255, 0.3)) !important; 
    margin: 1px 2px;
    vertical-align: middle;
    white-space: pre-wrap;
    word-break: break-word;
}

/* ===========* 表单和输入框 *=========== */

/* 左右侧边栏 */
#left-nav-panel {
  background: linear-gradient(to bottom,
    rgba(255, 250, 252, 0.6),   
    rgba(245, 190, 205, 0.5)   
  ) !important;
}

#right-nav-panel {
  background: linear-gradient(to bottom,
    rgba(250, 255, 255, 0.6),   /* 上：冰感白雾 */
    rgba(190, 225, 235, 0.5)    /* 下：水雾灰蓝 */
  ) !important;
}


/* 输入框、下拉、文本域 */
/* 选中状态下的复选框背景色 */
input[type="checkbox"]:checked {
  background-color:#6699a1 !important;  /* 錆浅葱 */
}

/* 下拉框字色 */
.text_pole option,
#world_editor_select option,
#themes option {
  color:#1c1c1c !important;
}

/* 预设字体加描边 */
[class="prompt-manager-inspect-action"] {
  text-shadow:
    0 0 1px rgba(173, 216, 230, 1),   
    0 0 1px rgba(221, 160, 221, 1),  
    0 0 1px rgba(255, 240, 245, 1);  
}

/* ===========* 头像边框 *=========== */
.avatar {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    width: 50px;
    height: 50px;
    box-sizing: border-box;
    border: 3px solid transparent; /* 定义边框宽度 */
    border-image: linear-gradient(45deg, #ffb6c1, #add8e6) 1 stretch;
    border-radius: 1px;
    overflow: hidden;
}

.avatar img {
    max-width: 100%;
    max-height: 100%;
    object-fit: cover; /* 图片填充满容器 */
}


/* ===========* 世界书 *=========== */
/* 世界书按钮 */
.select2-selection__choice__display {
   font-family: 'ZiTi';
}

/* 世界书下拉框样式 */
.select2-results__option--selectable::before {
  background-color: #81c7d4 !important;  /* 水 */
  content: "❖";           /* 使用日式传统菱形符号 */
  display: flex;          /* 启用弹性布局 */
  align-items: center;    /* 垂直居中 */
  color: #fffffb !important;  /* 金叶色 */
  border: 1px solid #cbb994; /* 边框颜色: 白橡 */
  font-size: 0.8em;        /* 符号尺寸 */
  margin-right: 8px;       /* 与文字间距 */
  vertical-align: 2px;     /* 垂直对齐微调 */
}


/* ===========* 聊天框 *=========== */
/*斜纹背景*/
#chat {
  background: 
    /* 上下渐变底色 */
    linear-gradient(
      to bottom,
      rgba(254,223,225, 0.4),  /* 顶部 */
      rgba(165,222,228, 0.3)   /* 底部 */
    ),
    /* 叠加斜纹 */
    repeating-linear-gradient(
      45deg,
      rgba(211, 211, 211, 0.1) 0px,  
      rgba(211, 211, 211, 0.1) 2px,  
      transparent 1px,
      transparent 4px  
    ),
    /* 叠加十字纹（水平 + 垂直） */
    repeating-linear-gradient(
      to right,
      rgba(211, 211, 211, 0.1) 0px, 
      rgba(211, 211, 211, 0.1) 2px,  
      transparent 1px,
      transparent 4px  
    ),
    repeating-linear-gradient(
      to bottom,
      rgba(211, 211, 211, 0.1) 0px,  
      rgba(211, 211, 211, 0.1) 2px, 
      transparent 1px,
      transparent 4px  
    );
}

/*气泡*/
/* 用户消息 */
body.bubblechat .mes[is_user="true"] {
  background: linear-gradient(90deg, rgba(255, 210, 225, 0.3), rgba(255, 255, 255, 0.4)); 
}

/* 系统消息和机器人消息*/
body.bubblechat .mes[is_system="true"] , 
body.bubblechat .mes[is_user="false"]  {
  background: linear-gradient(90deg, rgba(200, 225, 255, 0.3), rgba(255, 255, 255, 0.4)); 
}


/* ===========* 滚动条优化 *=========== */

/* 滚动条整体样式 */
#chat::-webkit-scrollbar,
[class="scrollableInner"]::-webkit-scrollbar,
#world_editor_select::-webkit-scrollbar {
  width: 8px;  /* 垂直滚动条宽度 */
  height: 8px; /* 水平滚动条高度 */
}

/* 滚动条滑块样式 */
#chat::-webkit-scrollbar-thumb,
[class="scrollableInner"]::-webkit-scrollbar-thumb {
  background: linear-gradient(
    45deg,
    rgba(165, 222, 228, 0.3),
    rgba(255, 240, 245, 0.5)
  );
  border-radius: 4px;
  border: 1px solid rgba(203, 178, 121, 0.3); /* 胡桃边框 */
  box-shadow: inset 0 0 4px rgba(255, 200, 220, 0.4);
  transition: all 0.3s ease;
}

#world_editor_select::-webkit-scrollbar-thumb {
  background-color: #e87a90; /* 樱色滑块 */
  border-radius: 10px;
  border: 3px solid transparent;
  background-clip: content-box;
  transition: all 0.3s ease;
}

/* 滚动条滑块悬停效果 */
#chat::-webkit-scrollbar-thumb:hover,
[class="scrollableInner"]::-webkit-scrollbar-thumb:hover {
  background: linear-gradient(
    135deg,
    rgba(165, 222, 228, 0.5),
    rgba(255, 240, 245, 0.8)
  );
  border: 1px solid rgba(255, 220, 200, 0.5);
  transform: scale(1.1);
  box-shadow: 0 0 6px rgba(255, 200, 220, 0.6);
}

#world_editor_select::-webkit-scrollbar-thumb:hover {
  background-color: #f59ca9; /* 更亮的樱色 */
  transform: scale(1.1);
  box-shadow: 0 0 6px rgba(232, 122, 144, 0.5);
}

/* 滚动条轨道样式 */
#world_editor_select::-webkit-scrollbar-track {
  background: #f1f1f1;
  border-radius: 10px;
}


/* ===========* 快捷栏 *=========== */
/* 初始隐藏的快捷栏样式 */
#qr--bar {
    height: 0; 
}

/* 鼠标悬停时展开快捷栏 */
#send_form:hover #qr--bar {
    height: 45px; /*高度可调，一层45px，auto为自动适应内容高度 */
}

/* ==========* 发送框*============ */
/* 发送框 */

#send_form {
  background: rgba(165,222,228, 0.3);
}

/* 替换FontAwesome按钮 */
.fa-circle-stop::before {
  content: "💮 " !important;
  font-size: 0.8em !important; /* 调整字体大小，确保符号清晰 */
  vertical-align: middle !important; /* 确保图标垂直居中 */
  display: inline-flex; /* 使用 inline-flex 来确保动画与对齐 */
  justify-content: center; /* 水平居中 */
  align-items: center; /* 垂直居中 */
  animation: sway 3s ease-in-out infinite; /* 保持动画效果 */
  max-width: 1em;
  transform-origin: center;
  transform: scale(1) rotate(0deg); /* 移除潜在的旋转，防止歪斜 */
  line-height: 1; /* 防止文字行高导致的偏差 */
}

/* 飘动动画 */
@keyframes sway {
  0%, 100% { transform: rotate(-10deg); }
  50% { transform: rotate(10deg); }
}

/* 悬停特效 */
.fa-circle-stop:hover::before {
  filter: 
    drop-shadow(0 0 3px #ff9eb5) 
    brightness(1.2);
  transform: scale(1.3);
}

/* ===========*下拉菜单*=========== */
/* 悬停状态 */
select:hover {
  box-shadow: 0 0 8px rgba(51,166,184, 1) !important; /* 添加光晕 */
}

/* 焦点状态 */
select:focus {
  border-color: #7db9de !important; 
}

/* ===========* 聊天记录 *=========== */
#shadow_select_chat_popup {
    background:     linear-gradient(
      to bottom,
      rgba(254,223,225, 0.4),  /* 顶部 */
      rgba(165,222,228, 0.3)   /* 底部 */
    );
}
#select_chat_popup {
    background:     linear-gradient(
      to bottom,
      rgba(254,223,225, 0.4),  /* 顶部 */
      rgba(165,222,228, 0.3)   /* 底部 */
    );
}

.select_chat_block {
    transition: background-color 0.3s ease; 
}

.select_chat_block:focus {
    background-color: rgba(173, 216, 230, 0.3); 
}


/* 移除 top-bar 的所有阴影 */
#top-bar,
.top-bar {
  box-shadow: none !important;
}

/* 全局响应式基础设置 */
* {
  box-sizing: border-box; /* 确保padding和border不会增加元素宽度 */
  word-wrap: break-word; /* 允许长单词换行 */
  overflow-wrap: break-word; /* 现代浏览器中更好的换行控制 */
}
