custom_css:
/* === 定义主题颜色 === */
:root {
    --main-words: var(--SmartThemeBodyColor); 
/* 对应主要文本 */
--yellow-words: var(--SmartThemeEmColor);        
/* 对应斜体文本-->按键的黄色+user边框 */
--darkyellow: #CF9F68; 
/*按钮被选中的颜色，没有多的颜色了*/
    --red-words: var(--SmartThemeUnderlineColor);    
/* 对应下划线文本-->char的红色边框+按钮文字*/
    --green-words: var(--SmartThemeQuoteColor); 
/* 对应引用文本+幽灵颜色+标题框框 */
    --ui-background-main: var(--SmartThemeUserMesBlurTintColor); 
/* 对应用户消息模糊色调*/
--ui-border-color: var(--SmartThemeBorderColor); 
/*对应UI边框颜色 */
--ui-hover-color:var(--SmartThemeShadowColor); /*对应阴影颜色*/    --crimson70a: #A70C0C;
--white100: #FFFFFF;

--topBarIconSize: calc(var(--mainFontSize) * 1.5);
--bottomFormIconSize: calc(var(--mainFontSize) * 1.5);
--bottomFormBlockPadding: calc(var(--mainFontSize) / 2);
}

/* ===== 菜单栏样式修改 ===== */
/*去除菜单栏下的阴影 */
#top-bar {
  box-shadow: none !important; /* 移除顶部菜单栏的阴影 */
  border-radius: 6px 6px 0 0; /* 顶部圆角，美化外观 */
}

/*修改菜单栏点击后出现的折叠框的颜色*/
.drawer-content {
  background-color: var(--ui-background-main) !important; /* 设置折叠框的背景色为指定的浅米色 */
  border: 1px solid var(--ui-border-color) !important; /* 添加边框以增强视觉效果 */
  border-top: none !important; /* 去掉顶部边框，与菜单栏连接处无边框 */
  border-radius: 0 0 6px 6px; /* 底部圆角，匹配整体风格 */
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1) !important; /* 轻微阴影，提升立体感 */
}

/* ===== 标题样式修改 ===== */
/*标题添加虚线边框*/
.standoutHeader:not(h4),
#rm_PinAndTabs,
#top-settings-holder .drawer:not(:first-child):not(:last-child) .drawer-content .alignItemsBaseline.wide100p,
#rm_api_block .flex-container.alignItemsBaseline,
#title_api,
#AdvancedFormatting .flex-container.alignItemsBaseline:not(h3),
#WorldInfo .alignitemscenter:not(#world_popup *),
#user-settings-block div[name="userSettingsRowOne"],
#rm_extensions_block .alignitemscenter.flex-container.wide100p:not(.justifyCenter) {
  background-image: none;
  border-radius: 4px; 
  border: 4px dashed var(--green-words);
  margin-bottom: calc(var(--mainFontSize) * 0.4);
  padding: calc(var(--mainFontSize) * 0.2); /* 调整内边距，使文字与边框有间距 */
}

/* 当角色名h2为空时，移除其父容器的边框和间距 */
#rm_PinAndTabs:has(#rm_button_selected_ch h2:empty) {
    border: none;
    margin-bottom: 0;
    padding: 0;
}

/* ===== 抽屉样式修改 ===== */
/* ---抽屉标题栏--- */
.inline-drawer-header:not(#WorldInfo .inline-drawer-header), /*排除世界书模块*/
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#top-settings-holder h4:not(#ai-config-button *):not(#rightNavHolder *) {
  position: relative;
  border: none !important;
  border-radius: 0;
  background-image: none !important;
  padding-left: 16px;
}

/* 左侧小三角形（排除世界书板块） */
.inline-drawer-header:not(#WorldInfo .inline-drawer-header)::before,
#extensions_settings .inline-drawer-toggle.inline-drawer-header::before,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header::before,
#top-settings-holder h4:not(#ai-config-button *):not(#rightNavHolder *)::before {
  content: "";
  position: absolute;
  left: 0;
  top: 50%;
  transform: translateY(-50%);
  width: 0;
  height: 0;
  border-top: 5px solid transparent;
  border-bottom: 5px solid transparent;
  border-left: 8px solid var(--green-words);
}

/* 箭头图标颜色同步 */
.inline-drawer-icon {
  font-size: var(--icon-size);
  color: var(--green-words);
}

/* 缩小特定元素的三角形 */
.userSettingsInnerExpandable.inline-drawer-header::before,
.wide100p.flexFlowColumn.inline-drawer-header::before,
.inline-drawer[name="AutoCompleteToggle"] .inline-drawer-header::before {
  border-top: 3px solid transparent !important;   /* 高度缩小 */
  border-bottom: 3px solid transparent !important; /* 高度缩小 */
  border-left: 6px solid var(--green-words) !important; /* 宽度缩小 */
}
/* ===输入框=== */
/* 基础输入框【文本，数字，开关，未指定类型的输入框（默认文本输入），非搜索类型的文本域（textarea）】样式 */
input[type="text"],
input[type="number"],
input[type="switch"],
input:not([type]),
textarea:not([type="search"]) {
  background-color: rgba(254, 250, 239, 0.2) !important;
  border: 1px solid var(--ui-border-color) !important; 
}

/*点击样式（没有变化）*/
input[type="text"]:focus,
input[type="number"]:focus,
input[type="switch"]:focus,
input:not([type]):focus,
textarea:not([type="search"]):focus {
  border: 1px solid var(--ui-border-color) !important;
}

/* 搜索框特殊处理 */
textarea[type="search"] {
  background-color: var(--ui-background-mainr) !important;
  border: none !important;
}

/* 范围滑块容器 */
.range-block {
  margin: calc(var(--mainFontSize) * 0.6) 0;
}

/* 范围滑块计数器 */
.range-block-counter {
  margin-left: 20px;
}

/* 特殊文本输入框 */
.text_pole:not(.objective-task) {
  background-color: rgba(254, 250, 239, 0.5)); 
  border: 1px solid var(--ui-border-color);
}

/* ===按键基础样式=== */
.menu_button:not(.mes_edit_buttons *) {
  background-color: var(--yellow-words); 
  color: var(--red-words); 
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: all 0.3s ease;       /* 平滑过渡动画 */
}

/* ---悬停状态--- */
.menu_button:not(.mes_edit_buttons *):hover {
  background-color: var(--darkyellow);      /* 悬停时背景色加深 */
  transform: translateY(-1px);    /* 轻微上浮效果 */
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
}

/* ---选中/激活状态--- */
.menu_button:not(.mes_edit_buttons *):active,
.menu_button:not(.mes_edit_buttons *).is-active {
  background-color: var(--darkyellow) !important;  /* 选中状态颜色深黄 */
  color: white !important;
  box-shadow: inset 0 2px 4px rgba(0,0,0,0.2); /* 内阴影表示按下状态 */
}

/* ---高风险按钮（保持原样）--- */
#delete_button,
#dialogue_del_mes_ok,
#dialogue_popup_ok,
#bulk_tag_popup_reset,
#bulk_tag_popup_remove_mutual,
.mes_edit_cancel.menu_button,
.menu_button.popup-button-ok,
.redWarningBG {
  background-color: var(--crimson70a) !important;
  color: var(--white100) !important;
}

/* ===代码块样式=== */ 
/* 基本<code>元素样式（适用于行内代码） */
code {
  margin: 0;                       /* 清除所有外边距 */
  border: 1px dashed var(--ui-border-color); 
  border-radius: 4px;             
  background-color: var(--ui-hover-color);      
  padding: 0em 0.4em;           /* 上下内边距0.2em，左右内边距0.4em */
  color:var(--green-words);         
}

/* 高亮代码块样式（适用于通过highlight.js等库高亮的代码块） */
body[data-stscript-style] .hljs.language-stscript, 
.hljs {                              
  background: #E9E4D5;             
  border: 3px dashed var(--ui-border-color);      
  border-radius: 6px;              
  padding: 0.8em;                   
  color: var(--main-words);          
}

/* Mermaid图表容器样式 */
.custom-mermaid {
  background-color: var(--ui-hover-color);        
  border: 1px dashed var(--ui-border-color); 
  border-radius: 6px;             
  padding: 1em;                   
  color:var(--darkgreen);           
}

/* 覆盖语法高亮插件的默认颜色设置 */
.hljs * {                          /* 选择所有高亮代码块内的子元素 */
  color: inherit !important;       /* 强制继承父元素的文字颜色 */
}
/* ==== 下拉框 ==== */
/*通用格式*/
select {
  background-color: var(--ui-background-main) !important;
  color: var(--main-words);
  border: 1px solid var(--ui-border-color) !important;
  margin: calc(var(--mainFontSize) * 0.3) 0 !important;
}
/*未选中选项的字体颜色*/​​
select option:not(:checked) {
  color: var(--main-words);
}
/*​选中选项​​*/
select option:checked {
  color: var(--main-words);
  background-color: var(--ui-hover-color) !important;
}
/*所有选项的背景颜色*/
select option {
  background-color: var(--ui-background-main) !important;
}

/*===世界书模块===*/
/* 输入框整体样式 */
.select2-container .select2-selection--multiple, 
.select2-container .select2-selection--single {
    background-color: rgba(254, 250, 239, 0.2);   /* 比底色较亮的颜色 */
    border: 1px solid var(--ui-border-color);   
    border-radius: 4px;      
}

/* 输入框右侧的叉叉样式 */
.select2-container .select2-selection .select2-selection__clear {
    transition: transform 0.3s ease;
    color: var(--red-words); 
}

/* 设置悬停时放大效果 */
.select2-container .select2-selection .select2-selection__clear:hover {
    transform: scale(1.1);           
}
/* 书名标签整体样式 */
.select2-container .select2-selection--multiple .select2-selection__choice, 
.select2-container .select2-selection--single .select2-selection__choice {
    border-radius: 4px;          
    border: none;              
    box-sizing: none;           
    color: var(--red-words);            
    background-color:var(--yellow-words);  
}

/* 书名标签右侧书名样式 */
.select2_choice_clickable_buttonstyle+span.select2-container .select2-selection__choice__display {
    background-color: transparent !important; 
    color: var(--red-words) !important; 
}
/*处理移动端左右滑动问题*/
@supports (-webkit-touch-callout: none) {
  #world_popup {
    margin-top: 0;
    width: 100%;
  }
}

/* === 聊天界面 === */
/*气泡基础样式*/
.mes {
    position: relative;
    margin: 5px 0; /* 两条消息的间距 */
    border-radius: 4px; /* 圆角 */
}

/* 用户消息特殊样式（边框做颜色区分）*/
div[is_user="true"] {
  border: 1px solid var(--yellow-words) !important; 
  border-left: 3px solid var(--yellow-words) !important; 
}

/* 角色消息特殊样式 */
div[is_user="false"] {
  border: 1px solid var(--red-words) !important; /* 四条边统一边框 */
  border-left: 3px solid var(--red-words) !important; /* 左侧边框加粗 */
}

/* 头像位置 - 增强稳定性 */ 
.mes .avatar {
    position: absolute !important; 
    top: 20px !important; 
    left: 20px !important; 
    z-index: 10 !important;
    width: 60px !important; /* 固定头像宽度 */
    height: 90px !important; /* 固定头像高度 */
}

/* 确保头像图片不会影响布局 */
.mes .avatar img {
    width: 100% !important;
    height: 100% !important;
    object-fit: cover !important;
}

/* 名字位置 - 增强定位稳定性 */
.mes .name_text {
    position: absolute !important;
    top: 15px !important;
    left: 90px !important;
    font-family: Microsoft Yahei !important;
    font-size: 1.2em !important;
    margin: 0 !important;
    padding: 0 !important;
    line-height: 1.2 !important;
    width: calc(100% - 110px) !important; /* 防止文字溢出 */
    z-index: 5 !important;
}

/* Token计数位置 - 确保不与其他元素重叠 */
.tokenCounterDisplay {
    position: absolute !important;
    left: 100px !important;
    top: 50px !important;
    opacity: 1 !important;
    margin: 0 !important;
    padding: 0 !important;
    display: block !important;
    z-index: 5 !important;
    font-size: 0.9em !important;
}

/* 时间计数位置 - 调整以避免重叠 */
.mes_timer {
    position: absolute !important;
    left: 145px !important; /* 增加左边距避免与token重叠 */
    top: 50px !important;
    opacity: 1 !important;
    padding: 0 !important;
    margin: 0 !important;
    display: block !important;
    z-index: 5 !important;
    font-size: 0.9em !important;
}

/* 聊天时间样式 - 确保位置稳定 */
.mes .timestamp {
    position: absolute !important;
    left: 90px !important;
    top: 60px !important;
    color: var(--main-words) !important;
    opacity: 1 !important;
    font-size: 0.8em !important;
    margin: 0 !important;
    padding: 0 !important;
    display: block !important;
    z-index: 5 !important;
}

/* 楼层计数样式 - 防止位置漂移 */
.mesIDDisplay {
    position: absolute !important;
    top: 90px !important;
    left: 100px !important;
    opacity: 1 !important;
    margin: 0 !important;
    padding: 0 !important;
    display: block !important;
    z-index: 5 !important;
    font-size: 0.9em !important;
}

/* 聊天内容空间调整 - 确保为头部信息留出足够空间 */
.mes_block {
    padding-top: 80px !important; /* 增加顶部空间 */
    padding-bottom: 10px !important;
    padding-left: 20px !important;
    padding-right: 20px !important;
    position: relative !important;
    z-index: 1 !important;
}

/* 聊天内容文本区域 */
.mes_text {   
    padding-right: 3px !important;
    position: relative !important;
    z-index: 1 !important;
    margin: 0 !important;
}

/* 聊天内容编辑图标 */
.mes .mes_button,
.mes .mes .extraMesButtons>div {
    filter: none !important;
    opacity: 1 !important;
    font-size: 0.8em !important;
    color: var(--main-words) !important;
}

/* 悬停小图标颜色变暗 */
.mes_button:hover, .extraMesButtons>div:hover {
    filter: brightness(0.9) !important;
}

/* 防止元素重叠的额外保护 */
.mes::before {
    content: "";
    display: block;
    height: 105px; /* 为头部信息预留空间 */
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    z-index: 0;
    pointer-events: none;
}

/* 幽灵图标样式 - 定位到又、右上角 */
.mes_ghost {
    position: absolute;
    top: 5px;
    right: 5px;
    font-size: 1.2em; 
    color: var(--main-words); /* 设置图标颜色*/
    z-index: 15; /* 确保图标在其他内容之上 */
}

/*===== char界面 =====*/
/*整体外观*/
/* 收藏夹滑动条*/
#HotSwapWrapper {
  overflow: visible; /* 容器不裁剪内容 */
}

#HotSwapWrapper > div {
  overflow-y: auto;  /* 改为竖直滚动 */
  overflow-x: hidden;  /* 禁止水平滚动 */
  flex-wrap: wrap;  /* 允许换行（竖直布局需要） */
  height: calc(var(--avatar-base-height) + var(--scroll-width) + 10px);
  max-height: calc(var(--avatar-base-height) * 2);

  /* 确保滚动条始终可见 */
  scrollbar-width: thin;
  scrollbar-color: var(--ui-hover-color) transparent;
}
/* 所有角色头像统一样式​​ */
#rm_print_characters_block .bogus_folder_select.entity_block .avatar img,
#rm_print_characters_block .character_select.entity_block .avatar img,
#rm_print_characters_block .group_select.entity_block .avatar img,
.hotswap .avatar img {
    border: none;
}

/*角色/群组/文件夹的块状框主体样式*/
#rm_print_characters_block .bogus_folder_select.entity_block,
#rm_print_characters_block .character_select.entity_block,
#rm_print_characters_block .group_select.entity_block {
    /* === 布局与尺寸 === */
    width: 100%; /* 改为100%父容器宽度 */
    min-width: 280px; /* 新增最小宽度防止挤压 */
    margin: 0 0 12px 0; /* 仅保留底部外边距 */
    box-sizing: border-box; /* 确保padding不额外增加宽度 */

    /* === 背景与边框 === */
    background-color: var(--ui-hover-color) !important; 
border: 2px dashed var(--green-words); 
    border-radius: 8px; /* 稍大的圆角 */
    box-shadow: 
        0 2px 6px rgba(98, 108, 58, 0.2), /* 柔和的下阴影 */
        inset 0 0 10px rgba(85, 25, 21, 0.05); /* 内敛的深色内阴影 */

    /* === 文字样式 === */
    color: var(--main-words) !important; 
    font-size: 15px;
    letter-spacing: 0.5px;
    line-height: 1.4;
    padding: 14px 18px; /* 增大内边距 */
    position: relative;

    /* === 过渡动画 === */
    transition: 
        transform 0.2s ease,
        box-shadow 0.3s ease;
}

/* 悬停状态增强 */
#rm_print_characters_block .bogus_folder_select.entity_block:hover,
#rm_print_characters_block .character_select.entity_block:hover,
#rm_print_characters_block .group_select.entity_block:hover {
    transform: translateY(-2px);
    box-shadow: 
        0 4px 10px rgba(98, 108, 58, 0.3),
        inset 0 0 12px rgba(85, 25, 21, 0.08);
}

/* 选中状态 */
#rm_print_characters_block .entity_block.character_selected {
    background-color: var(--ui-hover-color) !important;
    border-left: 4px solid #551915; /* 左侧加深色标识条 */
}

/* 移动端适配 */
@media (max-width: 768px) {
    #rm_print_characters_block .entity_block {
        min-width: 100%; /* 移动端全宽 */
        border-radius: 6px;
        padding: 12px 15px;
    }
}

/* --- 收藏样式 --- */
#rm_print_characters_block .character_select.is_fav .avatar,
#rm_print_characters_block .group_select.is_fav .avatar,
#rm_print_characters_block .group_member.is_fav .avatar,
#rm_print_characters_block .avatar.is_fav {
    outline: 1px solid var(--green-words) !important;
    outline: 8px;
}

/* 角色名颜色 */
#rm_print_characters_block .character_select.is_fav .ch_name,
#rm_print_characters_block .group_select.is_fav .ch_name,
#rm_print_characters_block .group_member.is_fav .ch_name {
    color: var(--green-words);
    padding-left: 22px;
    position: relative;
}
/* 圆圈图标表明已收藏 */
#rm_print_characters_block .character_select.is_fav .ch_name::before,
#rm_print_characters_block .group_select.is_fav .ch_name::before,
#rm_print_characters_block .group_member.is_fav .ch_name::before {
    content: "◉" ;
    position: absolute ;
    left: 2px ;
    top: 50% ;
    transform: translateY(-50%);
    color: var(--green-words);
    font-size: 14px;
}
/* 角色界面头像 */
#avatar_load_preview {
    transition: 0.2s ease;
}
#avatar_load_preview:hover {
    transform: scale(1.1);
}
#avatar_load_preview:focus-visible {
    outline: 0;
}
.add_avatar:hover {
    filter: initial;
}

/* === 主页最新消息 === */
/*主体卡片样式*/
.recentChat {
    background-color: var(--ui-hover-color) !important;
    border: 2px dashed var(--green-words) !important;
    border-radius: 8px;
    box-shadow: 
        0 2px 6px rgba(98, 108, 58, 0.2), 
        inset 0 0 10px rgba(85, 25, 21, 0.05);
    color: #551915 !important;
    font-size: 15px;
    padding: 12px 16px;
    margin-bottom: 10px;
    display: flex;
    gap: 14px;
    align-items: flex-start;
    transition: background 0.3s ease, transform 0.2s ease;
    position: relative;
}

.recentChat:hover {
    transform: translateY(-2px);
    box-shadow: 
        0 4px 10px rgba(98, 108, 58, 0.3),
        inset 0 0 12px rgba(85, 25, 21, 0.08);
    transform: scale(1.01);
}



/* 隐藏聊天预览文字（chatMessage 区域） */
.recentChat .chatMessage {
    display: none !important; 
/* 完全隐藏消息内容，只保留名字/时间/统计 */
}

/* 设置聊天信息区域的垂直布局（名字 -> 时间 -> 统计） */
.recentChat .chatNameContainer {
    display: flex !important;
    flex-direction: column !important;   
    align-items: flex-start !important;  
    justify-content: flex-end !important;
    height: 100%; /* 允许元素填满高度 */
    padding-top: 4px;
    padding-bottom: 4px;

    box-sizing: border-box;
}

/*名字样式 */
.recentChat .chatName {
    font-size: 16px !important;          
    font-weight: bold;
color: var(--main-words) !important;
    line-height: 1.2;
}

/* 时间样式 */
.recentChat .chatDate {
    position: static !important;         
/* 确保参与垂直排列 */
    font-size: 14px !important;          
/* ← 和消息预览一致 */
    color: var(--main-words) !important;
    margin: 0 !important;
    padding: 0 !important;
}

/* 消息统计贴底*/
.recentChat .chatStats {
    font-size: 14px !important;
    color: var(--yellow-words);
