@import url("https://static.zeoseven.com/zsft/256/main/result.css");

body {
    font-family: "Huiwen-mincho";
    font-weight: normal;
}
/* Apply custom font to input fields */
input[type="text"],
input[type="number"],
textarea,
.text_pole,
.search_field {
    font-family: "Huiwen-mincho", sans-serif !important; /* Replace "undefined" with your actual font name */
}

/* Apply custom font to code blocks */
pre,
code,
.hljs,
pre code {
    font-family: "Huiwen-mincho", monospace !important;
}
#send_textarea:placeholder-shown {
     font-family: "Huiwen-mincho";
     font-weight: normal;
     font-size: 15.5px;
}

/* -----Moaisha❥----- */

/* 全局去除所有元素的阴影效果 */
* {
    box-shadow: none !important;

}

/*---- 气泡区域----*/
#chat {
    backdrop-filter: none !important;
    bottom: 0;
}
/*消息*/
.mes_text {
    position: relative;
    margin: 25px 15px 15px !important;
    padding: 20px 25px !important;
    border: 2px solid #809C8D !important;
    border-radius: 8px !important;
    background: 
        linear-gradient(175deg, 
           rgba(255, 255, 255, 0.85) 0%, 
            rgba(255, 255, 255, 0.95) 100%) !important;
    box-shadow: 
        3px 3px 8px rgba(118,98,72,0.1),
        inset 1px 1px 3px rgba(255,255,255,0.2);
    transition: box-shadow 0.3s ease;
}

.mes_text::before {
    content: "";
    position: absolute;
    top: -7px;
    left: -7px;
    right: -7px;
    bottom: -7px;
    border: 1px solid rgba(44, 74, 52,0.3);
    border-radius: 12px 2px 12px 2px;
    z-index: -1;
}

/* 消息容器 */
.mes {
    position: relative;
    margin: 1.5em 0;
    padding: 15px;
    background: linear-gradient(145deg, var(--mes-bg-gradient-start) 0%, var(--mes-bg-gradient-end) 100%)!important;
    width: 100%;
    flex-direction: column;
    align-items: center;
    border: none !important;
    margin: 0;
}
.mes_block {
    margin: 0 !important;
    width: 98%;
    padding-left: 0;
}

.last_mes .mes_text {
  padding-right: 0;
}

.last_mes .mesAvatarWrapper {
  padding-bottom: 0;
}


body.bubblechat .mes {
    margin: -1px 0 !important;
    border-radius: 0 !important;
}
select {
    background: linear-gradient(145deg, #CDE2E8, #64A386);
    padding: 5px 10px;
    border-radius: 5px;
    transition: background 0.3s ease;
  color: #fff;
  border: none !important;
}

select option:not(:checked) {
  color: #000;
}
select option:checked {
  color:#fff;
  background-color:#BCDD8E!important;
}

select option {
  background-color:#64A386!important;
}

select:hover {
    background: linear-gradient(145deg, rgba(188, 221, 142, 0.9), #CDE2E8);
}
.menu_button {
    color: #334039;
    background-color: #fff;
    border: 1px solid var(--SmartThemeBorderColor);
}
/* ---主要气泡--- */
#chat .mes {
    background-color: rgba(255, 255, 255, 0.5); 
/* 设置用户消息气泡的背景色 */
    border-radius: 25px;
    padding: 15px 25px;
    box-shadow: 3px 3px 8px 0px rgba(0, 0, 0, 3%) !important;
}

body.big-avatars #chat .mes .mes_block {
    border-radius: 10px;
}

.mes {
    padding: 10px;
    margin-bottom: 5px;
}

/* USER消息 */
#chat .mes[is_user="true"]  {
    background-color: rgba(255, 255, 255, 0.5); /* 设置非用户消息气泡的背景色 */
}

/*---背景和文字颜色--- */
/* 非用户引用文本 */
body.bubblechat .mes:not([is_user="true"]) q {
    color: transparent; /* 保持文本颜色为透明 */
    background-image: linear-gradient(to bottom, rgba(44, 74, 52, 0.9), rgba(44, 74, 52, 0.5)); /* 保持渐变背景 */
    -webkit-background-clip: text; /* 使背景只应用于文本 */

    font-weight: bold; /* 加粗文本 */
    font-style: italic; /* 斜体 */
    font-size: 18px; /* 增大字体 */
    padding: 4.5px; /* 为引用文本增加一些内边距，增强视觉效果 */
    border-left: 2.5px solid rgba(44, 74, 52, 0.9); /* 左侧添加一条边框，突出引用部分 */
    margin-left: 5px; /* 给引用文本增加左边距，使其与正文分开 */body.bubblechat 
    display: inline-block; /* 让 q 适配文本高度 */
    padding: 2px 5px; /* 让整体小一点 */
}

/* 用户引用文本 */
body.bubblechat .mes[is_user="true"] q {
    color: transparent; /* 保持文本颜色为透明 */
    background-image: linear-gradient(to bottom, rgba(44, 74, 52, 0.9), rgba(44, 74, 52, 0.5)); /* 保持渐变背景 */
    -webkit-background-clip: text; /* 使背景只应用于文本 */

    font-weight: bold; /* 加粗文本 */
    font-style: italic; /* 斜体 */
    font-size: 18px; /* 增大字体 */
    padding: 4.5px; /* 为引用文本增加一些内边距，增强视觉效果 */
    border-left: 2.5px solid rgba(44, 74, 52, 0.9); /* 左侧添加一条边框，突出引用部分 */
    margin-left: 5px; /* 给引用文本增加左边距，使其与正文分开 */body.bubblechat 
    display: inline-block; /* 让 q 适配文本高度 */
    padding: 2px 5px; /* 让整体小一点 */
}
/* 代码块文本背景 */
body.bubblechat .mes code {
    border-radius: 15px;
    color: rgba(255, 255, 255, 0.5);
    border-color: transparent;
    font-size: 14px;  /* 调整字体大小 */
    line-height: 1.3; /* 调整行距 */
    background: rgba(44, 74, 52, 0.8) !important; /* 确保背景颜色 */
}

/* 确保 pre 也有相同的背景色 */
body.bubblechat .mes pre {
    background: rgba(44, 74, 52, 0.8) !important;
    border-radius: 15px;
}

/* 代码块的整体样式 */
.markdown-body pre,
.markdown-body code,
pre, code {
    background: rgba(44, 74, 52, 0.8) !important;
    border-radius: 15px !important;
}

/* 伞 */
pre::after {
    content: url('https://files.catbox.moe/7wzqa2.png');
    position: absolute;
    font-size: 20px;
    border-radius: 15px;
    letter-spacing: 10px;
    animation: snowFloat 3s ease-in-out infinite;
    bottom: -10px;
    right: 10px;
}

@keyframes snowFloat {
    0%, 100% {
        transform: translateY(0) rotate(0deg);
    }
    50% {
        transform: translateY(-5px) rotate(15deg);
    }
}

/* ------分割------- */
/*开关按钮*/
.world_entry .killSwitch.fa-toggle-on {
    background: linear-gradient(145deg, #CDE2E8, #64A386, #BCDD8E);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

/*调节*/
.neo-range-input {
    background: linear-gradient(145deg, rgba(100, 163, 134, 0.9), rgba(251, 252, 205, 0.9));

}
/*展开*/
.custom-drawer-icon,
.inline-drawer-icon {
    display: block;
    color: #64A386; 
}

/*顶部*/
#top-settings-holder {
    background:linear-gradient(145deg, rgba(255, 248, 248,0.15) 0%,  rgba(255,255,255,0.15) 100%) !important;
}

.drawer-icon {
    color: rgba(44, 74, 52, 0.8) !important;
    font-size: 1.5em;
}
/* 滚动条 */
* {
    scrollbar-color: rgba(44, 74, 52, 0.3) rgba(255, 255, 255, 0.3);
    scrollbar-width: thin;
}

/* 移动端滚动条隐藏 */
@media screen and (max-width: 1000px) {
    *::-webkit-scrollbar {
        display: none;
    }

    * {
        scrollbar-width: none;
        -ms-overflow-style: none;
    }
}
/* 输入框 */
#send_form {
    border-radius: 8px !important; /* 圆角 */
    border: 1.5px solid rgba(140, 180, 160, 0.6) !important; /* 浅绿色边框 */
    background: rgba(255, 255, 255, 0.2) !important; /* 干净的半透明背景 */
    backdrop-filter: blur(4px); /* 毛玻璃效果 */
    padding: 10px 15px; /* 内边距 */
    font-size: 16px; /* 字体大小 */
    color: #4a3a2a !important; /* 古典风格的深棕色文字 */
    text-shadow: 1px 1px 2px rgba(255, 255, 255, 0.3); /* 白色文字阴影，增强可读性 */
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1); /* 轻微阴影，增加层次感 */
}
/* 删除按钮 */
#delete_button, .redWarningBG, .menu_button.popup-button-ok {
    background: linear-gradient(to bottom, 
                rgba(44, 74, 52, 0.3), 
                rgba(44, 74, 52, 1)) !important;
}

/* 设置标题底色去除 */
.title_restorable {
    background-image: none !important;
}

pre {
    font-family: monospace;
    padding: 10px;
    border-radius: 5px;
    background-clip: padding-box;
    position: relative;
}

/* 设置标题字体修改 */
#extensions_settings .inline-drawer-toggle.inline-drawer-header, 
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header, 
#user-settings-block h4, 
.standoutHeader {

    background-image: none !important;
    background-color: rgba(44, 74, 52, 0.7);
    color: rgba(0, 0, 0, 0.3);
    text-shadow: 1px 1px 2px rgba(255, 255, 255, 0.5);
}
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
    color: #080401;
}
/* 编辑框黑色去除 */
.edit_textarea {
    background-color: transparent !important;
}

body.bubblechat .mes_block {
    padding-bottom: unset;
}

/* 隐藏代理警告 */
#ReverseProxyWarningMessage { 
    display: none !important; 
}

/* 标注区域 */
.mes_text blockquote {
    background-color: rgba(255, 250, 205, 0.3);
    border-left: 5px solid #e0f2f7;
    color: #388e3c;
}
/* 角色切换器优化 */
#HotSwapWrapper > div {
    padding: 2px 0;
}

.character_select {
    transition: transform 0.3s ease;
}

.character_select:hover {
    transform: translateY(-3px);
}
body.waifuMode #chat
{
    border-radius: 12px 12px 0 0;
    border-top: var(--waifu-chat-and-scrollbar);
    border-left: var(--waifu-chat-and-scrollbar);
    border-right: var(--waifu-chat-and-scrollbar);
    border-bottom: none;
}
/* 输入框三图 ——古风典雅图标*/
/* 修改 #options_button 按钮图标 */
#options_button {
    background: none !important; /* 移除原本的背景 */
}
#options_button::before {
    content: "";
    display: block;
    width: 50px; /* 根据需要调整大小 */
    height: 35px;
    background: url('https://files.catbox.moe/2m7ta1.png') no-repeat center;
    background-size: contain;
}

/* 修改 #extensionsMenuButton 按钮图标 */
#extensionsMenuButton {
    background: none !important;
}
#extensionsMenuButton::before {
    content: "";
    display: block;
    width: 50px;
    height: 35px;
    background: url('https://files.catbox.moe/uhhyoe.png') no-repeat center;
    background-size: contain;
}

/* 修改 #send_but 按钮图标 */
#send_but {
    background: none !important;
}
#send_but::before {
    content: "";
    display: block;
    width: 50px;
    height: 50px;
    background: url('https://files.catbox.moe/m7jscw.png') no-repeat center;
    background-size: contain;
}
